bool rayIntersect(vec3 ro, vec3 rd) {
	float time = iTime;
    vec3 sphere_center = 3.*vec3(sin(time)*cos(time), cos(time), 0);
    vec3 sphere_to_ray = ro - sphere_center;

    float a = dot(rd, rd);
    float b = 2.*dot(rd, sphere_to_ray);
    float c = dot(sphere_to_ray, sphere_to_ray) - 0.2;

    return b*b - 4.*a*c > 0.;

}

vec4 render(vec2 uv)
{ 
    vec3 ro = vec3(0, 0, -4);
    vec3 rd = normalize(vec3(uv, 1));


    vec3 col = vec3(0);
    if(rayIntersect(ro, rd)) {
        col = vec3(1);
    }

    return vec4(col,1.0);
}

