//=======================================================
//= Filtered Local Spot Noise 
//=======================================================
//== Local Spot Noise for Procedural Surface Details Synthesis  
//== https://acavalier.github.io/research/local-spot-noise-paper
//=======================================================
//== Analytic Filtering - https://www.shadertoy.com/view/tdyXzK
//== Normal Mapping - https://www.shadertoy.com/view/Wdc3W7
//== Control Map - https://www.shadertoy.com/view/Ws33W7
//=======================================================
//== Authors : Arthur Cavalier, Guillaume Gilet, Djamchid Ghazanfarpour
//=======================================================


//--------------------------------------------------------------------------------------------------------
// User Parameters ---------------------------------------------------------------------------------------
const uint  LSN_GLOBAL_SEED = 0u;
const float LSN_RESOLUTION  = 40.;
const int   LSN_IMPULSES    = 3;
#define KERNEL_ANIMATION

//--------------------------------------------------------------------------------------------------------
// Constants ---------------------------------------------------------------------------------------------
const float m_pi_2 = 1.5707963267;         
const float m_pi   = 3.1415926535;          
const float m_2_pi = 6.2831853071;  

//--------------------------------------------------------------------------------------------------------
// RayTracing---------------------------------------------------------------------------------------------
vec3 compute_camera_ray_direction(in vec2 screen_coordinates, in vec3 origin, in vec3 target, float cr )
{
	vec3 cw = normalize(target-origin);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw )*normalize(vec3(screen_coordinates,2.0));
}

float plane_intersection(in vec3 ro, in vec3 rd, in vec4 plane)
{
    return -(dot(ro,plane.xyz)+plane.w)/dot(rd,plane.xyz);
}

//--------------------------------------------------------------------------------------------------------
// Matrix Maths ------------------------------------------------------------------------------------------
float det2x2(in mat2 m)         {return (m[0][0]*m[1][1] - m[0][1]*m[1][0]);}
mat2  inv2x2(in mat2 m)         {return (1./det2x2(m))*mat2(m[1][1], -m[0][1], -m[1][0], m[0][0]);}
mat2  tra2x2(in mat2 m)         {return mat2(m[0][0],m[1][0],m[0][1],m[1][1]);}

//--------------------------------------------------------------------------------------------------------
// PRNG --------------------------------------------------------------------------------------------------
// Pseudo Random Number Generation
// From Texton Noise Source Code provided by Arthur Leclaire et al. 
// https://www.idpoisson.fr/galerne/texton_noise/index.html
// Sourced ::
/* 
 * From http://www.reedbeta.com/blog/2013/01/12/quick-and-easy-gpu-random-numbers-in-d3d11/
 * Same strategy as in Gabor noise by example
 * Apply hashtable to create cellseed
 * Use a linear congruential generator as fast PRNG
 */

uint  wang_hash(uint seed)                                          {seed=(seed^61u)^(seed>>16u);seed*=9u;seed=seed^(seed>>4u);seed*=668265261u;seed=seed^(seed>>15u);return(seed);}
uint  cell_seed(const in ivec2 c, const in uint offset)             {const uint period=1024u;uint s=((uint(c.y)%period)*period+(uint(c.x)%period))*period+offset; if(s==0u){s = 1u;}return(s);}
uint  myrand(inout uint p)                                          {p^=(p<<13u);p^=(p>>17u);p^=(p<<5u);return p;}
float myrand_uniform_0_1(inout uint p)                              {return float(myrand(p))/float(4294967295u);}
float myrand_uniform_m_M(inout uint p, in float mi, in float ma)    {return mi + (myrand_uniform_0_1(p) * (ma - mi));}

//--------------------------------------------------------------------------------------------------------
// Gaussians utils ---------------------------------------------------------------------------------------

/* Elliptical Gaussian Covariance Matrix    */
/* From RotationAngle, ScaleX, ScaleY       */
/* Geometric definition of the CovMatrix    */
mat2 RxSxSxRinv(in float theta, in float sx, in float sy)
{
    float A = cos(theta);
    float B = sin(theta);
    float A2 = A*A;
    float B2 = B*B;
    float sx2 = sx*sx;
    float sy2 = sy*sy;

    float r00 = A2*sx2+B2*sy2;
    float r11 = A2*sy2+B2*sx2;
    float r01 = A*B*sx2-A*B*sy2;
    float r10 = r01;
    return mat2(r00,r01,r10,r11);
}

/* K : \lambda    */
/* M : \mu        */
/* S : \Sigma     */
float gaussian(in vec2 X, in float K, in vec2 M, in mat2 S)
{
    vec2 P = X-M;
    return( K * exp( -0.5 * dot(P,inv2x2(S)*P) ) );
}

/* K : \lambda                              */
/* M : \mu                                  */
/* S : \Sigma                               */
/* Kp: Pixel footprint amplitude            */
/* Sp: Pixel footprint covariance matrix    */
float gaussian_filtered(in vec2 X, in float K, in vec2 M, in mat2 S, in float Kp, in mat2 Sp)
{
    mat2  invSp = inv2x2(Sp);
    mat2  invS = inv2x2(S);
    vec2  XM = X-M;
    mat2  S3 = inv2x2(invSp + invS);   
    return m_2_pi * sqrt(det2x2(S3)) * gaussian(XM,K*Kp,vec2(0.),S+Sp);
}


//--------------------------------------------------------------------------------------------------------
// Custom Kernel -----------------------------------------------------------------------------------------
float kernel(in uint prng, in vec2 xy, in int cell_id, in float Kp, in mat2 Sp, in bool anti_aliasing, in float orientation)
{
    float theta = orientation; 
    if(cell_id%2 == 0)
        theta += m_pi_2;

    if(anti_aliasing)
        return gaussian_filtered(xy, 1., vec2(0.0), RxSxSxRinv(theta,0.6,0.05), Kp, Sp);
    return gaussian(xy, 1., vec2(0.0), RxSxSxRinv(theta,0.6,0.05));
}

//--------------------------------------------------------------------------------------------------------
// Local Spot Noise --------------------------------------------------------------------------------------
float local_spot_noise(in vec2 texture_coords, in bool anti_aliasing)
{
    vec2 scaled_coords = LSN_RESOLUTION * texture_coords ;   
    vec2 cell_coords = fract(scaled_coords);
    vec2 cell_index = floor(scaled_coords);

    vec2 dUdx = 0.5 * dFdx(scaled_coords);
    vec2 dUdy = 0.5 * dFdy(scaled_coords);
    mat2  Jc = mat2(dUdx,dUdy); 
    mat2  Sp = Jc*tra2x2(Jc);
    float Kp = 1.0/(m_2_pi*sqrt(det2x2(Sp))); 

    ivec2 cell_ID;
    uint  seed, prng;
    vec2  cell_shot;


    float distribution 	= 0.;
    float chaos_max 	= 0.;
    float orientation 	= 0.;
    float perturbation 	= 0.;

#ifdef KERNEL_ANIMATION    
    distribution  = clamp( 0.5*sin(iTime), 0., 1. ); 
    chaos_max     = clamp( 0.2*sin(0.2*iTime), 0., 1. ) * m_pi_2; 
    orientation   = clamp( cos(iTime), 0., 1. ) * m_pi_2;
#endif

    float sum = 0.0;
    float wei = 1.0/float(LSN_IMPULSES);
    float amp = 1.0;
	for (int m = -1; m <= +1; m++)
    for (int n = -1; n <= +1; n++)
    {
        cell_ID.x = int(cell_index.x) + m;
        cell_ID.y = int(cell_index.y) + n;
        seed  = cell_seed(cell_ID, LSN_GLOBAL_SEED);
        prng  = wang_hash(seed);


        vec2 xy = cell_coords-vec2(m,n);
        for(int k=0;k<LSN_IMPULSES;k++)
        {
            cell_shot.x = myrand_uniform_m_M(prng, 0.5-distribution, 0.5+distribution);  
            cell_shot.y = myrand_uniform_m_M(prng, 0.5-distribution, 0.5+distribution); 
            perturbation = myrand_uniform_m_M(prng, 0., chaos_max);
            sum += kernel(prng, xy-cell_shot, cell_ID.x+cell_ID.y, Kp, Sp, anti_aliasing, orientation + perturbation);
        }
    }
    return sum * wei;
}


