void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coords = ((2.*fragCoord.xy-iResolution.xy) / iResolution.y);
    vec2 mouse = clamp(iMouse.xy/iResolution.xy, 0., 1.);

    // IQ - Raymarching Primitives https://www.shadertoy.com/view/Xds3zN Camera controls
    vec3 origin = vec3( 0.5+3.5*cos(0.1*iTime + 6.0*mouse.x), 2.3 + 2.0*mouse.y, -0.5+3.5*sin(0.1*iTime + 6.0*mouse) );
    vec3 target = vec3( 0.5, -0.4, -0.5 );
    vec3 direction = compute_camera_ray_direction(coords, origin, target, 0.0);

    // Background Color
    fragColor = vec4(vec3(0.),1.);
    float hit = plane_intersection(origin,direction,vec4(0.,1.,0.,0.));
    if(hit>0.0)
    {
        vec3 hit_position = (origin + hit*direction);
        vec2 texture_coords = hit_position.xz/5.;
        bool AA = (coords.x > 0.);      

        float spot_noise = local_spot_noise(texture_coords,AA);
        fragColor.rgb   = vec3(spot_noise);
    }
    fragColor.rgb*= smoothstep( 0.0, 0.01, abs(coords.x) );
}

#define PI 3.14159265358979323846
#define TWO_PI 6.28318530718

// http://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float equiTri(vec2 p){
  float side=sqrt(3.);
  p.x=abs(p.x)-1.;
  p.y=p.y+1./side;
  if(p.x+side*p.y>0.){
    p=vec2(p.x-side*p.y,-side*p.x-p.y)/2.;
  }
  p.x-=clamp(p.x,-2.,0.);
  return-length(p)*sign(p.y);
}

float sdCircle(vec2 p,float r)
{
  return length(p)-r;
}

float sdPentagon(in vec2 p,in float r)
{
  const vec3 k=vec3(.809016994,.587785252,.726542528);
  p.x=abs(p.x);
  p-=2.*min(dot(vec2(-k.x,k.y),p),0.)*vec2(-k.x,k.y);
  p-=2.*min(dot(vec2(k.x,k.y),p),0.)*vec2(k.x,k.y);
  return length(p-vec2(clamp(p.x,-r*k.z,r*k.z),r))*sign(p.y-r);
}

// http://www.iquilezles.org/www/articles/palettes/palettes.htm
// As t runs from 0 to 1 (our normalized palette index or domain),
//the cosine oscilates c times with a phase of d.
//The result is scaled and biased by a and b to meet the desired constrast and brightness.
vec3 cosPalette(float t,vec3 a,vec3 b,vec3 c,vec3 d)
{
  return a+b*cos(6.28318*(c*t+d));
}

// Rotate around a coordinate axis (i.e. in a plane perpendicular to that axis) by angle <a>.
// Read like this: R(p.xz, a) rotates "x towards z".
// This is fast if <a> is a compile-time constant and slower (but still practical) if not.
void pR(inout vec2 p,float a){
  p=cos(a)*p+sin(a)*vec2(p.y,-p.x);
}

vec2 tile(vec2 st,float zoom){
  st*=zoom;
  // _st.x += step(1., mod(_st.y,2.0)) * 0.5;
  return fract(st+iTime);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
  vec2 myRes = iResolution.yy;
  vec2 st=fragCoord/myRes.xy;
  vec3 color=vec3(0.);

  st=tile(st,4.);

  // normalize the coordinate space
  st-=vec2(.5);
  st*=2.;

  // spiiiiiin
  pR(st,mod(iTime,TWO_PI));

  // distance shapes
  float triangle=equiTri(st*vec2(3.));
  float circle=sdCircle(st*vec2(3.),.5);
  float pentagon=sdPentagon(st*vec2(3.),.5);
  float changingShape=sin(iTime)*(triangle-circle)+circle;
  // this makes a flying squirrel
  changingShape=sin(iTime)*(changingShape-pentagon)+pentagon;

  vec3 col=cosPalette(.5,vec3(.5),vec3(.5),vec3(1.),vec3(iTime*.01,iTime*.1,iTime*.2));

  // lighting: darken at the center
  col=vec3(changingShape)*col;

  // output: pixel color
  fragColor=vec4(col.rgb,1.);
}

//There are many tetrahedrons on shadertoy that don't round(not euclidean)-- I wanted one that did--//
//Is it euclidean accurate? IDK but seems reasonable at least-(it lines up with a euclidean sphere at least)
//You can also create octrahedon and other cutoff polyhedron shapes by adjusting gTetOffset
//Also irregular shaped tetrahadron by using non-uniform box

float maxElem(vec4 a){
    return max(a.x, max(a.y, max(a.z,a.w)));
}
//A box in 4D tetrahedral space
float sdBox4( vec4 p, vec4 b )
{
    vec4 d = abs(p) - b;
    return min(maxElem(d),0.0) + length(max(d,0.0));
}

float gTetOffset = 1.0;//using 1 makes this a tet, using 0 makes it an octahedron

//Convert from euclidean to tetrahedral space
vec4 GetTetCoord(vec3 p){
    float offset = gTetOffset;//sqrt(2.0);
    float s1 = 1.0/sqrt(4.0);
    float s2 = 1.0/sqrt(2.0);
    float s3 = 1.0;

    return vec4(offset - p.z*s1 - s2*p.x,
                offset - p.z*s1 + s2*p.x,
                offset + p.z*s1 + s2*p.y,
                offset + p.z*s1 - s2*p.y)*s3;
}

float sdTetrahedron(vec3 p, float size, float roundness){
    vec4 t = GetTetCoord(p); 

    float scaleDown = 1.0/sqrt(4.0);//scale it to match expectation of size--
    float offsetSize = gTetOffset;//offset size to expectation of tet
    size = size*scaleDown + offsetSize ;

  //roundness *= scaleDown;
    return sdBox4(t, vec4(size))-roundness; //rounded tetrahedon, yah!
    //return sdBox4(t, vec4(size,size*.05,size*1.0, size*3.0))-roundness; //can do irregular shaped tets also
  //return maxElem(abs(t)) - size;//max norm tetrahedron as seen in many shadertoys
}

