// Based on Spherical harmonics solver by Roninkoi
// @ https://www.shadertoy.com/view/3dKGWG
//
// Thanks mainly to optimized factorial calculations this shader gets upto order L=16
// while still retaining a little bit of semblance!
//
// (Factorials are incredibly wasteful, both computationally and cognitively,
// for simple things like multiplying all the numbers between A and B together.)
//
// Changes: 
// - Automatic mode to go through all configurations (using triangular numbers!)
// - Combining factorials to cancel unnecessary calculations
// - Calculating factorials using floating-point to cope better with the range of values
// - Using bit-shifting to calculate powers of -1 without looping

// Created by inigo quilez - iq/2013
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.


// Four bands of Spherical Harmonics functions (or atomic orbitals if you want). For
// reference and fun.

precision highp float;

// azimuthal quantum number
#define L 11

// magnetic quantum number
#define M 1

#if HW_PERFORMANCE==0
#define AA 1
#else
#define AA 2   // antialias level (try 1, 2, 3, ...)
#endif

#define AUTOMATIC
//#define SHOW_SPHERES

//---------------------------------------------------------------------------------

#define PI 3.1415926535898
#define E 2.7182818284591

int factorial(int n) // n!
{
    int r = 1;

    for (int i = 2; i <= n; ++i) {
        r *= i;
    }

    return r;
}
float factorialDiv(int n, int j) // ((n+j)!)/((n-j)!)
{
    float r = 1.0;
    for (int i = n-j+1; i <= n+j; i++) {
        r *= float(i);
    }

    return r;
}
float factorialMul(int n, int j) // ((n+j)!)*(n!)
{
    float r1 = 1.0;
    for (int i = 2; i <= n; i++) {
        r1 *= float(i);
    }
    float r2 = 1.0;
    for (int i = n+1; i <= n+j; i++) {
        r2 *= float(i);
    }

    return r1*r1*r2;
}
float factorialDivDividedByMul(int l, int k, int m) { // ((l+k)!)/((l-k)!) /  ((k+m)!)*(k!)

    float r1 = 1.0;
    float r2 = 1.0;
    float r3 = 1.0;
    for (int i = l-k+1; i <= l+k  ; i++) r1 *= float(i);
    for (int i = 2    ; i <=   k  ; i++) r2 *= float(i);
    for (int i = k+1  ; i <=   k+m; i++) r3 *= float(i);
    return r1/(r2*r2)/r3;
}

float powerim1(float x, int n) // negative power
{
    return float(1-((n&1)<<1));
}

float poweri(float x, int n) // negative power
{
/*    float p = 1.;

    for (int k = 0; k < n; ++k) {
        p *= x;
    }

    return p;*/
    return pow(x,float(n));
}

// hypergeometric
float F(int l, int m, int k, float x)
{
    //float f0 = float(factorialDiv(l + k)) / float(factorial(l - k));
    //float f0 = factorialDiv(l,k);
    float f0 = factorialDivDividedByMul(l,k,m);

    //float f1 = powerim1(-1., k) / float(factorial(m + k) * factorial(k));
    //float f1 = powerim1(-1., k) / factorialMul(k, m);
    float f1 = powerim1(-1., k);

    float f2 = poweri((1. - x) * 0.5, k);

    return f0 * f1 * f2;
}

// Legendre polynomials from hypergeometric, negative m
float Pgm(int l, int m, float x)
{
    float lf = float(l);
    float mf = float(m);

    float a = pow((1. - x) / (1. + x), mf * 0.5);

    float s = 0.0;

    for (int k = 0; k <= l; ++k) {
        s += F(l, m, k, x);
    }

    return a * s;
}

// Legendre polynomials from hypergeometric
float Pg(int l, int m, float x)
{
    float lf = float(l);
    float mf = float(m);

    //float a = float(factorial(l + m) / factorial(l - m));

    float pm = Pgm(l, m, x);

    //return a * pm;
    return pm;
}

// Y_l_m(s), where l is the band and m the range in [-l..l] 
float SH( in int l, in int m, in vec3 s ) 
{
    vec3 ns = normalize(s);

    if (m < 0) {
        float c = ns.x;
        ns.x = ns.z;
        ns.z = c;
        m = -m;
    }

    // quantum mechanics convention
    float a0 = sqrt(
//    	float(2 * l + 1) / float(factorial(l + m)/factorial(l - m)) / (4.0 * PI)
//    	float(2 * l + 1) * float(factorial(l + m)/factorial(l - m)) / (4.0 * PI)
    	float(2 * l + 1) * factorialDiv(l,m) / (4.0 * PI)
    );

    // spherical coordinates
    float thetax = ns.y;
    float phi = atan(ns.z, ns.x);

    float pl = Pg(l, m, abs(thetax));
    if (thetax < 0.0) pl *= powerim1(-1., l + m);

    float r = powerim1(-1.0, (m)) * a0 * cos(float(m) * phi) * pl;

    return r;
}

vec3 map( in vec3 p )
{
    vec3 p00 = p;

	float r, d; vec3 n, s, res;

    #ifdef SHOW_SPHERES
	#define SHAPE (vec3(d-0.35, -1.0+2.0*clamp(0.5 + 16.0*r,0.0,1.0),d))
	#else
	#define SHAPE (vec3(d-abs(r), sign(r),d))
	#endif

    int l = L;
    int m = M;
    #ifdef AUTOMATIC
    int x = ((iFrame>>1)&0x7FF)/15;
    int Y = int(floor(sqrt(0.25+float(2*x))-0.5));
    int X = int(floor(float(x)-0.5*float(Y+Y*Y)));
    l = Y+1;
    m = X+1;
    #endif
	d=length(p00); n=p00/d; r = SH(l, m, p ); s = SHAPE; res = s;

	return vec3( res.x, 0.5+0.5*res.y, res.z );
}

vec3 intersect( in vec3 ro, in vec3 rd )
{
	vec3 res = vec3(1e10,-1.0, 1.0);

	float maxd = 3.0;//10.0;
    float h = 1.0;
    float t = 1.0;
    vec2  m = vec2(-1.0);
    for( int i=0; i<100; i++ )
    {
        if( h<0.001||t>maxd ) break;
        vec3 p = ro+rd*t ;
	    vec3 res = map(p);
        h = res.x;
		m = res.yz;
        //t += h*0.3;
        t += h*0.2;
    }
	if( t<maxd && t<res.x ) res=vec3(t,m);


	return res;
}

vec3 calcNormal( in vec3 pos )
{
    vec3 eps = vec3(0.001,0.0,0.0);

	return normalize( vec3(
           map(pos+eps.xyy).x - map(pos-eps.xyy).x,
           map(pos+eps.yxy).x - map(pos-eps.yxy).x,
           map(pos+eps.yyx).x - map(pos-eps.yyx).x ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // camera
    float an = 10.0*iMouse.x/iResolution.x;
    float am = 2.*iMouse.y/iResolution.y - 1.;
    float turn = radians(360./256.)*float(iFrame&0xFF);
    vec3  ro = vec3(1.5*sin(an+turn),1.5*sin(am),1.5*cos(an+turn)) * 1.0;
    vec3  ta = vec3(0.0,0.0,0.0);

    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));

    vec3 tot = vec3(0.0);

    #define ZERO min(iFrame,0)
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {        
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+vec2(float(m),float(n))/float(AA))) / iResolution.y;

        // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 2.0*ww );

        // background 
        vec3 col = vec3(0.3) * clamp(1.0-length(p)*0.5,0.0,1.0);

        // raymarch
        vec3 tmat = intersect(ro,rd);
        if( tmat.y>-0.5 )
        {
            // geometry
            vec3 pos = ro + tmat.x*rd;
            vec3 nor = calcNormal(pos);
            vec3 ref = reflect( rd, nor );

            // material		
            vec3 mate = 0.5*mix( vec3(1.0,0.6,0.15), vec3(0.2,0.4,0.5), tmat.y );

            float occ = clamp( 2.0*tmat.z, 0.0, 1.0 );
            float sss = pow( clamp( 1.0 + dot(nor,rd), 0.0, 1.0 ), 1.0 );

            // lights
            vec3 lin  = 2.5*occ*vec3(1.0,1.00,1.00)*(0.6+0.4*nor.y);
                 lin += 1.0*sss*vec3(1.0,0.95,0.70)*occ;		

            // surface-light interacion
            col = mate.xyz * lin;
        }

        // gamma
        col = pow( clamp(col,0.0,1.0), vec3(0.4545) );
        tot += col;
    }
    tot /= float(AA*AA);
    fragColor = vec4( tot, 1.0 );
}

