    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*4378.5453);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;
    vec3 color = vec3(.0);

    // Scale
    st *= 2.;

    // Tile the space
    vec2 i_st = floor(st);
    vec2 f_st = fract(st);

    float m =10.;

    for (int j=-1; j<=1; j++ ) {
        for (int i=-1; i<=1; i++ ) {
            vec2 neighbor = vec2(float(i),float(j));
            vec2 point = random2(i_st + neighbor);
            point = 0.5 + 0.5*sin(iTime + 6.2831*point);
            vec2 diff = neighbor + point - f_st;
            float dist = length(diff);

            m = min(m,m*dist);
        }
    }

    st *= 20.;
    vec2 pt = vec2(floor(st)+0.5);
    float c = (1.0-length(st-pt))*(1.0-m*0.5);
    color = vec3(1.0-smoothstep(0.,0.075,abs(0.4-c)));


    fragColor = vec4(color,1.0);
}

//credit: http://jamie-wong.com/2016/07/15/ray-marching-signed-distance-functions/

#define SHADOW_ITER 128
#define AO_ITER 3
#define MARCH_ITER 200
#define MAX_MARCH_DIST 50.0
#define EPS 0.00005
#define FOV 45.0

//raise this for higher fidelity
#define AA 1.

struct PointLight {
    vec3 pos;
    vec3 color;
    float intensity;
};
struct Material {
    vec3 diffuseColor;
    vec3 specularColor;
    float specularPow;
    float specularIntensity;
};
struct Sphere {
    vec3 O;
    float radius;
};
struct Box {
	vec3 O;
    vec3 size;
};
struct Wall {
    float x;
};
const Material M1 = Material(vec3(0.75, 0.73, 0.6), vec3(1.), 25., 1.7);
const Material M2 = Material(vec3(0.2,0.4,1.0), vec3(1.), 2., .3);
const Material M3 = Material(vec3(0.39, 0.8, 0.9), vec3(1.), 5., 1.);

Sphere S = Sphere(vec3(-1.,-1.,-5.), 1.);
Box B = Box(vec3(2., -.6, -3.), vec3(.3,1.4,.3));
Wall Wx = Wall(-3.);
Wall Wy = Wall(-2.);
Wall Wz = Wall(-15.);

vec2 minim(vec2 a, vec2 b) {
	return a.x < b.x ? a : b;
}

/*
---------------SDFs---------------
*/

float planexSDF(float x, vec3 point) {
 	return point.x - x;   
}
float planeySDF(float y, vec3 point) {
 	return point.y - y;   
}
float planezSDF(float z, vec3 point) {
    return point.z - z;
}
float sphereSDF(vec3 O, float radius, vec3 point) {
 	return length(point - O) - radius;   
}
float boxSDF(vec3 O, vec3 dim, vec3 point)
{
    point -= O;
    vec3 q = abs(point) - dim;
    return length(max(q, 0.0)) + min(max(q.x,max(q.y,q.z)), 0.0);
}
vec2 sceneSDF(vec3 point) {
    vec2 b = vec2(boxSDF(B.O, B.size, point), 3.);
    vec2 s = vec2(sphereSDF(S.O, S.radius, point), 1.);
    vec2 p1 = vec2(planexSDF(Wx.x, point), 2.);
    vec2 p2 = vec2(planeySDF(Wy.x, point), 2.); 
    vec2 p3 = vec2(planezSDF(Wz.x, point), 2.);
    return minim(b, minim(s, minim(p1, minim(p2, p3))));
}

vec3 getNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPS, p.y, p.z)).x - sceneSDF(vec3(p.x - EPS, p.y, p.z)).x,
        sceneSDF(vec3(p.x, p.y + EPS, p.z)).x - sceneSDF(vec3(p.x, p.y - EPS, p.z)).x,
        sceneSDF(vec3(p.x, p.y, p.z  + EPS)).x - sceneSDF(vec3(p.x, p.y, p.z - EPS)).x
    ));
}

/*
-------------MARCHING---------------
*/

vec2 marchStep(vec3 eye, vec3 dir, float start, float end) {
    float depth = start;
    for (int i = 0; i < MARCH_ITER; i++) {
        vec2 dist = sceneSDF(eye + depth*dir);
        if (dist.x < EPS)
			return vec2(depth, dist.y);

        depth += dist.x;
        if (depth >= end) 
            return vec2(end, 0.);
    }

    return vec2(end, 0.);
}

vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = (size.y/2.) / tan(radians(fieldOfView / 2.));
    return normalize(vec3(xy, -z));
}

/*
----------------AO---------------
*/

//	This has faster falloff for AO intensity and fewer iterations
float AO(vec3 point, vec3 dir, float start) {
	float depth = start, dist;
    float step = 0.05, falloff = 1.0;
    float ao = 0.0;
    for(int i = 0; i < AO_ITER; ++i) {
        dist = sceneSDF(point + depth*dir).x;
        ao += falloff*clamp(depth - dist, 0.0, 1.0);
        depth += step;
        falloff *= 0.9;
    }

    return clamp(1.0 - 1.5*ao, 0.0, 1.0);
}

/*
-------------SHADOWS-------------
*/
//  Traditional, geometrical sharp shadows
float sharpShadow(vec3 point, vec3 dir, float start, float end) {
 	float depth = start, dist;
    for(int i = 0; i < SHADOW_ITER; ++i) {
     	dist = sceneSDF(point + depth*dir).x;  
        depth += dist;
        if(depth >= end)
            return 1.0;
        if(dist < EPS)
            return 0.0;
    }

    return 1.0;
}
//	Not very realistic, the soft shadow actually expands with distance
//and exceeds the geometrical projection
float softShadow(vec3 point, vec3 dir, float start, float end) {
 	float depth = start, dist, prevdist = 10e9;
    float shadow = 1.0;
    for(int i = 0; i < SHADOW_ITER; ++i) {
     	dist = sceneSDF(point + depth*dir).x;
        if(depth > end || shadow < EPS)
            break;
    //Traditional
        //shadow = min(shadow, 35.*dist/min(depth, 5.));
    //Sebastian Aaltonen's improvement
        float delta = dist*dist/(2.*prevdist);
        float dist2 = sqrt(dist*dist - delta*delta);
    //We clamp depth because we dont want the shadow to grow big and not look realistic
        shadow = min(shadow, 35.*dist2/max(0.0, min(depth - delta, 5.))); 
        prevdist = dist;

        depth += dist;
    }

    return clamp(shadow, 0.0, 1.0);
}
//	A more realistic model in which the casted ray goes through objects so that the shadow stays
//inside the geometrical projection, it doesnt expand and instead it softens on the inside edges like irl
float diffrShadow(vec3 point, vec3 dir, float start, float end) {
 	float depth = start, dist;
    float shadow = 1.0;
    for(int i = 0; i < SHADOW_ITER; ++i) {
     	dist = sceneSDF(point + depth*dir).x;
        if(depth > end || shadow < -1.0)
            break;

        shadow = min(shadow, 40.*dist/depth);
    	depth += clamp(dist, 0.005, 10.0);
    }

    shadow = max(shadow, -1.0);
    return smoothstep(-1.0, 0.0, shadow);
}

/*
---------------PHONG---------------
*/

vec3 PhongContribForPointLight(vec3 point, vec3 normal, Material M, PointLight light) {
	vec3 lightDir = light.pos - point;
    float d = length(lightDir);
    lightDir = lightDir / d;
    float attenuation = 1./ (.02*d*d + d + 1.);

    float cosine = max(0., dot(normal, lightDir));
    //vec3 scaledNormal = normal * cosine;
    vec3 specular = vec3(0.);
    if(cosine > EPS) {	//remove condition for smooth transition at extreme angles
        vec3 reflected = reflect(-lightDir, normal);	//normalize(2.*scaledNormal - lightDir);
        specular = light.color * M.specularIntensity * M.specularColor * attenuation *
        				pow( max(0., dot(reflected, normalize(-point)) ), M.specularPow );
    }

    vec3 diffuse = light.color * M.diffuseColor * light.intensity * attenuation * cosine;
    return diffuse + specular;  
}
vec3 PhongIllumination(vec3 point, vec3 normal, float colorCode) {
    Material M;
    if(colorCode == 1.)
        M = M1;
    else if(colorCode == 2.)
        M = M2;
    else
        M = M3;

    vec3 ambient = vec3(.79,.79,1.) * .05;
    vec3 color = ambient;

    PointLight light = PointLight(vec3(5.0 + .5*sin(iTime), 3.+cos(iTime), -1.0), vec3(.9, 1., .7), 4.);
    vec3 lightDir = light.pos - point;
    float lightDist = length(lightDir);
    lightDir /= lightDist;

    //! Soft Shadows cause problems with banding at high distances !
    //Change shadow technique here
    color += PhongContribForPointLight(point, normal, M, light) * diffrShadow(point, lightDir, .01, lightDist);

    light = PointLight(vec3(1.-cos(iTime),1.,4.), vec3(1., .7, .8), 4.);
    lightDir = light.pos - point;
    lightDist = length(lightDir);
    lightDir /= lightDist;
    //Change shadow technique here
    color += PhongContribForPointLight(point, normal, M, light) * diffrShadow(point, lightDir, .01, lightDist);

    return color * AO(point, normal, 0.001);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 eye = vec3(0.,0.,5.);
    vec3 color = vec3(.0);
    vec2 offset;
    //MSAA
    for(float i = 1.; i <= AA; ++i) {
    	offset.x = -0.5 + i/(AA+1.);
         for(float j = 1.; j <= AA; ++j) {
            offset.y = -0.5 + j/(AA+1.);
            vec2 pixel = fragCoord + offset;

            vec3 rayDir = rayDirection(FOV, iResolution.xy, pixel);
            vec2 inters = marchStep(eye, rayDir, .5, MAX_MARCH_DIST);

            if(inters.x < MAX_MARCH_DIST - EPS) {
                vec3 hit = eye + inters.x * rayDir;  
                vec3 normal = getNormal(hit);
                color += PhongIllumination(hit, normal, inters.y);
    		} 
        }
    }
    color /= AA*AA;

    fragColor = vec4(color, 1.0);
}

#define PI 3.14159265358979
#define TWOPI (PI * 2.0)

float tri(vec2 p, vec2 c, float r, float a) {
    int n = 6;
    vec2 pc = p - c;
    float th = atan(pc.x, pc.y) + PI + a;
    float thr = TWOPI / float(n);
    float d = cos(floor(th/thr+0.5)*thr-th)*length(pc);
    return smoothstep(r,r+0.01,d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime / 1.92;
    vec2 p = fragCoord / iResolution.xy;
    vec2 pc = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    pc *= 2.0;

    //vec3 col = vec3(d);
    float r = mod(t, 1.0); 
    float a = t;
    float d = 0.0;
    for (int i=1; i<=4; i++) {
        float r2 = pow(mod(r + float(i) * 0.25, 1.0) * 1.9, 1.24);
        d += (tri(pc, vec2(0.0), r2, a) * (1.0 - tri(pc, vec2(0.0), r2 + 0.01, a*1.5)));
    }
    vec3 col = vec3(1.0-d);

    fragColor = vec4(col, 1.0);
}

#define MAX_STEPS 100
#define SURF_DIST .01
#define MAX_DIST 100.
#define PI 3.14159265359

float DToRad(float d)//degree to radian
{
 	 return d /180.f * PI  ;
}

mat2 Rot(float angle)
{
 	float s = sin(angle);
    float c= cos(angle);
    return mat2(c, -s, s, c);
}

float smin(float a, float b , float k)
{
 	float h = clamp(.5 + .5*(b -a)/k, 0., 1.);
    return mix(b,a,h) - k*h*(1.f - h);
}

float Sphere(vec3 p, float r)
{
 	return length(p) - r;   
}

float Torus(vec3 p, float r1, float r2)//r1 bigger radius
{
    float x = length(p.xz) - r1;
    float y = p.y;
    float d = length(vec2(x,y));
    return d - r2;
}

float Capsule(vec3 p, vec3 a, vec3 b, float r, vec3 pos)
{
    p-=pos;
 	vec3 ab = b-a;
    vec3 ap = p-a;
    float ratio = clamp((dot(ap, ab) / dot(ab,ab)), 0., 1.);

    vec3 PointC = a + ab * ratio;
    return length(PointC - p)-r;
}


float Cylinder(vec3 p, vec3 a, vec3 b, float r, vec3 pos)
{
    p-=pos;
 	vec3 ab = b-a;
    vec3 ap = p-a;
    float t = dot(ap, ab) / dot(ab,ab);    
    vec3 PointC = a + ab * t;
    float d = length(PointC - p)-r;
    float y = abs(t-.5f) - .5f;// make the dist on y range from infinite...0...-0.5...0...infinite 
	float e = length(max(vec2(d,y),0.));
    float i = min(max(d,y),0.);
    return e+i;
}

float Link( in vec3 p, in float le, in float r1, in float r2 ,vec3 pos )
{
    p-=pos;
    vec3 q = vec3( p.x, max(abs(p.y)-le,0.0), p.z );
    return length(vec2(length(q.xy)-r1,q.z)) - r2;
}

float Box(vec3 p, float side)
{
    float dx = max(abs(p.x) - side,0.);
    float dy = max(abs(p.y) - side,0.);
    float dz = max(abs(p.z) - side,0.);
    return length(vec3(dx,dy,dz));
}

float GetDist(vec3 p)//a simple scene with just a ground plane and a sphere
{
    float t = iTime;

    // ground plane
    float pd = p.y;

    // rotating box & sphere transformation
    vec3 bp = p ;
    bp -= vec3(0,.75,3);		// translation
    bp.xz *= Rot(iTime);		// rotation
    float dsBox = Box(bp, 1.f);
    float dsSphere = Sphere(bp,1.8f);
    vec3 sp_B = p;
    sp_B -= vec3(0, 2.75f,3);
    sp_B.y -= 2.f*(sin(iTime * .5f));
    float dsSp_Blend = Sphere(sp_B,.8f);



    //Cylinder

    vec3 CylinderP = vec3(1.f,1.f,0.f);
    CylinderP.xz+=vec2(sin(iTime)*5.f, cos(iTime)*5.f);//move
    CylinderP.xz *= Rot(iTime*1.5f);
    float dsCylinder = Cylinder(p, vec3(0,2,0),vec3(3,2,0),.2f, CylinderP);
    float dsCylinder2 = Cylinder(p, vec3(1.5f,2,-1.5f),vec3(1.5f,2,1.5f),.2f, CylinderP);
    float dsCylinder3 = Cylinder(p, vec3(1.5f,2.f+1.5f,0),vec3(1.5f,2.f-1.5f,0),.2f, CylinderP);
    dsCylinder = mix(dsCylinder, dsCylinder2, abs(sin(iTime)));
    dsCylinder = smin(dsCylinder, dsCylinder3,.3);


    //Sphere substraction
    vec3 Sp1 = vec3(-2.f, 1.f, -2.f);
    Sp1 = p-Sp1;//translate    
    Sp1.y -= abs(sin(iTime));//jump
    Sp1.xz +=vec2(sin(iTime), cos(iTime));   
    vec3 Sp2 = vec3(-1.f, 1.f, -2.f);
    Sp2 = p-Sp2;   
    Sp2.y -= abs(sin(iTime));
    Sp2.x += .4f*sin(iTime);    
    float dsSp = Sphere(Sp1, 1.f);
    float dsSp2 = Sphere(Sp2, .8f);
    dsSp = max(-dsSp2,dsSp);
    dsSp = smin(dsSp,dsCylinder,.3f);


    //Torus

    vec3 torusP = p;
    torusP -= vec3(3.f + sin(iTime) * 5.f,1.f,(-2.f) + cos(iTime)*5.f);
    torusP.y -= abs(sin(iTime)) * 3.f;//translate
    torusP *= vec3(1.f + abs(sin(iTime)),1,1);//scale
    torusP.yz *= Rot(DToRad(90.f));//rotate

    float dsTorus = Torus(torusP, 1.f,.25f);




    //final dist calculation
    float d;
    d = mix(dsBox, dsSphere, abs(sin(iTime)));
    d = smin(d,dsSp_Blend,.4f);
    d = min(d, pd);
    d = min(d, dsCylinder);
    d = min(d, dsSp);
    d = min(d,dsTorus/2.f);

    return d;
}


vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

float RayMarching(vec3 ro, vec3 rd)
{
    float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = abs( GetDist(p) );
        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }

    return dO;
}



float GetLight(vec3 p, vec3 lightPos) {

    lightPos.xz += vec2(sin(iTime), cos(iTime)) * 2.f;
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n,l),0. , 1.);
    float shadowR = RayMarching(p + n * SURF_DIST * 2., l);
    if(shadowR < length(p -  lightPos))
        dif *= .1f;
    return dif;
}

vec3 Ray(vec2 uv, vec3 ro, vec3 lookat, float dToScreen)//lookat camera
{
    vec3 f = normalize(lookat - ro);
    vec3 right = normalize(cross(vec3(0,1,0),f));
    vec3 up = normalize(cross(f, right));
    vec3 center = ro + f * dToScreen; 
    vec3 intersectP = center + uv.x * right + uv.y * up;
    return normalize(intersectP - ro);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

     vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

