vec2 rotate(vec2 st ,float sita ){
    mat2 rotate = mat2(vec2(sin(sita),cos(sita)),
                       vec2(-cos(sita),sin(sita)));
    return rotate * st;
}

float bendedRect(vec2 st ,vec2 rect ,float offset ,float timeOffset ,float timeFactor){
    float sita = iTime * timeFactor +timeOffset;

    st = abs(st);
    st = rotate(st ,sita);
    st.y -= offset;

    float blur = 0.3;
    float a = smoothstep(-0.08*st.x*st.x-rect.y/2.0-blur ,-0.08*st.x*st.x-rect.y/2.0 ,st.y);
    float b = 1.0 - smoothstep(-0.12*st.x*st.x+rect.y/2.0 ,-0.12*st.x*st.x+rect.y/2.0+blur ,st.y);
    float c = smoothstep(-rect.x/2.0-blur ,-rect.x/2.0 ,st.x);
    float d = 1.0 - smoothstep(rect.x/2.0 ,rect.x/2.0+blur ,st.x);

    return a * b * c * d;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;
    uv *= 20.;

    vec3 color = vec3(0.0);
    for(float i=0.0;i<12.0;i++){
        float r = bendedRect(uv ,vec2(4.0 - i*0.3 ,0.4 ),10.0-i*0.8 ,20.0 - i*20. ,2.8-0.2*i);
        color += r;
    }

    uv = rotate(uv ,iTime);

    // Output to screen
    fragColor = vec4(color * vec3(0.6+uv.x/10.,0.5,0.6+uv.y/10.),1.0);
}

const vec3 beamCol = vec3(.5,.2,.1);
const vec3 glowCol = vec3(0.2,.5,0.2);

const int pointCount = 8;
const vec3 points[pointCount] = vec3[pointCount](
    vec3(.2,+.2,+.2),
    vec3(.2,+.2,-.2),
    vec3(.2,-.2,-.2),
    vec3(.2,-.2,+.2),

    vec3(-.2,+.2,+.2),
    vec3(-.2,+.2,-.2),
    vec3(-.2,-.2,-.2),
    vec3(-.2,-.2,+.2)
);

const int lineCount = 12;
const ivec2 lines[lineCount] = ivec2[lineCount](
    ivec2(0,1),
    ivec2(1,2),
    ivec2(2,3),
    ivec2(3,0),

    ivec2(4,5),
    ivec2(5,6),
    ivec2(6,7),
    ivec2(7,4),

    ivec2(0,4),
    ivec2(1,5),
    ivec2(2,6),
    ivec2(3,7)
    );

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

vec3 dLine(vec2 p, vec2 start, vec2 end,float thickness)
{

    vec2 dir = normalize(end-start);
    float l = length(start-end);
    vec2 ndir = vec2(dir.y,-dir.x);
    float dLine = abs(dot(start-p,ndir));
    float dFuss = dot(p-start,dir);
    if(dFuss<0.)
        dLine = length(p-start);
    else if(dFuss > l)
        dLine = length(p-(start+l*dir));

    thickness+=(rand(p*iTime*dFuss)-0.5)*0.001;

    vec3 ret=smoothstep(3.*thickness,thickness*1.1,dLine)*beamCol;
    if(dLine>thickness)
        ret+=smoothstep(8.*thickness,thickness,dLine)*glowCol;
    return ret;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy/2.)/max(iResolution.x,iResolution.y);

    vec3 up = vec3(0.,0.,1.);
    float camDist = (sin(iTime*.2)+2.);
    vec3 camPos = vec3(camDist*sin(iTime),camDist*cos(iTime),0.8*sin(iTime*0.8));
    vec3 camDir = normalize(-camPos);
    vec3 camX = cross(camDir,up);
    vec3 camY = cross(camDir,camX);

    mat3 camFrame = mat3(camX,camY,camDir);

    vec3 projected[pointCount];
    for(int n=0;n<pointCount;n++)
    {
		projected[n] = (points[n] - camPos)*camFrame;
        projected[n].xy /= projected[n].z;
    }

    fragColor = vec4(0.,0.,0.,1.0);
    for(int n=0;n<lineCount;n++)
    {
    	fragColor += vec4(dLine(uv,projected[lines[n].x].xy,projected[lines[n].y].xy,0.003/projected[lines[n].y].z),1.);
    }
}

#define TWOPI 6.28318530718
#define Multiplier 200000.

//rotate, make lines of varied form, create a silhouette when there are lots of lines

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 r =  2.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;
	vec2 p = vec2(fragCoord.xy / iResolution.xy);


	vec3 pixel = vec3(0.0);

//	float y = cos(mod(10.*iTime, 100.)*TWOPI*p.y); 
    float value = p.y * p.x;
	float y = cos(Multiplier*TWOPI*value + iTime*4.); 

	y = (y+1.0)*0.5; // map [-1,1] to [0,1]

	pixel = vec3(y); 
	fragColor = vec4(pixel, 1.0);
}

#define M_PI 3.1415926535897932384626433832795

vec3 hsl2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 center = vec2(0.5) + vec2(cos(iTime)*0.2, sin(iTime)*0.2);

    vec2 spher = center - uv;

    float dist = length(spher);
    float angle = (atan(spher.x, spher.y) + M_PI) / ( 2.0 * M_PI);
    angle += iTime + dist*(2.0*sin(0.1*iTime));


    // Output to screen
    fragColor = vec4(hsl2rgb(vec3(angle, 0.8, 0.5)),1.0);
}

