struct Ray {
    vec3 o;     // origin
    vec3 d;     // direction - always set with normalized vector
};

float percentOfMarches(float marches){
	return (marches / float(MAX_MARCHES));
}

Ray createRay(vec3 o, vec3 d)
{
    Ray r;
    r.o = o;
    r.d = d;
    return r;
}

struct Camera
{
    vec3 origin;
    vec3 lowerLeftCorner;
    vec3 horizontal;
    vec3 vertical;
    vec3 u, v, w;
    float lensRadius;
};

Camera createCamera(
    vec3 lookFrom,
    vec3 lookAt,
    vec3 worldUp,
    float fovy,
    float aspect)
{
    float theta = fovy * pi / 180.0;
    float halfHeight = tan(theta * 0.5);
    float halfWidth = aspect * halfHeight;

    Camera cam;
    cam.origin = lookFrom;
    cam.w = normalize(lookFrom - lookAt);
    cam.u = normalize(cross(worldUp, cam.w));
    cam.v = cross(cam.w, cam.u);

    cam.lowerLeftCorner = cam.origin - halfWidth * cam.u - halfHeight * cam.v - cam.w;
    //cam.lowerLeftCorner = cam.origin - halfWidth  - halfHeight;
    cam.horizontal = 2.0 * halfWidth * cam.u;
    cam.vertical = 2.0 * halfHeight * cam.v;
    return cam;
}

Ray getRay(Camera cam, vec2 uv)
{
    return createRay(
        cam.origin,
        normalize(cam.lowerLeftCorner + uv.x * cam.horizontal + uv.y * cam.vertical - cam.origin));
}

vec3 modPoint(vec3 pt, vec3 c){
	return mod(pt+0.5*c,c)-0.5*c;
}

float sphereSdf(vec3 pt, vec3 circleOrigin, float circleRadius){
	return length(pt - circleOrigin) - circleRadius;
}

vec2 opSmoothUnion( vec2 d1, vec2 d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2.x-d1.x)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

// map
float distanceEstimator(vec3 pt){
	float speed = 3.0;
	float period = 3.0;
	float movement = (fract(iTime/speed)- .5)*period;
	vec2 c1 = vec2(sphereSdf(modPoint(pt,vec3(period)),vec3(0.),.4) , 23.0);
	vec3 moving = vec3(0.,(fract(iTime/speed) - .5)*period ,0.); // range of y -period to period
	vec2 c2 = vec2(sphereSdf(modPoint(pt-moving,vec3(period)),vec3(0.),.5) , 43.0);

	return opSmoothUnion(c1,c2,0.5).x;
	//return opSmoothUnion(c1,c2,0.5).x;

}

