float dist_field(vec3 p) {
    p.x += cos(0.1 * p.y ) + cos(0.07 * p.z);
    p.y += cos(0.1 * p.z );
    p.z += 3.0*cos(0.1 * p.x);
	p = max(cos(abs(p)), -0.8);
    return length(p) - 0.2;
}

vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        distanceEstimator(vec3(p.x + EPSILON, p.y, p.z)) - distanceEstimator(vec3(p.x - EPSILON, p.y, p.z)),
        distanceEstimator(vec3(p.x, p.y + EPSILON, p.z)) - distanceEstimator(vec3(p.x, p.y - EPSILON, p.z)),
        distanceEstimator(vec3(p.x, p.y, p.z  + EPSILON)) - distanceEstimator(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

vec3 estimateNormalDistField(vec3 p) {
    return normalize(vec3(
        dist_field(vec3(p.x + EPSILON, p.y, p.z)) - dist_field(vec3(p.x - EPSILON, p.y, p.z)),
        dist_field(vec3(p.x, p.y + EPSILON, p.z)) - dist_field(vec3(p.x, p.y - EPSILON, p.z)),
        dist_field(vec3(p.x, p.y, p.z  + EPSILON)) - dist_field(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

vec4 rayMarch(vec3 ro, vec3 rd, vec3 cam_position){

	vec3 point = ro + rd * MIN_DIST;
	int i = 0;
	for(i = 0; i < MAX_MARCHES; ++i){

		float dist = distanceEstimator(point);
		//float dist = dist_field(point);

		if(dist < EPSILON && length(point - cam_position ) > 1.0){

			break;
		}
		point += rd * dist;
	}
	return vec4(point, float(i));
}

