vec3 reflection(vec3 dir, vec3 normal){
	return dir - 2. * dot(dir, normal) * normal;
}

vec4 bounceRay(vec3 pt, vec3 rd, vec3 cam_position){
	vec4 result = rayMarch(pt, rd, cam_position);
	vec4 color = getColor(result.xyz, result.w);

	// bounce once
	//vec4 result2 = rayMarch(result.xyz,estimateNormal(result.xyz));
	//vec4 color2 = getColor(result2.xyz,result2.w);
	//return mix(color,color2,0.9);

	// bounce many times
	vec4 result2 = result; // to start
	for(int i = 0; i < 0; ++i){
		vec3 normal =  estimateNormal(result2.xyz);
		rd = reflection(rd, normal);
		result2 = rayMarch(result2.xyz, rd, cam_position);
		vec4 color2 = getColor(result2.xyz,result2.w);
		color = mix(color,color2,0.6);
	}
	return color;

}


