  }

  vec2 posT= vec2(0.,0.);
  float rT = 1.7+float(mod(iTime,0.25*sin(iTime)));
  float aT = (PI*2.)/3.;
  float offA = iTime;

  vec2 A = vec2(rT*sin(aT*1.+offA),rT *cos(aT*1.+offA))-posT;//+vec2(0.,0.1*sin(iTime));
vec2 B = vec2(rT*sin(aT*2.+offA),rT *cos(aT*2.+offA))-posT;
vec2 C = vec2(rT*sin(aT*3.+offA),rT *cos(aT*3.+offA))-posT;
if(inTriangle(A,B,C,uv,0.1))
  acc=1.-acc;

acc*= mix(vec3(1.),vec3(1.,0.,0.),1.-sat(sdf_rect(uv-vec2(0.,0.),vec2(0.5,2.5))*25.));
acc += (mod(uv.x,0.1)*20.)*vec3(0.4,0.4,0.89)* sat(1.-length(uv))*sat((abs(curve(uv.x)*.5)-abs(uv.y))*5.);
  acc+=(sin(iTime)*0.2+1.3)*2.*pow(sat((1.-lenny(uv*.5))*1.),3.)*vec3(.4,.4,.89);
