    float t = dot(uv-o, d);
    d = d.yx*vec2(-1.,1.);
    float dist = dot(uv-o,d);
    return vec2(abs(dist), t);
}

vec4 DrawPoint( vec2 uv, vec2 pos, vec3 col, float size)
{
    size *= 0.03;
    float dist = length(uv- pos);
    return vec4(col,smoothstep(0.,size,(size- dist)*1.5));
}

vec4 DrawSites( float dist )
{
    return vec4(1.,0.,0., clamp((1.-dist*40.)*2., 0.,1.));
}

vec4 DrawLineSegment(vec2 a, vec2 b, vec2 uv)
{
    if (dot(a-b,a-b) < 0.0001)
        return vec4(0.,0.,0.,0.);
	vec2 o = (a+b)/2.;
    vec2 d = normalize(a-b);

    vec2 dist = DistToLine(uv, o, d);


    float brightness = smoothstep(0.995,1.,1.-dist.x)*2.;
    return  saturate(vec4(1.,1.,1., brightness*saturate(ceil(1.-abs((dist.y)/length(a-b)*2.)))));    
}


vec4 GetEdgeLine(vec2 a, vec2 b)
{
    return vec4((b + a)/2.,normalize(a-b).yx * vec2(-1.,1.));
}

vec4 DrawEdgeLine(vec2 a, vec2 b, vec2 uv, float size)
{
    vec4 edge = GetEdgeLine(a,b);
	vec2 o = edge.xy;
	vec2 d = edge.zw;
	vec4 ret = (DrawLineSegment(o+d*size, o-d*size,uv));
    ret.xyz = vec3(.8,0.4,0.2);
    return ret;

}

