#define TAU 6.283
// https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm

float sdUnevenCapsule(vec2 p, float r1, float r2, float h) {
  p.x = abs(p.x);
  float b = (r1 - r2) / h;
  float a = sqrt(1.0 - b * b);
  float k = dot(p, vec2(-b, a));
  if (k < 0.0)
    return length(p) - r1;
  if (k > a * h)
    return length(p - vec2(0.0, h)) - r2;
  return dot(p, vec2(a, b)) - r1;
}

float sdLine(in vec2 p, in vec2 a, in vec2 b) {
  vec2 pa = p - a, ba = b - a;
  float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
  return length(pa - ba * h);
}

float sdArc( in vec2 p, in vec2 sca, in vec2 scb, in float ra, float rb )
{
    p *= mat2(sca.x,sca.y,-sca.y,sca.x);
    p.x = abs(p.x);
    float k = (scb.y*p.x>scb.x*p.y) ? dot(p.xy,scb) : length(p.xy);
    return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}

float wiggles(vec2 p, float h, float freq, float amp, float speed) {
  return p.y - (h + sin((iTime * speed + p.x) * freq) * amp);
}

float arms(vec2 p, float h, float l, float freq, float amp) {  
  return sdLine(p + vec2(0., h) + vec2(0., sin(p.x * freq) * amp), vec2(-l, 0.), vec2(l, 0.)) - .1;
}

float map(vec2 uv) {
  uv.x += sin((uv.y + iTime) * 4.) * .05;
  uv.y += sin(iTime) * .2+.15;

  float dist = 100.;

  float body = sdUnevenCapsule(uv + vec2(0., .2), .5, .4, .5);
  dist = max(-dist, body);

  float wigg = wiggles(uv, -.3, 8., .1, .5);
  dist = max(-wigg, dist);

  float arms = arms(uv, -.1, .7, 8., .1 * sin(iTime));
  dist = min(arms, dist);

  float eyel = length(uv - vec2(-.18,.4 + sin(iTime) * .02)) - .1;
  dist = max(-eyel, dist);

  float eyer = length(uv - vec2(.2,.3 + sin(iTime * 1.2) * .03)) - .1;
  dist = max(-eyer, dist);

  float mouthwiggle = .76 + sin(iTime*3.) * .01;
    float mouthwid = .1 + sin(iTime*2.) * .01;
  float mouth = sdArc(uv + vec2(.04,-0.35), vec2(sin(TAU*mouthwiggle), cos(TAU*mouthwiggle)), vec2(sin(TAU* mouthwid), cos(TAU* mouthwid)), .15, .02);
    dist = max(-mouth, dist);

  return dist;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  // Normalized pixel coordinates (from 0 to 1)
  vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;

  float ghost = map(uv);

  //vec3 col = vec3(step(0.,ghost));
  vec3 col = pow(abs(vec3(ghost)), vec3(.2));

  // Output to screen
  fragColor = vec4(col, 1.0);
}

#define FDIST 0.5
#define MAXSTEP 100
#define TOL 0.005
#define MAXDIST 100.

#define BEVEL 0.2
#define EXTRUDE 0.05

#define RAD 0.3
#define WHEELDEPTH 0.2

#define LENGTH 1.
#define WIDTH 0.75

#define AO_SAMPLES 5
#define AO_STRENGTH 2.

#define LAP_LENGTH 200.
#define RACESPEED 20.

vec3 lightdir = normalize(vec3(1.));

float wheelsdf(in vec3 p) {
    vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(RAD, WHEELDEPTH);
  	float dist = min(max(d.x,d.y),0.0) + length(max(d,0.0));
    return min(max(length(p.xz)-0.1, p.y), max(dist, -(length(p+vec3(0., -0.55, 0.))-0.4)));
}

// minimum d.x keeping track of the closest material in d.y
vec2 condmin(in vec2 d1, in vec2 d2) {
    return vec2(min(d1.x, d2.x), mix(d1.y, d2.y, step(d2.x, d1.x)));
}

vec2 trayracer(in vec3 ro) {
    //tray
    vec3 dims = vec3(LENGTH, WIDTH, 0.0);
    vec3 d = abs(ro)-dims;
    float dist = length(max(d,0.0)) + 
	       min(max(d.x, max(d.y, d.z)),0.0);
    dist = abs(dist-BEVEL-0.5*EXTRUDE) - 0.5*EXTRUDE;
    dist = max(ro.z, dist);

    //wheels
    float offset = (RAD+BEVEL);
    vec3 wheelro = vec3(abs(ro.xy), ro.z) - vec3(LENGTH, WIDTH, -offset);
    float wheel_dist = wheelsdf(wheelro);
    return condmin(vec2(dist, 1.), vec2(wheel_dist, 2.));
}

vec2 map(in vec3 ro, out vec2 uv) {

    vec4 o1 = vec4(1., 0.5, 0.75, 0.4) * sin(iTime * vec4(0.2, 0.45, 0.8, 0.15) + vec4(-1., 4.3, 2.15, 9.81));
    float offset1 = o1.x + o1.y + o1.z + o1.w;

    vec4 o2 = vec4(0.45, 0.7, 0.1, 0.1) * sin(iTime * vec4(0.6, 0.7, 4.1, 4.) + vec4(-8., -2., 1.1, 0.7));
    float offset2 = o2.x + o2.y + o2.z + o2.w;

    vec3 ro1 = ro + vec3(2.*offset1 + offset2, 1.4, 0.);
    vec3 ro2 = ro + vec3(0., -1.4, 0.);

    vec2 d1 = trayracer(ro1);
    vec2 d2 = trayracer(ro2);
    uv = d1.x < d2.x ? ro1.xy : ro2.xy;
    vec2 d = condmin(
        d1,
        d2
    );
    //ground
    float ground_dist = ro.z+BEVEL+2.*RAD;
    d = condmin(d, vec2(ground_dist, 3.));

    //walls
    float wall_dist = max(ro.y + 5., ro.z - 3.);
    return condmin(d, vec2(wall_dist, 4.));
}

vec3 getnormal(vec3 ro) {
    vec2 uv;
    vec2 d = vec2(TOL, 0.0);
    float x1 = map(ro+d.xyy, uv).x;
    float x2 = map(ro-d.xyy, uv).x;
    float y1 = map(ro+d.yxy, uv).x;
    float y2 = map(ro-d.yxy, uv).x;
    float z1 = map(ro+d.yyx, uv).x;
    float z2 = map(ro-d.yyx, uv).x;
    return normalize(vec3(
        x1-x2,
        y1-y2,
        z1-z2));
}

vec3 raymarch(in vec3 eye, in vec3 rd, out vec2 uv) {
    float t = 0.0;
    int i;
    for (i=0; i<MAXSTEP; i++) {
        vec3 pos = eye + rd*t;
        vec2 dist = map(pos, uv);
        t += dist.x;
        if (abs(dist.x) < TOL) {
            return vec3(t, dist.y, i);
        } else if (t > MAXDIST) break;
    }
    return vec3(t, 0., i);
}

bool shadowmarch(in vec3 eye, in vec3 rd) {
    float t = 0.;
    vec2 uv;
    for (int i=0; i<20; i++) {
        vec3 pos = eye + rd*t;
        vec2 dist = map(pos, uv);
        t += max(0.03, dist.x);
        if (dist.x < 0.) return true;
    }
    return false;
}

float occlusion(vec3 ro, vec3 rd){
    vec2 uv;
    float k = 1.0;
    float d = 0.0;
    float occ = 0.0;
    for(int i = 0; i < AO_SAMPLES; i++){
        d = map(ro + 0.1 * k * rd, uv).x;
        occ += 1.0 / pow(2.0, k) * (k * 0.1 - d);
        k += 1.0;
    }
    return 1.0 - clamp(AO_STRENGTH * occ, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.xx;

    vec3 up = vec3(0, 0, 1);
    vec3 eye = vec3(2. * sin(iTime*0.4), 5., 1.);
	vec3 w = -normalize(eye);
    vec3 u = normalize(cross(w, up));
    vec3 v = cross(u, w);
    vec3 rd = normalize(w*FDIST + u*uv.x + v*uv.y);
    vec2 texcoords;
	vec3 t = raymarch(eye, rd, texcoords);

    vec3 col;
    vec3 ro = eye + rd*t.x;
	float racepos = ro.x + iTime * RACESPEED;

    if (t.y < 0.5) {
        //sky
        col = vec3(0.5, 0.8, 1.);
    } else if (t.y < 1.5) {
        //tray
        vec3 tex = texture(iChannel1, texcoords).xyz;
        col = max(tex, vec3(0.25));
    } else if (t.y < 2.5) {
        //wheels
        col = vec3(0.3, 0.3, 0.3);
    } else if (t.y < 3.5) {
        //ground

        vec3 asphalt = vec3(0.4, 0.4, 0.4);
        vec3 mediancol = vec3(0.9);
        float medianfac = step(abs(ro.y), 0.2) * step(mod(racepos, 2.), 1.);
        float prixfac = abs(step(mod(racepos, 1.), 0.5) - step(mod(ro.y, 1.), 0.5)) * step(abs(mod(racepos + 0.5*LAP_LENGTH, LAP_LENGTH)-0.5*LAP_LENGTH), 2.);
        medianfac = max(medianfac, prixfac);
        vec3 roadcol = mix(asphalt, mediancol, medianfac);
        col = mix(vec3(0.5, 1., 0.0), roadcol, step(abs(ro.y), 4.));
    } else {
        //wall
        vec3 tex = texture(iChannel0, vec2(racepos, ro.z) * 0.2).xyz;
        col = tex;
    }
    vec3 n = getnormal(ro);
    bool shadowed = shadowmarch(ro + n*TOL, lightdir);
    float fac = max(0.3, shadowed ? 0. : max(0., dot(n, lightdir)));

    float aofac = occlusion(ro, n);

    //float aofac = m

    col = fac * col * vec3(2.05, 2.05, 2.) * aofac;



    fragColor = vec4(col, 1.);
}

