float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float lineFromDistance(float dist, float size) {
    const float thickness = 2.0;
	return clamp((abs(dist - 0.5) * size) - (thickness - 1.0), 0.0, 1.0);
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,vec2(0))) + min(max(d.x,d.y),0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float size = 25.0;
    const float period = 2.0;

    //vec2 coord = fragCoord + vec2(0.0, iTime * -10.0);
    vec2 coord = fragCoord;

    vec2 pixCoord = floor(coord / size) * size;

    float time = iTime * (rand(pixCoord) + 0.5) * 0.5;

    float blend = abs((mod(time, period) / period) - 0.5) * 2.0;

    float rand1 = rand(pixCoord + floor(time / period)) > 0.5 ? 1.0 : 0.0;
    float rand2 = rand(pixCoord + floor(((time + 99.9) + period * 0.5) / period)) > 0.5 ? 1.0 : 0.0;

    float randVal = mix(rand1, rand2, smoothstep(0.25, 0.75, blend));

    vec2 modcoord = mod(coord, size) / size;

    if (randVal > 0.5) {
    	modcoord.x = 1.0 - modcoord.x;
    }

    float boxMix = 1.0 - (abs(randVal - 0.5) * 2.0);

    float box1 = lineFromDistance(sdBox(modcoord, vec2(boxMix * 0.5)) + (boxMix * 0.5), size);
    float box2 = lineFromDistance(sdBox(1.0 - modcoord, vec2(boxMix * 0.5)) + (boxMix * 0.5), size);

    vec3 outcolor = vec3(box1 * box2);

    // Output to screen
    fragColor = vec4(outcolor, 1.0);
}

#define FDIST 0.7
#define PI 3.1415926


#define GROUNDSPACING 0.5
#define GROUNDGRID 0.05
#define BOXDIMS vec3(0.75, 0.75, 1.)

#define ABSORPTION_RATE vec3(0.7, 0.5, 0.5)
#define IOR 1.33
#define SCATTER_FACTOR 0.02
#define SAMPLES 25
#define REFLECTIONS 4

#define TIME_T 2.
#define TIME_H 2.
#define TIME_L 10.


vec2 rand2d(in vec2 uv) {
    return fract(mat2(-199.258, 457.1819, -1111.1895, 2244.185)*sin(mat2(111.415, -184, -2051, 505)*uv));
}

float box(in vec3 ro, in vec3 rd, in vec3 r, out vec3 nn, bool entering) {
    vec3 dr = 1.0/rd;
    vec3 n = ro * dr;
    vec3 k = r * abs(dr);

    vec3 pin = - k - n;
    vec3 pout =  k - n;
    float tin = max(pin.x, max(pin.y, pin.z));
    float tout = min(pout.x, min(pout.y, pout.z));
    if (tin > tout) return -1.;
    if (entering) {
    	nn = -sign(rd) * step(pin.zxy, pin.xyz) * step(pin.yzx, pin.xyz);
    } else {
        nn = sign(rd) * step(pout.xyz, pout.zxy) * step(pout.xyz, pout.yzx);
    }
    return entering ? tin : tout;
}

vec2 sphere(in vec3 ro, in vec3 rd, in float r, out vec3 ni) {
	float pd = dot(ro, rd);
    float disc = pd*pd + r*r - dot(ro, ro);
    if (disc < 0.) return vec2(-1.);
    float tdiff = sqrt(disc);
    float tin = -pd - tdiff;
    float tout = -pd + tdiff;
    ni = normalize(ro + tin * rd);

    return vec2(tin, tout);
}

vec3 bgcol(in vec3 rd) {
    return mix(vec3(0., 0., 1.), vec3(0.6, 0.8, 1.), 1.-pow(abs(rd.z), 2.));
}

//raytrace the exterior surroundings
vec3 background(in vec3 ro, in vec3 rd) {
    float t = (-1. - ro.z)/rd.z;
    if (t < 0.) return bgcol(rd);
    vec2 uv = ro.xy+t*rd.xy;
    if (max(abs(uv.x), abs(uv.y)) > 8.) return bgcol(rd);
    vec2 checkers = smoothstep(vec2(GROUNDGRID*0.75), vec2(GROUNDGRID), abs(mod(uv, vec2(GROUNDSPACING))*2.-GROUNDSPACING));
    float aofac = smoothstep(-0.5, 1., length(abs(uv)-min(abs(uv), vec2(0.75))));
    return mix(vec3(0.2), vec3(1.), min(checkers.x,checkers.y)) * aofac;
}

//raytrace the insides
vec3 insides(in vec3 ro, in vec3 rd, in float INNERRAD, in mat2 rot, out float tout) {
    vec3 ni;
    vec2 t = sphere(ro, rd, INNERRAD, ni);
    vec3 ro2 = ro + t.x * rd;
    // shading/texture
    vec2 checkers = step(mod(rot * ro2.xy, vec2(0.25)), vec2(0.01));
    vec3 tex = mix(vec3(1.), vec3(0., 0.7, 0.), abs(checkers.x-checkers.y));
    float fac = -ni.z;

    //inner background
    vec3 n;
    float tb = box(ro, rd, vec3(INNERRAD), n, false);
    vec3 rob = ro + tb * rd;
    vec3 checkersb = abs(mod(rob.xyz, vec3(0.5))-0.25)*4.;
    vec3 texb = mix(vec3(0., 0., 1.), vec3(0.), step(0.25, abs(abs(checkersb.x-checkersb.y)-checkersb.z)));
    tout = mix(tb, t.x, step(0., t.x));
    return mix(mix(vec3(0.5), texb, step(0., tb)) * 0.5, tex * fac, step(0., t.x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t_osc = 0.5*(TIME_H+TIME_L)+TIME_T;
    float h_l = 0.5*TIME_L/t_osc;
    float h_h = (0.5*TIME_L+TIME_T)/t_osc;
    float osc = smoothstep(0., 1., (clamp(abs(mod(iTime, t_osc*2.)/t_osc-1.), h_l, h_h) - h_l) / (h_h - h_l));
    float INNERRAD = mix(0.5, 1.5, osc);
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.x;
    float mouseY = iMouse.y < 1. ? 0.5 : (1.0-1.15*iMouse.y/iResolution.y) * 0.5 * PI;
    float mouseX = iMouse.x < 1. ? iTime*0.25 : -(iMouse.x/iResolution.x) * 2. * PI;
    vec3 eye = 4.*vec3(cos(mouseX) * cos(mouseY), sin(mouseX) * cos(mouseY), sin(mouseY));
    vec3 w = normalize(-eye);
    vec3 up = vec3(0., 0., 1.);
    vec3 u = normalize(cross(w, up));
    vec3 v = cross(u, w);

    vec3 rd = normalize(w * FDIST + uv.x * u + uv.y * v);

    vec3 ni;
    float t = box(eye, rd, BOXDIMS, ni, true);
    vec3 ro = eye + t * rd;
    vec2 coords = ro.xy * ni.z + ro.yz * ni.x + ro.zx * ni.y;

    if (t > 0.) {
        float ang = -iTime * 0.33;
    	float c = cos(ang);
    	float s = sin(ang);
    	mat2 rot = mat2(c, -s, s, c);
        vec3 col = vec3(0.);
        float R0 = (IOR-1.)/(IOR+1.);
        R0*=R0;
        for (int i=0; i<SAMPLES; i++) {

            vec2 theta = rand2d(coords + float(i) * vec2(1., 0.) * vec2(104., -30.6));
            theta *= vec2(2.*PI, SCATTER_FACTOR*PI);
            vec3 n = vec3(cos(theta.x)*sin(theta.y), sin(theta.x)*sin(theta.y), cos(theta.y));
            // reflection
            vec3 nr = n.zxy * ni.x + n.yzx * ni.y + n.xyz * ni.z;
            vec3 rdr = reflect(rd, nr);
            vec3 reflcol = background(ro, rdr);

            // refraction & insides

            //vec3 rd2 = rd.yzx * ni.x + rd.zxy * ni.y + rd.xyz * ni.z;

            vec3 rd2 = refract(rd, nr, 1./IOR);

            vec3 insidecol = vec3(0.);
            float accum = 1.;
            vec3 transmission = vec3(1.);
            vec3 no2 = ni;
            vec3 ro_refr = ro;

            for (int j=0; j<REFLECTIONS; j++) {
                float tb;
                //no2 = -no2;
                vec2 coords2 = ro_refr.xy * no2.z + ro_refr.yz * no2.x + ro_refr.zx * no2.y;
                vec3 eye2 = vec3(coords2, -max(INNERRAD, 1.));
                vec3 rd2trans = rd2.yzx * no2.x + rd2.zxy * no2.y + rd2.xyz * no2.z;
                rd2trans.z = -rd2trans.z;
                vec3 internalcol = insides(eye2, rd2trans, INNERRAD, rot, tb);
                if (tb > 0.) {
                    //terminate at interior geometry
                    insidecol += accum * internalcol * transmission * pow(ABSORPTION_RATE, vec3(tb));
					break;
                } else {
                    //compute contribution of the light leaked from the environment through this bounce
                    float tout = box(ro_refr, rd2, BOXDIMS, no2, false);
                    no2 = n.zyx * no2.x + n.xzy * no2.y + n.yxz * no2.z;
                    vec3 rout = ro_refr + tout * rd2;
                    vec3 rdout = refract(rd2, -no2, IOR);
                    float fresnel2 = R0 + (1.-R0) * pow(1.-dot(rdout, no2), 5.);
                    rd2 = reflect(rd2, -no2);

                    ro_refr = rout;
                    ro_refr.z = max(ro_refr.z, -0.999);

                    transmission *= pow(ABSORPTION_RATE, vec3(tout));
                    insidecol += accum * (1.-fresnel2) * background(ro_refr, rdout) * transmission;
                    if (fresnel2 < 0.1) break;
                    accum *= fresnel2;
                }
            }

            // background

            float fresnel = R0 + (1.-R0) * pow(1.-dot(-rd, nr), 5.);
            col += mix(insidecol, reflcol, fresnel);
        }
        col /= float(SAMPLES);

        fragColor = vec4(col, 1.);
    } else {
		fragColor = vec4(background(eye, rd), 1.);
    }
}

const float perStep=0.4;
const float PI = 3.14;
const float epsilon = 0.001;  

struct Ray
{
    vec3 from;
    vec3 dir;
};

bool hitPlane(in Ray ray,in vec3 planeN,in vec3 planeC,inout vec3 hitPos)
{
    // ray hit plane 
    vec3 from =ray.from;
    vec3 dir = ray.dir;
    //(F-C)„ÄÇN + t (D„ÄÇN) = 0
    // t  = (C-F)„ÄÇN / (D„ÄÇN)
    // t  = (A / (B)
    float B = dot(dir, planeN);
    float A = dot(planeC- from, planeN);

    // avoid divide by 0
    if (abs(B) < epsilon)
        return false;

    float t = A / B;
    hitPos = from + t * dir;

    if(t>0.0)
    	return true;
}

Ray[4] createRayDiff(vec3 eye, vec3 xAxis,vec3 yAxis,vec3 p){
    vec2[4] multisampleDiff;
    float x = 0.5;
    float y =0.5;
    // offset from p
    multisampleDiff[0]=vec2(x,y)/iResolution.y;   
    multisampleDiff[1]=vec2(-y,x)/iResolution.y; 
    multisampleDiff[2]=vec2(-x,-y)/iResolution.y;
    multisampleDiff[3]=vec2(y,-x)/iResolution.y;

    Ray[4] rays;
    for(int i=0;i<4;++i){
        rays[i].from =p+( multisampleDiff[i].x*xAxis + multisampleDiff[i].y*yAxis );
        rays[i].dir =normalize(rays[i].from-eye);
    }

    return rays;
}

// from https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdVerticalCapsule( vec3 p, float h, float r,float offset )
{
    p.y -= clamp( p.y, offset, h+offset );
    return length( p ) - r;
}

float bottle(vec3 pos){
	float H =2.,R =1.,Offset=0.;
	float h=1.,r=0.25,offset =3.;

    //return sdVerticalCapsule(pos,h,r,offset);
    return min(sdVerticalCapsule(pos,H,R,Offset),sdVerticalCapsule(pos,h,r,offset));
}

// gradient is normal
vec3 getNormal(vec3 pos){
    float delta =0.01;
    vec3 temp=vec3(bottle(pos+vec3(delta,0.,0.))-bottle(pos),
                   bottle(pos+vec3(0.,delta,0.))-bottle(pos),
                   bottle(pos+vec3(0.,0.,delta))-bottle(pos)
                  );
	return normalize(temp);
}

vec3 shading(Ray ray,vec3 eye){
    vec3 shadingColor = vec3(0.0,0.0,0.0);

    // obj 3 axis and offset
    vec3 obj_offset=vec3(0.,1.,0.);
    vec3 zAxis =vec3(0.,0.,1.);
    float para_obj_rot=iTime;
    vec3 yAxis = vec3(-sin(para_obj_rot),cos(para_obj_rot),0.);
    vec3 xAxis =vec3(cos(para_obj_rot),sin(para_obj_rot),0.);

    // to local space
    vec3 temp=(ray.from-obj_offset);
    Ray localRay;
    localRay.from =vec3(dot(xAxis,temp),dot(yAxis,temp),dot(zAxis,temp));
    localRay.dir=vec3(dot(xAxis,ray.dir),dot(yAxis,ray.dir),dot(zAxis,ray.dir));

    vec3 lightDir=normalize(vec3(1.,1.,1.));
    vec3 localLightDir =vec3(dot(xAxis,lightDir),dot(yAxis,lightDir),dot(zAxis,lightDir));

    temp= eye-obj_offset;
    vec3 localEye =vec3(dot(xAxis,temp),dot(yAxis,temp),dot(zAxis,temp));

    vec3 back_color=vec3(0.2,0.2,0.2);
    //vec3 water_color =vec3(0.4,0.1,0.6);
    vec3 water_color =vec3(1.0,0.9,0.15);

    //front side of bottle
    bool hit_bottle;
    vec3 bottle_color;
    vec3 bottle_hit_pos;
    float dist_bottle_to_eye;
    for(int i=0;i<20;i++){
    	float dist = bottle(localRay.from);
        if(dist<0.01){

            //shading bottle color
            vec3 n=getNormal(localRay.from);
        	bottle_color= vec3(0.0,1.0,0.75)*max(0.,dot(n,localLightDir))
                +vec3(1.0,1.0,0.0)*max(0.,dot(n,-localLightDir));

            hit_bottle=true;
            bottle_hit_pos= localRay.from;
            dist_bottle_to_eye=length(localRay.from-localEye);
            break;
        }

        localRay.from+=dist*localRay.dir;
    }


    //back side of bottle
    localRay.from+=100.*localRay.dir;
    localRay.dir=-localRay.dir;

    float dist_back_bottle_to_eye;
    for(int i=0;i<20;i++){
        float dist = bottle(localRay.from);
        if(dist<0.01){

            dist_back_bottle_to_eye=length(localRay.from-localEye);
            break;
        }

        localRay.from+=dist*localRay.dir;
    }

    //plane of water
    float param_plane_rot=0.5*PI+0.1*PI*(sin(iTime*5.0));
    vec3 planeN =vec3(0.,sin(param_plane_rot),cos(param_plane_rot));
    vec3 help = vec3(0.0,1.0,0.0);
    vec3 planeAxisX = vec3(1.0,0.0,0.0);
    float lower_factor=1.5;

    //pull cos funciton  downÔºåfor reasonable water level
    vec3 planeC =obj_offset+vec3(0.,(cos(iTime)-1.0)*lower_factor+1.0,0.);

	vec3 planeColor;
    vec3 hitPos;
    bool hit_plane=hitPlane(ray,planeN,planeC,hitPos);
    if(hit_plane )
    {
        float d=length(hitPos-eye);
        //between back side of bottle and front side of bottle
        if(d<dist_back_bottle_to_eye && d>dist_bottle_to_eye )
        {
            float thickness_inverse =min(max(1.0-(dist_back_bottle_to_eye-d)*0.3,0.),1.);
            return mix(vec3(0.1)+water_color*thickness_inverse,bottle_color,0.25);  
        }
    }

    vec3 bottle_hit_pos_world = obj_offset
        + bottle_hit_pos.x* xAxis+bottle_hit_pos.y* yAxis+bottle_hit_pos.z* zAxis;

    bool normalBackFace =dot(bottle_hit_pos_world-planeC,planeN)<0.0;

    if(hit_bottle)
    {

        if(normalBackFace){
    		float thickness_inverse =min(max(1.0-(dist_back_bottle_to_eye-dist_bottle_to_eye)*0.3,0.),1.);
        	return mix(back_color,mix(water_color*thickness_inverse,bottle_color,0.25),0.5);
        }else
            return mix(back_color,bottle_color,0.5);       
    }

    shadingColor=back_color;
    return shadingColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // weight (from -1 to 1)
    vec2 weight = fragCoord/iResolution.xy;
    weight= weight*2.0-1.0;

    vec3 lookAt =vec3(0.0,1.0,0.0);
    vec3 eye = vec3(0.0,1.0,5.0);
    //vec3 eye = lookAt+ 5.0*vec3(cos(iTime),0.0,sin(iTime))+vec3(0.0,1.0,0.0);

    // camera 3 axis
    vec3 zAsix = normalize(eye-lookAt);
    vec3 yAxis = vec3(0.0,1.0,0.0);
    vec3 xAxis = cross(yAxis,zAsix);

    //near plane of view frustum (z = -1)
    float fovDegree =90.0;
    float halfFov = radians(0.5*fovDegree);
    float tanH = tan(halfFov);
    float tanW = tanH*iResolution.x/iResolution.y;

    // generate point from the plane
    vec3 pointOnPlane = eye -zAsix + xAxis*weight.x*tanW + yAxis*weight.y*tanH;

    // disturb ray dir
    float A =0.1*sin(0.5*iTime);
    //pointOnPlane+=vec3(A*sin(10.*weight.y+5.*iTime),0.,0.);

    // Antialiasing
    Ray[4] rays =createRayDiff(eye,xAxis,yAxis,pointOnPlane);

    vec3 color= vec3(0.0,0.0,0.0);
    for(int i=0;i<1;++i)
        color += shading(rays[i],eye);
    // color *=0.25;

    fragColor = vec4(color,1.0);
}

