const int NONE = 0;
const int SPHERE = 1;
const int PLANE = 2;
const int TORUS = 3;
const int BOX = 4;
const int LIGHT = 5;

const vec3 sphere_pos = vec3(2.0, -2.0, -5.0);
const vec3 box_pos = vec3(-2.0, -2.0, -5.0);
const vec3 torus_pos = vec3(0.0, 2.0, -5.0);
const vec3 plane_pos = vec3(0.0, -2.0, 0.0);

struct Obj {
    int type;
    float d;
};


// sdf primitives https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float sdBox( vec3 p, vec3 b )
{
  float t = iTime;
  //p.xz *= mat2(cos(t), -sin(t), sin(t), cos(t));
  //p.yz *= mat2(cos(t), -sin(t), sin(t), cos(t));
  //p.xy *= mat2(cos(t), -sin(t), sin(t), cos(t));
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdTorus( vec3 p, vec2 t )
{
  float t0 = iTime;
  p.xz *= mat2(cos(t0), -sin(t0), sin(t0), cos(t0));
  p.yz *= mat2(cos(t0), -sin(t0), sin(t0), cos(t0));
  p.xy *= mat2(cos(t0), -sin(t0), sin(t0), cos(t0));
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float sdPlane( vec3 p, vec4 n )
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

vec3 traceAmbient()
{
    // ambient
    float ambientStrength = 0.1;
    vec3 ambient = ambientStrength * vec3(1.0);
    return ambient;
}

vec3 traceDiffuse(vec3 lightDir, vec3 n)
{
    // diffuse
    float diff = max(dot(n, lightDir), 0.0);
    vec3 diffuse = diff * vec3(1.0);

    vec3 c = vec3(1.0);

    return diffuse * c;
}

vec3 traceSpecular(vec3 lightDir, vec3 hit, vec3 l0, vec3 n)
{
    // specular
    vec3 viewDir = normalize(hit - l0);
    vec3 reflectDir = reflect(lightDir, n); 
    float spec = pow(max(dot(viewDir, reflectDir), 0.0), 64.0);
    return vec3(spec);
}

vec3 getLightPos()
{
    float t0 = iTime;
    vec3 lightPos = vec3(0.0, 2.0, -15.0);
    lightPos.z += cos(t0) * 8.0;
    lightPos.x += sin(t0) * 8.0;
    return lightPos;
}


vec3 getLightPosv2()
{
    float t0 = iTime;
    vec3 lightPos = vec3(0.0, 1.0, -2.0);
    lightPos.x += sin(t0) * 4.0;

    return lightPos;
}


vec3 traceLight(vec3 n, vec3 hit, vec3 l0)
{
    vec3 lightDir = normalize(getLightPos() - hit);

    vec3 ambient = traceAmbient();

    vec3 diffuse = traceDiffuse(lightDir, n);

    vec3 spec = traceSpecular(lightDir, hit, l0, n);

    return ambient + diffuse + spec;



}






vec3 traceLightLuz(vec3 n, vec3 hit, vec3 l0, vec3 luz)
{
    vec3 lightDir = normalize(luz - hit);

    vec3 ambient = traceAmbient();

    vec3 diffuse = traceDiffuse(lightDir, n);

    vec3 spec = traceSpecular(lightDir, hit, l0, n);



    return ambient + diffuse + spec;
}




#define EPSILON 1e-3
const vec3 e = vec3(0.01, 0, 0);

vec3 nBox(vec3 p, vec3 b) {
    float d = sdBox(p, b);
	vec3 n = d - vec3(
    	sdBox(p - e.xyy, b),
        sdBox(p - e.yxy, b),
        sdBox(p - e.yyx, b)
    );
    return normalize(n);
}

vec3 nSphere(vec3 p, float s) {
    float d = sdSphere(p, s);
	vec3 n = d - vec3(
    	sdSphere(p - e.xyy, s),
        sdSphere(p - e.yxy, s),
        sdSphere(p - e.yyx, s)
    );
    return normalize(n);
}

vec3 nTorus(vec3 p, vec2 t) {
    float d = sdTorus(p, t);
	vec3 n = d - vec3(
    	sdTorus(p - e.xyy, t),
        sdTorus(p - e.yxy, t),
        sdTorus(p - e.yyx, t)
    );
    return normalize(n);
}

vec3 mapLight(Obj obj, vec3 hit, vec3 l0)
{
    vec3 col;
    vec3 n = vec3(0.0);

    if (obj.type == PLANE) {
        n = vec3(0.0, 1.0, 0.0);
        col=vec3(1,0,0);
    } else if (obj.type == SPHERE) {
    	n = nSphere(hit - sphere_pos, 1.0);
        col=vec3(1,1,0);

    } else if (obj.type == BOX) {
    	n = nBox(hit - box_pos, vec3(1.0));
        col=vec3(1,0,1);


    } else if (obj.type == TORUS) {
    	n = nTorus(hit - torus_pos, vec2(1.5, 0.5));
        col=vec3(0,1,1);

    } else if (obj.type == LIGHT) {
    	return vec3(1.0);
    }

    return (traceLight(n, hit, l0)*col+traceLightLuz(n, hit, l0,getLightPosv2())*col)/1.5;
    //return traceLightLuz(n, hit, l0,vec3(0.0, 1.0, 1.0))*col;
}

Obj map(vec3 p)
{


    float d = 1e6;
    float t = 0.0;
    int type = NONE;

    t = sdSphere(sphere_pos - p, 1.0);
    if (t < d) { type = SPHERE; d = t; }

    t = sdSphere(getLightPos() - p, 0.25);
    if (t < d) { type = LIGHT; d = t; }

    //t = sdSphere(getLightPosv2() - p, 0.5);
    //if (t < d) { type = LIGHT; d = t; }


    t = sdBox(box_pos - p, vec3(1.0));
    if (t < d) { type = BOX; d = t; }

    t = sdTorus(torus_pos - p, vec2(1.5, 0.5));
    if (t < d) { type = TORUS; d = t; }

    t = sdPlane(plane_pos - p, normalize(vec4(0.0, -1.0, 0.0, 5.0)));
	if (t < d) { type = PLANE; d = t; }

    return Obj(type, d);
}

Obj trace(vec3 o, vec3 r)
{
    Obj obj = Obj(NONE, 1e6);

    float t = 0.0;
    for (int i = 0; i < 32; i++) {
        vec3 p = o + r * t;
        obj = map(p);
        t += obj.d;
        if (obj.d < 1e-3) {
        	break;
        }
    }

    obj.d = t;

    return obj;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // [-1, 1]
    uv = uv * 2.0 - 1.0;

    // aspect corrected
    uv.x *= iResolution.x / iResolution.y;

    vec3 o = vec3(0.0, 0.0, 0.0);
    vec3 r = vec3(uv, -1.0);

    Obj obj = trace(o, r);

    vec3 hit = o + obj.d * r;

    vec3 light = mapLight(obj, hit, o);

    float fog = 1.0 / (1.0 + obj.d * obj.d * 0.01);

	vec3 c = vec3(1.0);

    c = vec3(c * fog * light);

    fragColor = vec4(c, 1.0);
}

