const float PI = 3.14159265;

mat2 r2d(float a){float sa = sin(a);float ca=cos(a);return mat2(ca,sa,-sa,ca);}

float lenny(vec2 uv)
{
  return abs(uv.x)+abs(uv.y);
}

float sat(float a)
{
  return clamp(a,0.,1.);
}

vec3 sat(vec3 v)
{
  return vec3(sat(v.x),sat(v.y), sat(v.z));
}

float _sub(float a, float b)
{
  return max(-a,b);
}

float _union(float a, float b)
{
  return min(a,b);
}

float sdf_sqr(vec2 uv, float sz)
{
  return lenny(uv*r2d(PI/4.))-sz;
}

float sdf_sqrborder(vec2 uv, float sz, float th)
{
  return _sub(sdf_sqr(uv,sz-th),sdf_sqr(uv,sz));
}

vec3 rdr(vec2 uv)
{
  vec3 col;
  vec3 acc;
  const int cnt = 8;
  float sp=0.1;
  for (int i=0;i<cnt;++i)
  {
    float fi =float(i);
    float fc = float(cnt);
    vec2 pos = vec2(fi-fc*.5)*sp;
    acc +=vec3(float(i)/8.+0.1,pos.x*4.-sin(iTime*2.)*.5,pos.y*5.)*0.5*sat(1.-(vec3(sin(iTime+fi)*0.5+0.9)*sdf_sqrborder(uv-pos,0.3,0.01)*10.));
  }
  return col + acc;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = fragCoord.xy / iResolution.xx;
  uv -= vec2(.5)*(iResolution.xy/iResolution.xx);
    vec2 ouv = uv;
  uv *= 2.;
  vec3 col;
  uv.y+=uv.x;
  uv.x*=1.5;
  col = rdr(uv);
    col += (1.-lenny(ouv))*vec3(.35,.27,.59).zyx;
  fragColor = vec4(col, 1.0);
}

