#define FK(k) floatBitsToInt(cos(k))^floatBitsToInt(k)
float hash(float k) {
  int x = FK(k);int y = FK(cos(k));
  return float((x*x-y)*(y*y+x)-x)/2.14e9;
}

vec3 hash3(float k) {
  float t1 = hash(k);
  float t2 = hash(t1);
  float t3 = hash(t2);
  return vec3(t1, t2, t3);
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax,p)*ax, p, cos(ro)) + sin(ro)*cross(ax,p);
}

float bumps(vec3 p, float scale) {
  p = (fract(p/scale)-0.5)*scale;
  return length(p)-scale*0.5;
}

float comp(vec3 p, vec3 off, vec3 rot) {
  float phi = acos(rot.x);
  float theta = rot.y * acos(-1.);
  float lambda = rot.z * acos(-1.);
  vec3 ax = vec3(cos(phi), sin(theta)*sin(phi), cos(theta)*sin(phi));

  p = erot(p, ax, lambda);
  p += off;

  vec3 ind = floor(p);
  p = fract(p)-0.5;
  p *= mod(ind,2.)*2.-1.;

  return (p.x+p.y+p.z)/sqrt(3.);
}

float sminCubic( float a, float b, float k )
{
    float h = max( k-abs(a-b), 0.0 )/k;
    return min( a, b ) - h*h*h*k*(1.0/6.0);
}

float bpm = 60.;

float scene(vec3 p) {
  float dist = 100000.;

  for (int i = 0; i < 10; i++) {
    float rnd = cos(float(i));
    vec3 off = hash3(rnd);
    vec3 rot = hash3(cos(rnd));
    float str = hash(cos(cos(rnd)));
    float d = comp(p, vec3(off)*str*iTime*0.3, vec3(rot))+0.6;
    dist = sminCubic(dist, d, 0.5);
  }

  float off = sin(p.x*0.5);
  float bt = pow(abs(fract(iTime/60.*bpm + off)-0.5)*2., 2.);
  return mix(dist, bumps(p, 0.04), 0.5*bt);
}

#define AP(f,k) vec3(f(k[0]),f(k[1]),f(k[2]))
vec3 norm(vec3 p) {
  mat3 k = mat3(p,p,p) - mat3(0.001);
  return normalize(scene(p) - AP(scene,k));
}

vec3 gam(int x,int y, int z) {
  return pow(vec3(float(x),float(y),float(z))/float(0xff),vec3(2));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  vec3 cam = normalize(vec3(0.5, uv));
  vec3 init = vec3(0, iTime/7., 0);
  vec3 p = init;
  bool hit = false;
  float sgn = 1.;
  for (int i = 0; i < 50; i++) {
    float dist = scene(p);
    if (i == 0) sgn = sign(dist);
    dist *= sgn;
    if (abs(dist)<0.001) { hit = true; break; }
    if (distance(p, init) > 100.) break;
    p += cam*dist;
  }

  vec3 n = norm(p);
  vec3 rf = reflect(cam,n);
  float col1 = dot(rf, normalize(vec3(-1,-0.6,0)))*0.5+0.5;
  float col2 = dot(rf, normalize(vec3(-1,0.5,0)))*0.5+0.5;
  float fog = pow(exp(-distance(p, init)*0.5)/exp(0.), 2.);
  //Corn Harvest #876310 / Blue Romance #C6F5E0
  fragColor.xyz = sqrt(mix(gam(0x07,0x03,0x10), gam(0x87,0x63,0x10)*col2 + gam(0xC6,0xF5,0xE0)*col1, fog));
}

#define FK(k) floatBitsToInt(cos(k))^floatBitsToInt(k)
float hash(vec2 p) {
  int x = FK(p.x); int y = FK(p.y);
  return float((x*x-y)*(y*y+x)-x)/2.14e9;
}

float hash2(float a, float b) {
  return hash(vec2(a, b));
}

float hash3(float a, float b, float c) {
  return hash(vec2(hash(vec2(a,b)),c));
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(p,ax)*ax, p, cos(ro)) + sin(ro)*cross(p,ax);
}

float rep(vec3 p, vec3 sc, vec3 off, float ro) {
  p = erot(p, vec3(0,1,0), ro);
  p = erot(p, vec3(0,0,1), 3.1415*hash2(ro, ro));
  p = erot(p, vec3(1,0,0), 3.1415*hash3(ro, ro, ro));
  p -= off*sc;
  p = (fract(p/sc)-0.5)*sc;
  return length(p);
}

float scene(vec3 p) {
  float dist = 0.;
  float cum = 0.;
  for (int i = 0; i < 10; i ++) {
    float ro = hash2(float(i+1), 2.3238) * 3.14159;
    vec3 off = vec3(hash2(float(i+1), 384.12), hash2(float(i+1), 6969.0), hash2(float(i+1), 420.348));
    float sc = 1./float(i+1);
    dist += rep(p, vec3(sc), off, ro);
    cum += 1.;
  }
  return (dist - 1.2)/sqrt(cum);
}

vec3 shadeDistance(float d) {
    float dist = d*200.0;
    float banding = max(sin(dist), 0.0);
    float strength = sqrt(clamp(log(abs(d)+1.0)*1.5, 0.0, 1.0));
    float pattern = mix(strength, banding, (0.6-abs(strength-0.5))*0.3);

    vec3 color = vec3(pattern);

    if (d > 0.0) {
        //red tint
        color *= vec3(1.0,0.56,0.4);
    } else {
        //blue tint
        color *= vec3(0.4,0.9,1.0);

    }
    return color;
}

vec3 planeinterp(vec3 a, vec3 b, vec3 c, vec3 d, vec2 k) {
  a = a*a;
  b = b*b;
  c = c*c;
  d = d*d;
  return mix(mix(a,b,k.x),mix(c,d,k.x),k.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

