  float ro = iTime/12.;
  float str = 2.;
  vec3 init = vec3(-4.+iTime/5., cos(ro)*str, sin(ro)*str);
  vec3 p = init;
  float k = 1.;
  float iter = 20. + cos(iTime/2.)*10.;
  for( int i = 0; i < int(iter); i++) {
    float dst = scene(p);
    if (i == 0) k = sign(dst);
    dst*=k;
    if (abs(dst) < 0.001) { break; }

    if (distance(p, init) > 100.) break;
    p += cam*dst;
  }

  float hs1 =  hash(uv*iTime)*0.05;
  float hs2 =  hash(uv*iTime*43.43)*0.05;
  float c = length(sin(p)*0.5+0.5)/sqrt(3.) + hs1;
  float d = 1.-exp(-distance(init,p))/exp(0.) + hs2;
  fragColor.xyz = sqrt(planeinterp(
    vec3(78./255., 58./255., 88./255.), vec3(54./255., 193./255., 251./255.),
    vec3(192./255., 69./255., 124./255.), vec3(241./255., 255./255., 194./255.),
    vec2(c, d)
  ));
  //fragColor.xyz = shadeDistance(scene(vec3(uv, iTime*0.1)));
}

