//Material var
 float reflectionFactor = 0.0;


float MaxComp(in vec3 pos)
{
    return max(pos.x, max(pos.y, pos.z));
}


void Rotate(inout vec2 pos, float angle)
{
    pos = vec2(pos.x * cos(angle) + pos.y * sin(angle), - pos.x * sin(angle) + pos.y * cos(angle));
}

float DSphere(in vec3 pos, in float radius)
{
    return length(pos) - radius;
}


float DBox(in vec3 pos, in vec3 b)
{
    vec3 di = abs(pos) - b;
    float mc = MaxComp(di);
    return min(mc, length(max(di, 0.0)));
}


//soft min that allows object to fuse into the other 
float Smin(float a, float b, float k)
{
//when the object are far, the return value is 'a' or 'b' 
    float res = exp(-k * a) + exp(-k * b);
    return -log(res)/k;
}

//Holds all the geometries that will be render to screen
float DistanceEstimator(in vec3 pos)
{
    float sphere1 = DSphere(pos, 0.62);
    float box = DBox(pos, vec3(0.5));
    //float holeBox = max(box, -sphere1);
    for(int i = 0; i < 12; i++)
    {
        vec3 offset = vec3(2.0 * sin(iTime *0.25), 0.0, 2.0 * cos(iTime));
        Rotate(offset.xz, float(i));
        float sphere2 = DSphere(pos - sin(iTime*0.3) * offset , 0.15);
        sphere1 = Smin(sphere1, sphere2, 10.0);
    }
    float boxBase = DBox(pos - vec3(0.0, -0.50, 0.5 * cos(iTime)), vec3(0.9, 0.1, 1.0)); 
    float scene_obj =  min(boxBase, sphere1);
    if (scene_obj == boxBase) reflectionFactor = 0.7;
    else reflectionFactor = 0.3;

    return scene_obj;
}

vec3 normal(in vec3 pos)
{
    vec3 eps = vec3(0.001, 0.0, 0.0);//epsilon: small number to work with
    vec3 nor;
    //finds the ratio of change(slope) in x,y,z... similar to derivative 
    nor.x = DistanceEstimator(pos + eps.xyy) - DistanceEstimator(pos - eps.xyy);
    nor.y = DistanceEstimator(pos + eps.yxy) - DistanceEstimator(pos - eps.yxy);
    nor.z = DistanceEstimator(pos + eps.yyx) - DistanceEstimator(pos - eps.yyx);
    return normalize(nor);
}

#define LIGHT_POS_1 20.0*vec3(-0.3, 0.15, 1.0)
#define LIGHT_POS_2 20.0*vec3(-0.33, -0.2, -1.0)
#define LIGHT_COL_1 vec3(1.0, 7.0/8.0, 3.0/4.0)
#define LIGHT_COL_2 vec3(1.0, 3.0/4.0, 7.0/8.0)

