//allows to make a visible sun
vec3 getSkyColor(in vec3 rd)
{
    vec3 lightDir1 = normalize(LIGHT_POS_1);
    vec3 lightDir2 = normalize(LIGHT_POS_2);
    float ld1 = max(dot(lightDir1, rd), 0.0);
    float ld2 = max(dot(lightDir2, rd), 0.0);
    vec3 final = vec3(0.125);

    //skybox color
    //if ((rd.y > abs(rd.x)) && (rd.y > abs(rd.z * 0.25))) final = vec3(2.0) * rd.y;
    //float roundBox = length(max(abs(rd.xz/max(0.0, rd.y)) - vec2(0.9, 4.0), 0.0)) - 0.1;
    //final += vec3(0.8) * pow(saturate(1.0 - roundBox * 0.5), 6.0);

    const float SUN_INSTENSITY = 200.0;
    const float SUN_REFLECTION_OBJECT = SUN_INSTENSITY /2.0;

    //sun1
    final += pow(LIGHT_COL_1, vec3(2.0, 1.5, 1.5)) * pow(ld1, 10.0);
    final += LIGHT_COL_1 * pow(ld1, SUN_REFLECTION_OBJECT);
    //sun2
    final += pow(LIGHT_COL_2, vec3(2.0, 1.5, 1.5)) * pow(ld2, 10.0);
    final += LIGHT_COL_2 * pow(ld2, SUN_INSTENSITY);

    return final;
}



#define MAX_TOLERANCE 0.001
#define MAX_RAY_LENGTH 32.0
#define MAX_RAY_MARCHES 144.0
#define VIOLET vec3(0.50, 0.0, 0.8);
#define DARK_BLUE vec3(0.20, 0.0, 1.0);
#define WHITE vec3(1.0)    


//ro: ray origin
//rd: ray direction(normalize vector)

//RAYMARCHING: a form to implement raytracing
float RayMarch(in vec3 ro, in vec3 rd)
{
    float distanceOrigin = 0.1;
    for(int i = 0; i < int(MAX_RAY_MARCHES); i++)
    {
        vec3 pos = ro + distanceOrigin * rd;
        float distance = DistanceEstimator(pos);
        if (distance < MAX_TOLERANCE || distanceOrigin > MAX_RAY_LENGTH) break;//miss the ray
        distanceOrigin += distance;//add shortest distance
    }
    return distanceOrigin;
}

//post proccessing
//ld: ligth direction
float DiffuseLighting(in vec3 nor, in vec3 ld)
{
    //the result tends to 1 if the normal and ligth are are pointing the same way
    return max(dot(nor,ld), 0.0);
}

//allows to show the origin of the ligth source on screen
float Specular(in vec3 nor, in vec3 ld, in vec3 rd)
{
    return pow(max(dot(reflect(ld, nor), rd),0.0), 75.0);
}

//basic shadows
float SoftShadow(in vec3 pos, in vec3 ld, float mint, float k)
{
    float res = 1.0;
    float t = mint;
    for(int i = 0; i < 32; i++)
    {
        //applies shadow base on the geometry render
        float distance = DistanceEstimator(pos + ld*t); 
        res = min(res, k*distance/t);
        t += max(distance, mint*0.2);
    }
    return clamp(res, 0.25, 1.0);
}


//render: call the raymarch function, base on result compute a color
vec3 Render(in vec3 ro, in vec3 rd)
{
    vec3 skyColor = mix(vec3(0.8, 0.8, 1.0) * 0.3, vec3(0.8, 0.8, 1.0)*0.6, rd.y * 0.75 + 0.25);
    vec3 sphereColor =  vec3(0.75, 0.70, 1.00);
    vec3 aggregateColor = vec3(0.0);
    vec3 lightPosition =  vec3(1.5, 3.0, -1.0) * -2.0;


    float agregateReflectionFactor = 1.0;

    //applies refractions by recalculate point(RAYTRACING MAGIC)
    for(int i =0; i < 3; i++)
    {
        float t = RayMarch(ro, rd);//distance to object    
        //ray intersect object
        if (t < MAX_RAY_LENGTH) 
        {
            //diffuse ligth contact the surface of object
            vec3 position = ro + t * rd; 
            vec3 lightDir = normalize(position - lightPosition);
            float d = DiffuseLighting(normal(position), lightDir);//ambient light
            //specular lighting
            //float spec = Specular(normal(position), lightDir, rd);
            //add shadows 
            float shadow = SoftShadow(position, lightDir, 0.01, 16.0);
            //get sky reflection(easy way) 
            vec3 reflectionDir =  reflect(rd, normal(position));

            aggregateColor += agregateReflectionFactor * reflectionFactor * sphereColor * d * shadow;
            agregateReflectionFactor *= 1.0 - reflectionFactor;
            ro = position;
            rd = reflectionDir;
        }
        else
        {
            aggregateColor += agregateReflectionFactor * getSkyColor(rd);//ray missed
            break;
        }

    }

    return aggregateColor;
}

