void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 outputColor = vec3(0.0);

    vec2 uv = fragCoord/iResolution.xy - vec2(0.5);
    uv.x *= iResolution.x / iResolution.y;

   //camera setup
    vec3 ro = vec3(5.0, 2.0, 2.0);   

    //rotation especcify the axis view
    Rotate(ro.xz, iTime*0.25);

    vec3 ww = normalize(vec3(0.0, -1.0, 0.0)-ro);//z
    vec3 uu = normalize(cross(vec3(0.0, 1.0, 0.0), ww));//x
    vec3 vv = normalize(cross(ww,uu));//y

    //ray direction
    vec3 rd = normalize(uv.x * uu + uv.y * vv + 2.5 * ww);   
    //get distance to object
    outputColor = Render(ro + vec3(0.25, 0.25, 0.0), rd);
    // Output to screen
    fragColor = vec4(outputColor,1.0);
}

