// gotta do a Truchet tiling amirite
#define TAU 6.283
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //----------------------------------- tiling and camera ------------------------------
    // fb_uv: original framebuffer uv
    // uv: uv of the each cell
    // s_uv: scaled uv
    // cell: vec2 of cell coords
    vec2 uv = fragCoord/iResolution.xy;
    float aspect = iResolution.x / iResolution.y;
    uv.x *= aspect;

    vec2 fb_uv = uv;
    float ytiles = 5. * (sin(iTime) + 2.);
    vec2 s_uv = uv * ytiles;

    // move the "camera" around
    s_uv -= ytiles * .5;
    mat2 canvasRotation = rot(iTime);
    s_uv *= canvasRotation;
    s_uv += ytiles * .5;
    s_uv += iTime * 5.;

    uv = fract(s_uv);    
    vec2 cell = floor(s_uv);

    //----------------------------------- truchet stuff -----------------------------------

    float noise = floor(.5 + texture(iChannel0, cell * .01).r);

    // temp change origin for rotation
    uv -= .5;
    mat2 cellRotation = rot(TAU / 4. * noise);
    uv *= cellRotation;
    uv += .5;

    vec2 mouse = iMouse.xy/iResolution.xy;
    mouse.x *= aspect;

    vec3 col = vec3(.2,.5,.9);

    const float width = 0.1;
    const float st = (.5 - width);
    const float en = (.5 + width);
    const float specpower = 6.;
    vec3 lcolor = vec3(.1, .9, 1.) * .6;
    vec3 scolor = vec3(1.);

    vec2 lp = normalize(mouse - fb_uv);
    vec3 lp3 = normalize(vec3(lp, 1.));

    // --- bottom left circle ---
    float d = length(uv);
    float t = (d - st) / (en - st);
    t = clamp(t, 0., 1.);
	float z = sin(t * PI);
    vec2 tang = canvasRotation * cellRotation * (normalize(uv) * (t * 2. - 1.));
    vec3 tcol = vec3(tang.xy, z);
    float incd = clamp(dot(tcol, lp3), 0., 1.);
    //float masking = (1. - pow(abs(cos(PI * z/2.)), 10.));
    // todo the "masking" term fixes the soft z but edges get crackly when the camera zooms out so whatev
    col = mix(col, incd * lcolor + pow(incd, specpower) * scolor, z);

    // --- top right circle ---
    float e = length(uv - 1.);
    t = 1. - (e - st) / (en - st);
    t = clamp(t, 0., 1.);
	z = sin(t * PI);
    tang = canvasRotation * cellRotation * (normalize(1. - uv) * (t * 2. - 1.));
    tcol = vec3(tang.xy, z);
    incd = clamp(dot(tcol, lp3), 0., 1.);
    //masking = (1. - pow(abs(cos(PI * z/2.)), 10.));
    col = mix(col, incd * lcolor + pow(incd, specpower) * scolor, z);

    // fake light
    col *= 1.5-length(fb_uv - mouse)*.5;


    fragColor = vec4(col,1.0);
}

vec2 rand(vec2 p)
{
