    vec2 ran = rand(fl);
    float t = pow(abs(sin(ran.x*6.2831 + iTime*(.25 + ran.y))), 8.);
    col = vec3(ran.x * ran.y, ran.y, .0)*g*s*t;
    fragColor = vec4(col,1.);
}

float tt, cy; 

mat2 rot(float a) {
	return mat2(cos(a), sin(a), -sin(a), cos(a));
}

float box(vec3 p, vec3 r) {
	p = abs(p) - r;
    return length(max(p, 0.)) + min(max(p.x, max(p.y, p.z)), 0.);
}

vec2 repeat(inout vec2 p, vec2 s) {
    vec2 id = floor(p/s-.5);
    // p = (fract(p/s-.5)-.5)*s;
    p = mod(p+.5*s, s)-.5*s;
    return id;
}


float N21(vec2 p) {
	return fract(sin(dot(p, vec2(341.234, 934.838)))*34234.23);
}


float map(vec3 p) {
    vec2 id = repeat(p.xz, vec2(3.2));
    float h = N21(id);
    vec2 c = vec2(1, -5.);
    float s = cos(length(id+c)+tt*2.)*.3 +.6;
    float w = sin(length(id+c)*0.7+tt*3.)*1.5;
    p.y += (exp(w)-1.5)*.7;
    cy = p.y;
    p.xy *= rot(h+0.3*tt*h*sign(h-.5));

    return (box(p, vec3(s))-.1);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    tt = iTime;

	vec3 ro = vec3(0, -10., -20),
         rd = normalize(vec3(-uv, .7)),
         l = normalize(vec3(0, -1, -4.));

    rd.yz *= rot(.6);

    vec3 col, bg;
    col = bg = mix(vec3(.78, .97, .99), vec3(.3), -(uv.y-0.3));  


    float i, d, t = 0.1;

    vec3 p;
    for(i=0.; i<100.; i++) {
     	p = ro + t*rd;
    	d = map(p);
        if(t < 0.0001 || t > 90.) break;       
		t += d;
    }

    vec2 e = vec2(.0005, -.0005);
    if(d < 0.001) {

        vec3 n = normalize(e.xyy*map(p+e.xyy) + e.yyx*map(p+e.yyx) +
                           e.yxy*map(p+e.yxy) + e.xxx*map(p+e.xxx));

        float ch = p.y - cy; // cube height
        float dif = max(dot(n, normalize(p-l)), .0);
        float fog = 1.-exp(-.00002*t*t*t);

        // subsurface scattering from evvvvil
        float sss = smoothstep(0., 1., map(p+l*.4));

        vec3 al = mix(vec3(0.1, 0.6, 0.8)*.9, vec3(.15, 0, 1), ch);
        col = al*.4 + 0.8*al*(dif+sss);
        col = mix(col, bg, 0.5*fog);
    }

    fragColor = vec4(col,1.0);
}

vec2 hash(float n) {
    return floor(vec2(fract(sin(n)*138.5453123), fract(sin(n)*832.83037595)) * 7.) / 7.;
}

float rippleValue(float n, vec2 uv) {
    vec2 params = hash(n + 0.3859903);
    params.x = params.x * 0.1 + 0.01;
    float ripple = floor(iTime * params.x + 10.);
    float distance = length(uv - hash(ripple * n) * vec2(iResolution.x/iResolution.y, 1.0));
    float peak = fract(iTime * params.x) * 2.0;
    float spread = peak * 0.1;
    return pow((smoothstep(peak-spread, peak, distance) - smoothstep(peak, peak+spread, distance)), 0.2) / pow(peak, 0.8);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.yy;
    vec3 value = vec3(0.);
    for (int i = 0; i < 20; i++) {
        float v = rippleValue(float(i), uv) * 2.;
        value += pow(vec3(v, v * 0.5, 0.), vec3(2.2));
        if (i % 6 == 0) value = value.xzy;
        if (i % 6 == 1) value = value.zyx;
        if (i % 6 == 2) value = value.yxz;
        if (i % 6 == 3) value = value.zxy;
        if (i % 6 == 4) value = value.yzx;

    }
    value = pow(value, vec3(1./2.2));
    fragColor = vec4(vec3(value*0.2 * (iMouse.y/iResolution.y + 1.0)), 1.0);
}

