#define GRID_SIZE 20.

const float PI = acos(-1.);

float screenIn(in vec2 uv) { return step(abs(uv.x-0.5), 0.5) * step(abs(uv.y-0.5), 0.5); }
mat2 rotate(in float r) { float c=cos(r),s=sin(r); return mat2(c,-s,s,c); }
float hash(in float v) { return fract(sin(v)*43237.5324); }
float hash(in vec2 v) { return fract(sin(dot(v, vec2(12.9898, 78.233)))*43237.5324); }

// video getter
vec4 tex1(in vec2 st) { return texture(iChannel0, st); }
vec4 tex2(in vec2 st) { return texture(iChannel1, st); }

// transition
// ratio - value to select video, 0~1
// st - uv coordination of the pixel.
vec4 transition(float ratio, in vec2 st) {
    vec2 uv = st;
    uv *= rotate(PI/6.);

    uv.y *= GRID_SIZE;
    vec2 id = vec2(0.);
    id.y = floor(uv.y);
    uv.x *= GRID_SIZE*hash(id.y);
    id.x = id.y + floor(uv.x);

    float angle = sign(hash(id.x)*2.-1.);
    float offset = hash(id.x + id.y)+1.414;

    vec2 uv1 = st;
    vec2 uv2 = st + vec2(angle * offset * ratio, 0.)*rotate(-PI/6.);

    return screenIn(uv2)<1. ? tex2(uv1) : tex1(uv2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float ratio = smoothstep(-1., 1., sin(iTime));
    vec4 video = transition(ratio, uv);

    vec3 col = uv.y<0.04 ? vec3(step(uv.x, ratio)) : video.rgb;

    fragColor = vec4(col,1.0);
}

/**
I tried modeling clouds using perlin-worley noise as described by Andrew Schneider
in the chapter Real-Time Volumetric Cloudscapes of GPU Pro 7. There are two types
of worley fbm functions used, a low frequency one to model the cloud shapes, and
a high frequency one used to add finer details around the edges of the clouds. Finally,
a simple 2D ray march along the light direction to add some fake lighting and shadows
to the cloudscapes.

Drag around the sun with the mouse to see a change in the lighting.
*/

#define CLOUD_COVERAGE .8
#define CLOUD_AMBIENT .01

// Hash functions by Dave_Hoskins
float hash12(vec2 p)
{
	uvec2 q = uvec2(ivec2(p)) * uvec2(1597334673U, 3812015801U);
	uint n = (q.x ^ q.y) * 1597334673U;
	return float(n) * (1.0 / float(0xffffffffU));
}

vec2 hash22(vec2 p)
{
	uvec2 q = uvec2(ivec2(p))*uvec2(1597334673U, 3812015801U);
	q = (q.x ^ q.y) * uvec2(1597334673U, 3812015801U);
	return vec2(q) * (1.0 / float(0xffffffffU));
}

float remap(float x, float a, float b, float c, float d)
{
    return (((x - a) / (b - a)) * (d - c)) + c;
}

// Noise function by morgan3d
float perlinNoise(vec2 x) {
    vec2 i = floor(x);
    vec2 f = fract(x);

	float a = hash12(i);
    float b = hash12(i + vec2(1.0, 0.0));
    float c = hash12(i + vec2(0.0, 1.0));
    float d = hash12(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);
	return mix(a, b, u.x) + (c - a) * u.y * (1.0 - u.x) + (d - b) * u.x * u.y;
}

vec2 curlNoise(vec2 uv)
{
    vec2 eps = vec2(0., 1.);

    float n1, n2, a, b;
    n1 = perlinNoise(uv + eps);
    n2 = perlinNoise(uv - eps);
    a = (n1 - n2) / (2. * eps.y);

    n1 = perlinNoise(uv + eps.yx);
    n2 = perlinNoise(uv - eps.yx);
    b = (n1 - n2)/(2. * eps.y);

    return vec2(a, -b);
}

float worleyNoise(vec2 uv, float freq, float t, bool curl)
{
    uv *= freq;
    uv += t + (curl ? curlNoise(uv*2.) : vec2(0.)); // exaggerate the curl noise a bit

    vec2 id = floor(uv);
    vec2 gv = fract(uv);

    float minDist = 100.;
    for (float y = -1.; y <= 1.; ++y)
    {
        for(float x = -1.; x <= 1.; ++x)
        {
            vec2 offset = vec2(x, y);
            vec2 h = hash22(id + offset) * .8 + .1; // .1 - .9
    		h += offset;
            vec2 d = gv - h;
           	minDist = min(minDist, dot(d, d));
        }
    }

    return minDist;
}

float perlinFbm (vec2 uv, float freq, float t)
{
    uv *= freq;
    uv += t;
    float amp = .5;
    float noise = 0.;
    for (int i = 0; i < 8; ++i)
    {
        noise += amp * perlinNoise(uv);
        uv *= 1.9;
        amp *= .55;
    }
    return noise;
}

// Worley fbm inspired by Andrew Schneider's Real-Time Volumetric Cloudscapes
// chapter in GPU Pro 7.
vec4 worleyFbm(vec2 uv, float freq, float t, bool curl)
{
    // worley0 isn't used for high freq noise, so we can save a few ops here
    float worley0 = 0.;
    if (freq < 4.)
    	worley0 = 1. - worleyNoise(uv, freq * 1., t * 1., false);
    float worley1 = 1. - worleyNoise(uv, freq * 2., t * 2., curl);
    float worley2 = 1. - worleyNoise(uv, freq * 4., t * 4., curl);
    float worley3 = 1. - worleyNoise(uv, freq * 8., t * 8., curl);
    float worley4 = 1. - worleyNoise(uv, freq * 16., t * 16., curl);

    // Only generate fbm0 for low freq
    float fbm0 = (freq > 4. ? 0. : worley0 * .625 + worley1 * .25 + worley2 * .125);
    float fbm1 = worley1 * .625 + worley2 * .25 + worley3 * .125;
    float fbm2 = worley2 * .625 + worley3 * .25 + worley4 * .125;
    float fbm3 = worley3 * .75 + worley4 * .25;
    return vec4(fbm0, fbm1, fbm2, fbm3);
}

float clouds(vec2 uv, float t)
{
    float coverage = hash12(vec2(uv.x * iResolution.y/iResolution.x, uv.y)) *
        .1 + (CLOUD_COVERAGE * .5 + .5); // coverage between whatever value and 1.
 	float pfbm = perlinFbm(uv, 2., t);
    vec4 wfbmLowFreq = worleyFbm(uv, 1.6, t * 1.25, false); // low freq without curl
    vec4 wfbmHighFreq = worleyFbm(uv, 8., t * 1.5, true); // high freq with curl
    float perlinWorley = remap(abs(pfbm * 2. - 1.),
                               1. - wfbmLowFreq.r, 1., 0., 1.);
    perlinWorley = remap(perlinWorley, 1. - coverage, 1., 0., 1.) * coverage;
    float worleyLowFreq = wfbmLowFreq.g * .625 + wfbmLowFreq.b * .25
        + wfbmLowFreq.a * .125;
    float worleyHighFreq = wfbmHighFreq.g * .625 + wfbmHighFreq.b * .25
        + wfbmHighFreq.a * .125;
    float c = remap(perlinWorley, (worleyLowFreq - 1.) * .64, 1., 0., 1.);
    c = remap(c, worleyHighFreq * .08, 1., 0., 1.);
    return max(0., c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.y;
    vec2 m = iMouse.xy / iResolution.y;
    float t = mod(iTime + 600., 7200.) * .03;

    // set up 2D ray march variables
    vec2 marchDist = vec2(.35 * max(iResolution.x, iResolution.y)) / iResolution.xy;
    const float steps = 10.;
    float stepsInv = 1. / steps;
    vec2 sunDir = normalize(m - uv) * marchDist * stepsInv;
    vec2 marchUv = uv;
    float cloudColor = 1.;
    float cloudShape = clouds(uv, t);

    // 2D ray march lighting loop based on uncharted 4
    for (float i = 0.; i < marchDist.x; i += marchDist.x * stepsInv)
    {
        marchUv += sunDir * i;
   		float c = clouds(marchUv, t);
        cloudColor *= clamp(1. - c, 0., 1.);
    }

    cloudColor += CLOUD_AMBIENT; // ambient
    // beer's law + powder sugar
    cloudColor = exp(-cloudColor) * (1. - exp(-cloudColor*2.)) * 2.;
    cloudColor *= cloudShape;

    vec3 skyCol = mix(vec3(.1, .5, .9), vec3(.1, .1, .9), uv.y);
    vec3 col = vec3(0.);
    col = skyCol + cloudShape;
  	col = mix(vec3(cloudColor) * 25., col, 1.-cloudShape);
    float sun = .002 / pow(length(uv - m), 1.7);
    col += (1. - smoothstep(.0, .4, cloudShape)) * sun;
    fragColor = vec4(sqrt(col), 1.0);
}

#define PI 3.14159265358979323846

vec2 rotate2D(vec2 _st,float _angle){
  _st-=.5;
  _st=mat2(cos(_angle),-sin(_angle),
  sin(_angle),cos(_angle))*_st;
  _st+=.5;
  return _st;
}

vec2 tile(vec2 _st,float _zoom){
  _st*=_zoom;
  return fract(_st);
}

vec2 rotateTilePattern(vec2 _st){

  //  Scale the coordinate system by 2x2
  _st*=2.;

  //  Give each cell an index number
  //  according to its position
  float index=0.;
  index+=step(1.,mod(_st.x,2.));
  index+=step(1.,mod(_st.y,2.))*2.;

  //      |
  //  2   |   3
  //      |
  //--------------
  //      |
  //  0   |   1
  //      |

  // Make each cell between 0.0 - 1.0
  _st=fract(_st);

  // Rotate each cell according to the index
  // 0 rotates 0
  if(index==1.){
    //  Rotate cell 2 by -90 degrees
    _st=rotate2D(_st,PI*-.5);
  }else if(index==2.){
    //  Rotate cell 1 by 90 degrees
    _st=rotate2D(_st,PI*.5);
  }else if(index==3.){
    //  Rotate cell 3 by 180 degrees
    _st=rotate2D(_st,PI);
  }

  return _st;
}

// http://www.iquilezles.org/www/articles/palettes/palettes.htm
// As t runs from 0 to 1 (our normalized palette index or domain),
//the cosine oscilates c times with a phase of d.
//The result is scaled and biased by a and b to meet the desired constrast and brightness.
vec3 cosPalette(float t,vec3 a,vec3 b,vec3 c,vec3 d)
{
  return a+b*cos(6.28318*(c*t+d));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
  vec2 myRes = iResolution.yy/1.5;
  vec2 st=fragCoord.xy/myRes.xy;

  st=tile(st,3.);
  st=rotateTilePattern(st);

  // Make more interesting combinations
  st=tile(st,3.);
  st=rotate2D(st,-PI*iTime*.15);
  //st = rotateTilePattern(st*2.);
  // st = rotate2D(st,PI*u_time*0.25);

  vec3 color=cosPalette(iTime/10.,vec3(.8),vec3(.3),vec3(1.),vec3(iTime*.01,iTime*.1,iTime*.2));
  // step(st.x,st.y) just makes a b&w triangles
  // but you can use whatever design you want.
  fragColor=vec4(vec3(step(st.x,st.y))*color,1.);
}

#define MAX_STEPS 50
#define MAX_DIST 50.
#define SURF_DIST .001

//#define SHADOWS // optional shadows

#define PI 3.1415926
#define minDistance 100.
#define SEED 345.
#define iTime (iTime+SEED)



vec2 N22(vec2 p){
    vec3 a = fract(p.xyx * vec3(123.34, 234.34, 345.65));
    a += dot(a, a + 34.45);
    return fract(vec2(a.x * a.y, a.y * a.z));
}

vec2 voronoi(vec2 p){
	vec2 gv = fract(p) - 0.5;
    vec2 id = floor(p);
    float minDist = minDistance;
    float d = 0.;
    vec2 cid = vec2(0);
    for (float y = -1.; y <= 1.; y++){
        for (float x = -1.; x <= 1.; x++){
        	vec2 offs = vec2(x, y);

            vec2 n = N22(id + offs);
            vec2 p = offs + sin(n * SEED) * .25;
            p += cos(n * iTime) * 0.0666;
            d = length(gv - p);
            if (d < minDist) {
            	minDist = d;
                cid = n;
            }
        }
    }

    vec2 mult = cid * iTime * PI * 0.5;

    return vec2(minDist, minDist * 8. + (sin(mult) * cos(mult * .25)));
}

vec2 sdFloor(vec3 p){
    float d = p.y;

    vec2 vor = voronoi(p.xz);
    float v = vor.x;
    v = sin(v * PI * 1.5) * (0.25 + sin(vor.y) * 0.25) + 0.25;

    d -= pow(v, 4.);

    float r = texture(iChannel0, p.xz).r;
	r = pow(r, 2.) * 0.05;
    d += r;

    float g = texture(iChannel1, p.xz * 0.5).g;
    d += pow(g, 4.) * 0.05;         

    return vec2(d * 0.5, vor.y);
}


vec2 GetDist( vec3 p ){

    return sdFloor(p);
}

vec2 RayMarch( vec3 ro, vec3 rd ){

	vec2 dO = vec2(0.);

    for (int i = 0; i < MAX_STEPS; i++){
    	vec3 p = ro + rd * dO.x;
        vec2 dS = GetDist(p);
        dO.x += dS.x;
        dO.y = dS.y;
        if (dO.x > MAX_DIST || dO.x < SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal( vec3 p ){

    vec2 e = vec2(.0001, 0);

    vec3 n = vec3(
        GetDist(p + e.xyy).x - GetDist(p - e.xyy).x,
        GetDist(p + e.yxy).x - GetDist(p - e.yxy).x,
        GetDist(p + e.yyx).x - GetDist(p - e.yyx).x);

    return normalize(n);
}

float GetLight( vec3 p ){

	vec3 lightPos = vec3(0, 5, 0);
    lightPos.xz += vec2(cos(iTime * 0.328), sin(iTime)) * 2.;
    vec3 l = normalize(lightPos - p);

    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);

    float plDist = length(lightPos - p);
    #ifdef SHADOWS

        float d = RayMarch(p + n * SURF_DIST * 2., l).x;
        if (d < plDist) dif *= 0.25;

    #endif
    dif *= smoothstep(10., 0., plDist);

    return dif;
}

vec3 setFromSphericalCoords( float radius, float phi, float theta ) { // stolen from three.js :-P

    	vec3 v = vec3(0);

    	float sinPhiRadius = sin( phi ) * radius;

		v.x = sinPhiRadius * sin( theta );
		v.y = cos( phi ) * radius;
		v.z = sinPhiRadius * cos( theta );

		return v;

}


