    vec3 lookAt = vec3(0, 0, 0);

    float phi = phiStart - mouse.y * (PI / 6.1);
    float theta = thetaStart + mouse.x * PI;

    vec3 ro = setFromSphericalCoords(distToOrigin, phi, theta);


    float zoom = 1.;
    vec3 f = normalize(lookAt - ro),
        r = normalize(cross(vec3(0, 1, 0), f)),
        u = cross(f, r),
        c = ro + f * zoom,
        i = c + uv.x * r + uv.y * u,
        rd = normalize(i - ro);

    // -------------------------------------------------------

    vec2 d = RayMarch( ro, rd );  // returns distance(x) and data for gradient (y);

    vec3 p = ro + rd * d.x;

    float dif = GetLight(p) * 1.5;

    //vec3 col = vec3(0.125, 0.3, 0.25);
