// Modelisation du cadran 	
float Dial(vec3 p, bool simplified)
{	
  float dial = disk(p, vec3(3.0, 18.0, 0.0), vec3(1.0, 0.0, 0.0), 5.0, 1.0, 1.5);
  float holeCy1 = cylinder(p, vec3(4.7, 18.0, 0.0), vec3(1.0, 0.0, 0.0), 4.3, 1.0, 1.0, 1.0);
  dial = Diff(dial, holeCy1);

  float midSeg1 = segment(p, vec3(3.0, 18.0, 0.0), vec3(3.7, 18.0, 0.0), 1.0, 0.5);
  dial = Union(dial, midSeg1);

  if(!simplified)
  {
      float twelve = segment(p, vec3(3.2, 21.5, 0.6), vec3(3.2, 22.0, 0.6), 1.0, 0.3);
      twelve = Union(twelve, segment(p, vec3(3.2, 21.5, 0.2), vec3(3.2, 22.0, 0.2), 1.0, 0.3));
      twelve = Union(twelve, segment(p, vec3(3.2, 21.5, -0.3), vec3(3.2, 22.0, -0.7), 1.0, 0.3));
      twelve = Union(twelve, segment(p, vec3(3.2, 22.0, -0.3), vec3(3.2, 21.5, -0.7), 1.0, 0.3));
      dial = Union(dial, twelve);

      float three = segment(p, vec3(3.2, 17.75, 3.5), vec3(3.2, 18.25, 3.5), 1.0, 0.3);
      three = Union(three, segment(p, vec3(3.2, 17.75, 3.9), vec3(3.2, 18.25, 3.9), 1.0, 0.3));
      three = Union(three, segment(p, vec3(3.2, 17.75, 4.3), vec3(3.2, 18.25, 4.3), 1.0, 0.3));
      dial = Union(dial, three);

      float six = segment(p, vec3(3.2, 14.5, -0.7), vec3(3.2, 14.0, -0.4), 1.0, 0.3);
      six = Union(six, segment(p, vec3(3.2, 14.0, -0.4), vec3(3.2, 14.5, -0.1), 1.0, 0.3));
      six = Union(six, segment(p, vec3(3.2, 14.5, 0.3), vec3(3.2, 14.0, 0.3), 1.0, 0.3));
      dial = Union(dial, six);

      float nine = segment(p, vec3(3.2, 17.75, -3.9), vec3(3.2, 18.25, -3.5), 1.0, 0.3);
      nine = Union(nine, segment(p, vec3(3.2, 17.75, -3.5), vec3(3.2, 18.25, -3.9), 1.0, 0.3));
      nine = Union(nine, segment(p, vec3(3.2, 17.75, -4.3), vec3(3.2, 18.25, -4.3), 1.0, 0.3));
      dial = Union(dial, nine);
  }   

  return dial;
}

// Modelisation de l'engrenage qui tourne
float Clockwork(vec3 p, float t, bool simplified)
{
  float clockwork = cylinder(p, vec3(-3.0, 10.0, 0.0), vec3(0.0, 1.0, 0.0), 0.2, 3.0, 1.0, 1.0); 

  if(!simplified)
  {
      float t1 = t + M_PI/2.0;
      float t2 = t + M_PI;
      float t3 = t + (3.0 * M_PI)/2.0;

      float holeSeg1 = segment(p, vec3(cos(t) - 3.0, 12.0, -sin(t)), 
                                    vec3(cos(t1) - 3.0, 8.0, -sin(t1)), 1.0, 1.4); 
      clockwork = Diff(clockwork, holeSeg1);

      float holeSeg2 = segment(p, vec3(cos(t2) - 3.0, 12.0, -sin(t2)), 
                                    vec3(cos(t3) - 3.0, 8.0, -sin(t3)), 1.0, 1.4); 
      clockwork = Diff(clockwork, holeSeg2);
  }

  return clockwork;
}

// Modelisation des aiguilles qui tournent
float HandMoving(vec3 p, float t)
{
  float t1 = t / 12.0;

  float hands = segment(p, vec3(3.4, 18.0, 0.0), 
                        vec3(3.4, cos(t) * 3.0 + 18.0,  sin(t) * 3.0),
                        1.0, 0.2);
  hands = Union(hands, segment(p, vec3(3.7, 18.0, 0.0), 
                        vec3(3.8, cos(t1) * 2.0 + 18.0,  sin(t1) * 2.0),
                        1.0, 0.2));
  return hands;
}

// Modelisation des pendules qui tournent
float PendulumMoving(vec3 p, float t)
{
  float t1 = t + M_PI/2.0;
  float t2 = t + M_PI;
  float t3 = t + (3.0 * M_PI)/2.0;

  float ball1 = sphere(p, vec3(cos(t) * 3.0 - 3.0, 6.0, sin(t) * 3.0), 1.2, 1.0, 1.0);
  float ball2 = sphere(p, vec3(cos(t1) * 3.0 - 3.0, 6.0, sin(t1) * 3.0), 1.2, 1.0, 1.0);
  float pendulums = Union(ball1, ball2);

  float ball3 = sphere(p, vec3(cos(t2) * 3.0 - 3.0, 6.0, sin(t2) * 3.0), 1.2, 1.0, 1.0);
  pendulums = Union(pendulums, ball3);

  float ball4 = sphere(p, vec3(cos(t3) * 3.0 - 3.0, 6.0, sin(t3) * 3.0), 1.2, 1.0, 1.0);
  pendulums = Union(pendulums, ball4);

  float attachSeg1 = segment(p, vec3(cos(t) * 3.0 - 3.0, 6.0, sin(t) * 3.0), 
                             vec3(-3.0, 6.0, 0.0), 1.0, 0.3); 
  pendulums = Blend(pendulums, attachSeg1);

  float attachSeg2 = segment(p, vec3(cos(t1) * 3.0 - 3.0, 6.0, sin(t1) * 3.0), 
                             vec3(-3.0, 6.0, 0.0), 1.0, 0.3); 
  pendulums = Blend(pendulums, attachSeg2);

  float attachSeg3 = segment(p, vec3(cos(t2) * 3.0 - 3.0, 6.0, sin(t2) * 3.0), 
                             vec3(-3.0, 6.0, 0.0), 1.0, 0.3); 
  pendulums = Blend(pendulums, attachSeg3);

  float attachSeg4 = segment(p, vec3(cos(t3) * 3.0 - 3.0, 6.0, sin(t3) * 3.0), 
                             vec3(-3.0, 6.0, 0.0), 1.0, 0.3);  
  pendulums = Blend(pendulums, attachSeg4);

  return pendulums;
}

float Object_complex(vec3 p)
{
  float t = iTime;

  // base horloge
  float base = Base(p, false);

  // arriere du cadran
  float battery = Battery(p, false);
  float finalObj = Union(base, battery);

  // cadran
  float dial = Dial(p, false);
  finalObj = Union(finalObj, dial);

  // engrenage
  float clockwork = Clockwork(p, t, false);
  finalObj = Union(finalObj, clockwork);

  // aiguilles
  float hands = HandMoving(p, t);
  finalObj = Union(finalObj, hands);

  // pendules
  float pendulums = PendulumMoving(p, t);
  finalObj = Blend(finalObj, pendulums);

  return finalObj;  
}

float Object_simplified(vec3 p)
{
  float t = iTime;  

  // base horloge
  float base = Base(p, true);

  // arriere du cadran
  float battery = Battery(p, true);
  float finalObj = Union(base, battery);

  // cadran
  float dial = Dial(p, true);
  finalObj = Union(finalObj, dial);

  // mecanisme
  float clockwork = Clockwork(p, t, true);
  finalObj = Union(finalObj, clockwork);

  // aiguilles
  float hands = HandMoving(p, t);
  finalObj = Union(finalObj, hands);

  // pendules
  float pendulums = PendulumMoving(p, t);
  finalObj = Blend(finalObj, pendulums);

  return finalObj;
}

