// Calculate object normal
// p : point
vec3 ObjectNormal(in vec3 p )
{
  float eps = 0.0001;
  vec3 n;
  float v = Object(p);
  n.x = Object( vec3(p.x+eps, p.y, p.z) ) - v;
  n.y = Object( vec3(p.x, p.y+eps, p.z) ) - v;
  n.z = Object( vec3(p.x, p.y, p.z+eps) ) - v;
  return normalize(n);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// h : hit
// s : Number of steps
float Trace(vec3 o, vec3 u, out bool h,out int s)
{
  h = false;

  // Don't start at the origin, instead move a little bit forward
  float t=rA;

  for(int i=0; i<Steps; i++)
  {
    s=i;
    vec3 p = o+t*u;
    float v = Object(p);
    // Hit object
      if (v > 0.0)
      {
          s=i;
          h = true;
          break;
      }
      // Move along ray
      t += Epsilon;
      // Escape marched far away
      if (t>rB)
      {
          break;
      }
  }
  return t;
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// h : hit
// s : Number of steps
float SphereTrace(vec3 o, vec3 u, out bool h,out int s)
{
  h = false;

    // Don't start at the origin, instead move a little bit forward
    float t=rA;

  for(int i=0; i<Steps; i++)
  {
    s=i;
    vec3 p = o+t*u;
    float v = Object(p);
    // Hit object
      if (v > 0.0)
      {
          s=i;
          h = true;
          break;
      }
      // Move along ray
      t += max(Epsilon,abs(v)/4.0);
      // Escape marched far away
      if (t>rB)
      {
          break;
      }
  }
  return t;
}


// Background color
// d : Ray direction
vec3 Background(vec3 d)
{
  return mix(vec3(0.4, 0.3, 0.75), vec3(0.75, 0.85, 1.0), d.y*0.5+0.5);
}

// Shading and lighting
// p : point,
// n : normal at point
vec3 Shade(vec3 p, vec3 n)
{
  // point light
  const vec3 lightPos = vec3(5.0, 5.0, 5.0);
  const vec3 lightColor = vec3(0.5, 0.5, 0.5);

  vec3 c = 0.25*Background(n);
  vec3 l = normalize(lightPos - p);

  // Not even Phong shading, use weighted cosine instead for smooth transitions
  float diff = 0.5*(1.0+dot(n, l));

  c += diff*lightColor;

  return c;
}

// Shading with number of steps
vec3 ShadeSteps(int n)
{
   float t=float(n)/(float(Steps-1));
   return vec3(t,0.25+0.75*t,0.5-0.5*t);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 pixel = (gl_FragCoord.xy / iResolution.xy)*2.0-1.0;

  // compute ray origin and direction
  float asp = iResolution.x / iResolution.y;
  //vec3 rd = normalize(vec3(asp*pixel.x, pixel.y, -4.0));
  vec3 rd = normalize(vec3(asp*pixel.x, pixel.y, -2.0));
  vec3 ro = vec3(0.0, 10.0, 30);

  // position camera base
  ro = rotateY(ro, 90.0);
  rd = rotateY(rd, 90.0);
  ro = rotateZ(ro, -0.2);
  rd = rotateZ(rd, -0.2);

  vec2 mouse = iMouse.xy / iResolution.xy;
  ro = rotateY(ro, -mouse.x * 5.0);
  rd = rotateY(rd, -mouse.x * 5.0);
  ro = rotateZ(ro, -mouse.y * 5.0);
  rd = rotateZ(rd, -mouse.y * 5.0);


  //float a=iTime*0.25;
  //ro = rotateY(ro, a);
  //rd = rotateY(rd, a);


  // Trace ray
  bool hit;

  // Number of steps
  int s;

  float t = SphereTrace(ro, rd, hit,s);
  vec3 pos=ro+t*rd;
  // Shade background
  vec3 rgb = Background(rd);

  if (hit)
  {
    // Compute normal
    vec3 n = ObjectNormal(pos);

    // Shade object with light
    rgb = Shade(pos, n);
  }

  // Uncomment this line to shade image with false colors representing the number of steps
  //rgb = ShadeSteps(s);

  fragColor=vec4(rgb, 1.0);
}

#define S(a,b,t) smoothstep(a,b,t)

vec4 Grid(vec2 point, float num)
{
    vec2 r = vec2(1, 1.732);
    vec2 h = r*.5;
    vec2 uv = point*num;

    vec2 a = mod(uv, r) - h;
    vec2 b = mod(uv-h, r) - h;

    uv = length(a)<length(b)? a:b;
    vec2 id = floor(point*num);

    return vec4(uv, id); 
}

float Hex(vec2 uv)
{
    uv=abs(uv);
    float c = dot(uv, normalize(vec2(1, 1.732))); 
    c=max(c, uv.x);
    float d = atan(uv.x, uv.y);

    return c;
}

vec2 Polar(vec2 uv)
{
    return vec2 (length(uv), atan(uv.x, uv.y)+3.1415);
}

float N21(vec2 p)
{
    p=fract(p*vec2(233.34, 851.73));
    p+=dot(p,p+23.45);
    return fract(p.x*p.y);
}

vec2 N22(vec2 p)
{
    float n=N21(p);
    return vec2(n, N21(p+n));
}

vec2 GetPos(vec2 id, vec2 offs)
{
    vec2 n =N22(id+offs)*iTime;

    return offs+sin(n)*.4;
}

float DistLine(vec2 p, vec2 a, vec2 b)
{
    vec2 pa = p-a;
    vec2 ba = b-a;
    float t=clamp(dot(pa,ba)/dot(ba,ba), 0., 1.);
    return length(pa-ba*t);
}

float Line(vec2 p, vec2 a, vec2 b)
{
    float d = DistLine(p,a,b);
    float f = 1.-min(length(p-a), length(p-b));
    float m= S(f*2., .2, d*20.);
    m *=S(1.2, .8, length(a-b));
    m *= f*f*f;
    return m;
}

float Layer(vec2 uv)
{
    float m=0.;
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    vec2 p[9];
    int i=0;
    for(int x=-1; x<2; x++)
        for(int y=-1; y<2; y++)
        {
            p[i++] = GetPos(id, vec2(x,y));
        }
    for(int i=0; i<9; i++)
    {
        m+=Line(gv, p[4], p[i]);

        float j = length(vec2(p[i] - gv)*15.);
        float spark =1. / dot(dot(j,j), j);
        m+= spark * sin(iTime+id.x+id.y)*.5;
    }
    m+=Line(gv, p[1], p[3]);
    m+=Line(gv, p[1], p[5]);
    m+=Line(gv, p[7], p[3]);
    m+=Line(gv, p[7], p[5]);

    return m;
}

vec3 Universe(vec2 uv, float m)
{
    if (m>0.) return vec3(0);
    float t = iTime*.1;
    mat2 rot = mat2(cos(t), -sin(t), sin(t), cos(t));
    uv *=rot;

    for(float i=0.; i<1.; i+= 1./4.)
    {
        float z = fract(i+t);
        float size = mix(10., .5, z);
        float fade = S(0., .5, z) * S(1., .8, z);

        m+=Layer(uv*size+i*20.)*fade;
    }

    vec3 base = sin(t*vec3(.3, .5, .7))*.2 +.6;
    return vec3(m*base);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    //uv += iTime/10.;
	vec4 coord = Grid(uv, 20.);
    vec2 st = coord.xy;

    // c= hex radius
    float r = length(Hex(st));
    float index = ((coord.w*3.) + (coord.z*3.))*.06;
    float d = length(fragCoord - iResolution.xy/2.)/iResolution.y;
    float r1 = smoothstep(.3+sin(iTime+d*3.)*.1,.4+sin(iTime+d*3.)*.1732 ,r);
    float r2 = smoothstep(.27+sin(iTime+d*3.)*.1,.37+sin(iTime+d*3.)*.15 ,r);

    vec3 col = vec3(.0, .3, .4+(sin(iTime*.3+d)*.2))*(.5-r)*step(r1, .5);
    if (r>r1 && r<r2) col+=vec3(.2);

    col +=Universe(uv, col.b);    
    // Output to screen
    fragColor = vec4(col, 1.0);
}

// Useful macro for time
#define time iTime
// Functions defining a circle, vertical line and horizontal line
float circle (vec2 p, float r)
{
	return length(p) - r;
}
float vline (vec2 p) 
{
	return abs(p.x);
}
float hline (vec2 p)
{
	return abs(p.y);
}
// Merge two objects (used for the circles)
vec2 merge(vec2 d1, vec2 d2) {
    return abs(d1).x<abs(d2.x) ? d1 : d2;
}
// Repeat function using fract
vec4 cRep4(vec2 p, float n) 
{
     vec2 pn = p * n;
     return vec4(fract(pn) * 2.0 - 1.0, floor(pn)+vec2(1.0));
}
//
float asLine(float d) {
    return smoothstep(.1,.0,abs(d));
}

float asDisc(float d) {
    return smoothstep(.2,.1,d);
}
// Do points of given cell
vec2 doCell(vec2 local, vec2 cellCoord, vec2 cellOffset) 
{
    vec2 l = local - cellOffset * 2.0; // adjusted local coords
    vec2 id = cellCoord + cellOffset; // cell coordinates/id
    float c = circle(l+vec2(sin(time*1.7+id.x*id.y*-.02)*id.y*.2, cos(time*1.7+id.x*id.y*-.02)*id.y*.2), .1);
    return vec2(c, id.x*id.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // uv = Pixel normalised coordinates
	vec2 uv = (2.*fragCoord-iResolution.xy )/iResolution.y;
    // Lines to form divisions between panels
    float line1 = smoothstep(.01, .02, vline(uv));
    float line2 = smoothstep(.01, .02, hline(uv));

    //Checking which area the current pixel is in and running the appropriate code for that panel
    if (uv.x < 0. && uv.y < 0.) 
    {   
        // Bottom left panel
    	uv = uv + vec2(.9, .51);

        // c1 is the large circle, c2 is the moving one
    	float c1 = smoothstep(.02, .001, abs(circle(uv, (.4))));
    	float c2 = smoothstep(.02, .01, circle(uv+vec2(sin(time*2.), cos(time*2.))*.4, (.05)));

        fragColor = vec4(vec3(c1+c2, .1, .1)*line1*line2, 1.0);
    }
    else if (uv.y > 0.)
    {	
        // Top panel

        float surface = 13.;                     
        vec4 cell = cRep4(uv, 15.);
        vec2 dist = vec2(100.0,0);
        // Looking at neighbouring and displaying dots leaking from other cells to prevent cutoff
        if(cell.w<surface-2.) {
            dist = merge(dist, doCell(cell.xy, cell.zw, vec2(-1,1)));
            dist = merge(dist, doCell(cell.xy, cell.zw, vec2(1,1)));
            dist = merge(dist, doCell(cell.xy, cell.zw, vec2(0,1)));
        }
        if(cell.w<surface-1.) {
            dist = merge(dist, doCell(cell.xy, cell.zw, vec2(0.0)));
            dist = merge(dist, doCell(cell.xy, cell.zw, vec2(1,0)));
            dist = merge(dist, doCell(cell.xy, cell.zw, vec2(-1,0)));
        }
        if(cell.w<surface) {
        	dist = merge(dist, doCell(cell.xy, cell.zw, vec2(1,-1)));
        	dist = merge(dist, doCell(cell.xy, cell.zw, vec2(-1,-1)));
        	dist = merge(dist, doCell(cell.xy, cell.zw, vec2(0,-1)));
        }


        fragColor = vec4(vec3(asDisc(dist.x), .1, .1), 1.);//vec4(vec3(c3, .1, .1)*line2, 1.0);
    }
    else
    { 	
        // Bottom right panel
     	// Defining the plane waves, i.e. lines
     	float planewave = sin(uv.x*30.+iTime*10.)*.7+.3;

    	fragColor = vec4(planewave*.7, .1, .1, 1.)*line1*line2;       
    }
}

#define PI		3.14159265359

#define hue(h) clamp( abs( fract(h + vec4(3,2,1,0)/3.) * 6. - 3.) -1. , 0., 1.)

void rotate(in float angle, inout vec2 uv)
{    
    float ca = cos(angle);
    float sa = sin(angle);
    uv *= mat2(ca, -sa, sa, ca);	
}

float map(vec3 p) 
{
    return length(mod(p, 2.0) - 1.0) - 1.375;
}

vec3 getNormal(vec3 p) 
{
    float t = map(p);
    vec2 d = vec2(.5, 0.0);
    return normalize(vec3(t - map(p + d.xyy), t - map(p + d.yxy), t - map(p + d.yyx)));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;
    vec2 q = fragCoord.xy / iResolution.xy;

    rotate(iTime*.5, uv);

    float tZ = (sin(iTime) * 0.25 + 0.5) * 0.75 + .25;

    vec3 camDir = normalize(vec3(uv*5. , 1.1));

    // Compute the orientation of the camera
	float yawAngle = PI * (1.2 + 0.2 * cos (iTime * 0.5));
	float pitchAngle = PI * (0.1 * cos (iTime * 0.3) - 0.05);

	yawAngle += 4.0 * PI * iMouse.x / iResolution.x;
	pitchAngle += PI * 0.3 * (1.0 - iMouse.y / iResolution.y);

    float cosYaw = cos (yawAngle);
	float sinYaw = sin (yawAngle);
	float cosPitch = cos (pitchAngle);
	float sinPitch = sin (pitchAngle);

    mat3 cameraOrientation;
	cameraOrientation [0] = vec3 (cosYaw, 0.0, -sinYaw);
	cameraOrientation [1] = vec3 (sinYaw * sinPitch, cosPitch, cosYaw * sinPitch);
	cameraOrientation [2] = vec3 (sinYaw * cosPitch, -sinPitch, cosYaw * cosPitch);

	camDir = cameraOrientation * camDir;

    vec3 camPos = vec3(1.0, 1. , - iTime * 3.);    

    float t = 0.0;
    for(int i = 0 ; i < 100; i += 1) {
        t += map(camDir * t + camPos);        
    }
    vec3 surf = camDir * t + camPos;    
    vec3 light = normalize(vec3(0.0, 0.0, 1.0)) ;
    vec3 normal = getNormal(surf);    

    float cg = (camDir * t).x + (camDir * t).y + (camDir * t).z;

    vec3 col = hue(cg*.05 - iTime * .2 ).rgb * clamp(dot(light, normal), .25, 1.);

    // Border dark
    col *= 0.2 + 0.8 * pow(32.0 * q.x * q.y * (1.0 - q.x) * (1.0 - q.y), 0.3);   

    fragColor = vec4(col, 1.0);
}

vec3 rotate(vec3 p, float angle, vec3 axis)
{
    vec3 a = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float r = 1.0 - c;
    mat3 m = mat3(
        a.x * a.x * r + c,
        a.y * a.x * r + a.z * s,
        a.z * a.x * r - a.y * s,
        a.x * a.y * r - a.z * s,
        a.y * a.y * r + c,
        a.z * a.y * r + a.x * s,
        a.x * a.z * r + a.y * s,
        a.y * a.z * r - a.x * s,
        a.z * a.z * r + c
    );
    return m * p;
}

float map(vec3 p){
  vec3 w = p;
  float m = dot(w,w);

  vec4 trap = vec4(abs(w),m);
	float dz = 1.;


	for( int i=0; i<4; i++ )
  {
    float power = 3. + 5. * abs(cos(iTime));
    dz = power*pow(sqrt(m),power-1.)*dz + 1.0;
		// dz = 8.0*pow(m,3.5)*dz + 1.0;

    float r = length(w);
    float b = power*acos( w.y/r) + iTime * 5.;
    float a = power*atan( w.x, w.z ) + iTime * 2.;
    w = p + pow(r,power) * vec3( sin(b)*sin(a), cos(b), sin(b)*cos(a) );
    trap = min( trap, vec4(abs(w),m) );

    m = dot(w,w);
		if( m > 256.0 )
            break;
  }

  return 0.25*log(m)*sqrt(m)/dz;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){

    vec2 uv = (fragCoord / iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec4 c = vec4(uv, 0.75, 1.0);

    vec3 ro = vec3(0. , 0., cos(0.)) * 2.;
    vec3 rd = vec3(uv, (1.-dot(uv,uv)));
    rd = rotate(rd, 3.14159265, vec3(1., 0., 0.));

    // t is total distance
    // d is step distance
    float i = 0., t = 0., d = 0.;
    vec3 p;
    // raymarching loop
    for(i = 0.; i < 100.; i++){
      p = ro + rd * t;
      d = map(p);
      t += d*.75;
      if(d < 0.001 || d > 100.){break;}
    }

    vec4 blue = vec4(0.5411, 0.90196, 0.8745, 1.);
    vec4 pink = vec4(1.0, 0.72156, 0.7686, 1.0);

    c = (1. - vec4(i / 150.)) * blue * 1.2;
    if(d > 100.) { c =  pink; }

    fragColor = c;
}

/**
*	ray marching tools adapted from hughsk's 2D SDF Toy https://www.shadertoy.com/view/XsyGRW
*/

#define TRACE_STEPS 20
#define TRACE_RAY

// 0 = Distance Field Display
// 1 = Raymarched Edges
// 2 = Resulting Solid
// 3 = Distance Field Polarity
#define DISPLAY 0

// 0 = Angle controlled By iTime
// 1 = Angle controlled By iMouse
#define MOUSE 0
const float PI = 3.14159265359;

float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

vec2 squareFrame(vec2 screenSize, vec2 coord) {
    vec2 position = 2.0 * (coord.xy / screenSize.xy) - 1.0;
    position.x *= screenSize.x / screenSize.y;
    return position;
}

// translational symmetry
vec2 tsym(vec2 p, float m) {
    return mod(p + m*.5, m) - m*.5;
}

float tsym(float p, float m) {
    return mod(p + m*.5, m) - m*.5;
}

// rotational symmetry
vec2 rsym(vec2 p, float n){
	float pr = length(p);
    float pa = atan(p.y, p.x);
    pa = tsym(pa, 2.*PI/n);
    p = vec2(pr*cos(pa), pr*sin(pa));
    return p;
}

vec2 rotate(vec2 p, float a) {
    return vec2(p.x*cos(a) - p.y*sin(a),
                p.x*sin(a) + p.y*cos(a));   
}

float add(float a, float b) {
    return min(a, b);
}

float sub(float a, float b) {
	return max(a, -b);
}



float shape_circle(vec2 p, float r) {
    return length(p) - r;
}

float shape_rect(vec2 p, vec2 b) {
    vec2 d = abs(p) - b;
    return length(max(d, 0.0)) +  min(max(d.x, d.y), 0.0);  // out + in
}

float shape_line(vec2 p, vec2 a, vec2 b) {
    vec2 dir = b - a;
    return abs(dot(normalize(vec2(dir.y, -dir.x)), a - p));
}

float shape_segment(vec2 p, vec2 a, vec2 b) {
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

//https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float shape_trapezoid(vec2 p, float r1, float r2, float he ) {
    vec2 k1 = vec2(r2,he);
    vec2 k2 = vec2(r2-r1,2.0*he);
    p.x = abs(p.x);
    vec2 ca = vec2(p.x-min(p.x,(p.y<0.0)?r1:r2), abs(p.y)-he);
    vec2 cb = p - k1 + k2*clamp( dot(k1-p,k2)/dot(k2, k2), 0.0, 1.0 );
    float s = (cb.x<0.0 && ca.y<0.0) ? -1.0 : 1.0;
    return s*sqrt( min(dot(ca, ca),dot(cb, cb)) );
}



float shape_revolver_base(vec2 p) {
    return shape_trapezoid(p + vec2(0, .5), .15, .1, .8) - .05;
}

float shape_revolver_cylinder_fill(vec2 p) {
    vec2 p1 = rotate(p, iTime);
    return shape_circle(p1, .4);
}

float shape_revolver_cylinder_cut(vec2 p) {
    float cbig = shape_circle(p, .4);
    float cmid  = shape_circle(p, .03);

    vec2 p11 = abs(rsym(rotate(p, PI/6.), 6.)) - vec2(.25, 0);
    float cin  = shape_circle(p11, .075);

    vec2 p12 = abs(rsym(p, 6.)) - vec2(.4, 0);
    float cout = shape_circle(p12, .075);

    return sub(add(add(cmid, cin), cout), -cbig-.03);
}

float shape_revolver_barrel(vec2 p) {
    p.x = abs(p.x);
    float rect1 = shape_rect(p - vec2(0, .45), vec2(.04, .06)) - .01;
    float rect2 = shape_rect(p - vec2(.08, .42), vec2(.04, .04)) - .01;
	float circle = abs(shape_circle(p - vec2(0, 0.25), 0.15)) - .025;
    return add(sub(add(rect1, rect2), circle), circle);
}

float shape_revolver_frame(vec2 p) {
    float rect = shape_rect(p+vec2(0, .25), vec2(.06, .2));
    float circle = shape_circle(p, .1);
	return add(rect, circle) - .025;
}

float shape_revolver_trigger(vec2 p) {
    float rect1 = shape_rect(p+vec2(0, .55), vec2(.06, .05));
    float trap = shape_trapezoid(p+vec2(0, .7), .03, .06, .08);
    return add(trap, rect1) - .025;
}

float SAMPLER(vec2 p) {
    float scale = .8;
    p.y -= .25;

    p /= scale;

    vec2 p1, p2;
    float time = mod(iTime/PI, 2.);
    if (time > 1.){
        p1 = rotate(p + vec2(0, .4), abs(sin(iTime))*PI*.25) - vec2(0, .4);
        p2 = rotate(p1, iTime);
    }else{
        if (time > .11 && time < .2 && length(p - vec2(0, .25)) < pow(time, 2.)*120.) return 0.;
        p.y -= (time > .1)? exp(-time*5.)*.75 : 0.;
        p1 = p;
        p2 = p;
    }

    float cylinder_back = shape_circle(p, .425);
    float cylinder_cut = shape_revolver_cylinder_cut(p2);
    float cylinder_fill = shape_revolver_cylinder_fill(p2);
    float base = shape_revolver_base(p);
    float s1 = sub(add(add(cylinder_fill, cylinder_back), base), cylinder_cut);

    float trigger = shape_revolver_trigger(p);
    float s2 = add(sub(s1, trigger - .025), trigger);

    float frame_still = shape_revolver_frame(p);
    float frame_move = shape_revolver_frame(p1/.9)*.9;
    float s3 = add(sub(s2, frame_move - .025), frame_move);
    float s4 = add(sub(s3, frame_still - .025), frame_still);


    float barrel = shape_revolver_barrel(p);
    float s5 = add(sub(s4, barrel - .025), barrel);


    return s5*scale;
}


vec3 draw_line(float d, float thickness) {
    const float aa = 3.0;
    return vec3(smoothstep(0.0, aa / iResolution.y, max(0.0, abs(d) - thickness)));
}

vec3 draw_line(float d) {
    return draw_line(d, 0.0025);
}

float draw_solid(float d) {
    return smoothstep(0.0, 3.0 / iResolution.y, max(0.0, d));
}

vec3 draw_polarity(float d, vec2 p) {
    p += iTime * -0.1 * sign(d) * vec2(0, 1);
    p = mod(p + 0.06125, 0.125) - 0.06125;
    float s = sign(d) * 0.5 + 0.5;
    float base = draw_solid(d);
    float neg = shape_rect(p, vec2(0.045, 0.0085) * 0.5);
    float pos = shape_rect(p, vec2(0.0085, 0.045) * 0.5);
    pos = min(pos, neg);
    float pol = mix(neg, pos, s);

    float amp = abs(base - draw_solid(pol)) - 0.9 * s;

    return vec3(1.0 - amp);
}

vec3 draw_distance(float d, vec2 p) {
    float t = clamp(d * 0.85, 0.0, 1.0);
    vec3 grad = mix(vec3(1, 0.8, 0.5), vec3(0.3, 0.8, 1), t);

    float d0 = abs(1.0 - draw_line(mod(d + 0.1, 0.2) - 0.1).x);
    float d1 = abs(1.0 - draw_line(mod(d + 0.025, 0.05) - 0.025).x);
    float d2 = abs(1.0 - draw_line(d).x);
    vec3 rim = vec3(max(d2 * 0.85, max(d0 * 0.25, d1 * 0.06125)));

    grad -= rim;
    grad -= mix(vec3(0.05, 0.35, 0.35), vec3(0.0), draw_solid(d));

    return grad;
}

vec3 draw_trace(float d, vec2 p, vec2 ro, vec2 rd) {
    vec3 col = vec3(0);
    vec3 line = vec3(1, 1, 1);
    vec2 _ro = ro;

    for (int i = 0; i < TRACE_STEPS; i++) {
        float t = SAMPLER(ro);
        col += 0.8 * line * (1.0 - draw_line(length(p.xy - ro) - abs(t), 0.));
        col += 0.2 * line * (1.0 - draw_solid(length(p.xy - ro) - abs(t) + 0.02));
        col += line * (1.0 - draw_solid(length(p.xy - ro) - 0.015));
        ro += rd * t;
        if (t < 0.01) break;
    }

    #ifdef TRACE_RAY
    col += 1.0 - line * draw_line(shape_segment(p, _ro, ro), 0.);
    #endif

    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float t = iTime * 0.5;
    vec2 uv = squareFrame(iResolution.xy, fragCoord);
    float d;
    vec3 col;
    vec2 ro = vec2(iMouse.xy / iResolution.xy) * 2.0 - 1.0;
    ro.x *= squareFrame(iResolution.xy, iResolution.xy).x;

    vec2 rd = normalize(-ro);

    d = SAMPLER(uv);

    #if DISPLAY == 0
    col = vec3(draw_distance(d, uv.xy));
    #if MOUSE == 0
    col -= (iMouse.z > 0.0 ? 1.0 : 0.0) * vec3(draw_trace(d, uv.xy, ro, rd));
    #endif
    #endif
    #if DISPLAY == 1
    col = vec3(0) + 1.0 - vec3(draw_line(d));
    #if MOUSE == 0
    col += (iMouse.z > 0.0 ? 1.0 : 0.0) * vec3(1, 0.25, 0) * vec3(draw_trace(d, uv.xy, ro, rd));
    #endif
    col = 1. - col;
    #endif
    #if DISPLAY == 2
    col = vec3(draw_solid(d));
    #endif
    #if DISPLAY == 3
    col = vec3(draw_polarity(d, uv.xy));
    #endif

    fragColor.rgb = col;
    fragColor.a   = 1.0;
}

