#define AA  1   // optional antialiasing

float PrBoxDf (vec3 p, vec3 b);
float PrBox2Df (vec2 p, vec2 b);
float PrCylDf (vec3 p, float r, float h);
float Minv3 (vec3 p);
float Maxv3 (vec3 p);
float SmoothMin (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);
float Noisefv2 (vec2 p);
float Fbm1 (float p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 bSize, sunDir, vnBlk;
vec2 blbRad;
float tCur, dstFar;
int idObj;
bool isSh;
const float pi = 3.14159;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

vec3 SMap (vec3 p, float t)
{
  float f;
  f = 2.;
  for (int k = 0; k < 5; k ++) {
    p += 0.4 * sin (1.7 * p.yzx / f + f * t);
    f *= 0.8;
  }
  return p;
}

float BlobDf (vec3 p)
{
  vec3 q;
  float d, t;
  t = tCur + 1.31;
  q = p;
  q.xz = Rot2D (q.xz, 0.2 * t);
  d = SmoothMin (PrBoxDf (SMap (q - vec3 (0.7, -0.3, 0.), t + 2.), vec3 (blbRad.x)),
     PrBoxDf (SMap (q - vec3 (-0.7, -0.3, 0.), 1.3 * t), vec3 (blbRad.y)), 0.2);
  q = p;
  q.y -= -2.6;
  d = SmoothMin (d, PrCylDf (q.xzy, 2.7, 0.2), 0.5);
  return d * (isSh ? 1. : 0.25);
}

float ObjDf (vec3 p)
{
  vec3 q;
  vec2 cs, b, s;
  float dMin, d, g, w;
  dMin = dstFar;
  cs = sin (pi / 8. + vec2 (0., 0.5 * pi));
  w = 0.06;
  q = p;
  q.y -= 7.3;
  g = dot (vec2 (abs (q.x), q.y), cs);
  d = min (max (min (abs (g) - w, 0.), - q.y - 1.5),
     max (min (abs (q.y + 1.525) - w, 0.), g - w));
  q.y -= -0.25;
  g = dot (vec2 (abs (q.x), q.y), cs);
  d = min (d, min (max (min (abs (g) - w, 0.), - q.y - 1.),
     max (min (abs (q.y + 1.025) - w, 0.), g - w)));
  b = vec2 (4., 2.5);
  q = p;
  q.y -= 3.;
  s = abs (q.xy) - b;
  d = min (d, min (max  (min (s.y - w, 0.), min (abs (s.x) - w, 0.)),
     max  (min (s.x - w, 0.), min (abs (s.y) - w, 0.))));
  q = p;
  q.yz -= vec2 (4., bSize.z);
  d = max (PrBoxDf (q - vec3 (0., 0., -0.2), bSize), - d);
  q.y -= -1.;
  d = max (d, - PrBox2Df (q.xy, b - 3. * w));
  q.y -= -2.3;
  d = max (d, - PrCylDf (q.xzy, 2.7, 0.1));
  DMIN (1);
  q = p;
  q.y -= 4.;
  d = PrBoxDf (q, vec3 (bSize.xy - 0.05, 0.1));
  q.y -= -1.;
  d = max (d, - PrBox2Df (q.xy, b - 3. * w + 0.05));
  DMIN (2);
  q = p;
  q.y -= 3.;
  g = abs (q.x) - b.x;
  q.xz = vec2 (mod (q.x + 0.75, 1.5) - 0.75, abs (q.z - bSize.z) - bSize.z + 0.3);
  d = PrCylDf (q.xzy, 0.2, b.y - 4. * w);
  q.y = abs (q.y) - b.y + 4. * w;
  d = max (min (d, PrCylDf (q.xzy, 0.275, w)), g);
  DMIN (3);  
  q = p;
  q.yz -= vec2 (3., bSize.z);
  if (! isSh) d = PrBoxDf (q, vec3 (b - 3. * w, bSize.z)) + 0.1;
  if (isSh || d < dMin) {
    d = BlobDf (q);
    DMIN (4);
  } else dMin = min (dMin, d);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 180; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.002, -0.002);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = 0; j < 40; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += max (0.02, h);
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

float BlkHit (vec3 ro, vec3 rd, vec3 sz)
{
  vec3 v, tm, tp;
  float dMin, dn, df;
  dMin = dstFar;
  v = ro / rd;
  tp = sz / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  if (df > 0. && dn < df) {
    dMin = dn;
    vnBlk = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  }
  return dMin;
}

float RippleHt (vec2 p)
{
  vec2 q;
  float s1, s2;
  q = Rot2D (p, -0.02 * pi);
  s1 = abs (sin (4. * pi * abs (q.y + 1.5 * Fbm2 (0.7 * q))));
  s1 = (1. - s1) * (s1 + sqrt (1. - s1 * s1));
  q = Rot2D (p, 0.01 * pi);
  s2 = abs (sin (3.1 * pi * abs (q.y + 1.9 * Fbm2 (0.5 * q))));
  s2 = (1. - s2) * (s2 + sqrt (1. - s2 * s2));
  return mix (s1, s2, 0.1 + 0.8 * smoothstep (0.3, 0.7, Fbm2 (2. * p)));
}

vec3 RippleNorm (vec2 p, vec3 vn, float f)
{
  vec2 e;
  float h;
  e = vec2 (0.002, 0.);
  h = RippleHt (p);
  vn.xy = Rot2D (vn.xy, f * (RippleHt (p + e) - h));
  vn.zy = Rot2D (vn.zy, f * (RippleHt (p + e.yx) - h));
  return vn;
}

vec3 SkyBgCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol;
  vec2 q;
  float f, fd, ff;
  if (rd.y > -0.02 && rd.y < 0.03 * Fbm1 (16. * atan (rd.z, - rd.x))) {
    col = vec3 (0.3, 0.41, 0.55);
  } else {
    q = 0.02 * (ro.xz + 0.5 * tCur + ((100. - ro.y) / rd.y) * rd.xz);
    ff = Fbm2 (q);
    f = smoothstep (0.2, 0.8, ff);
    fd = smoothstep (0.2, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
    clCol = (0.7 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
       smoothstep (0., 0.05, abs (fd)));
    col = mix (vec3 (0.4, 0.5, 0.8), clCol, 0.1 + 0.9 * f * smoothstep (0.01, 0.1, rd.y));
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, roo;
  vec2 vf, s;
  float dstObj, dstGrnd, db, sh, f;
  bool isBg;
  isBg = true;
  sh = 1.;
  roo = ro;
  bSize = vec3 (7., 4., 3.75);
  db = BlkHit (ro + vec3 (0., - bSize.y, 0.1), rd, vec3 (bSize.xy, 0.05));
  if (db < dstFar) {
    vf = vec2 (0.);
    if (vnBlk.z < -0.99) {
      blbRad = vec2 (1.1 + 0.31 * sin (tCur + 1.31), 1. + 0.41 * sin (1.7 * (tCur + 1.31)));
      isSh = false;
      dstObj = ObjRay (ro, rd);
      if (dstObj < dstFar) {
        ro += dstObj * rd;
        vn = ObjNf (ro);
        if (idObj == 1) {
          if (vn.y > 0.99 && length (ro.xz- vec2 (0., bSize.z)) < 2.7) {
            col4 = vec4 (0.6, 0.7, 0.1, 0.2);
          } else {
            col4 = vec4 (0.4, 0.25, 0.15, 0.);
            if (vn.z < -0.99) vf = vec2 (8., 2.);
            else vf = vec2 (16., 1.);
          }
        } else if (idObj == 2) {
          col4 = vec4 (0.7, 0.5, 0.2, 0.3);
        } else if (idObj == 3) {
          col4 = vec4 (0.6, 0.4, 0.2, 0.3);
          s = vec2 (mod (ro.x + 0.75, 1.5) - 0.75, abs (ro.z - bSize.z) - bSize.z + 0.3);
          vn.xz = Rot2D (vn.xz, -0.15 * pi * sin (pi * (0.5 -
             mod (16. * (atan (s.x, s.y) / (2. * pi) + 0.5), 1.))));
        } else if (idObj == 4) {
          col4 = vec4 (mix (vec3 (0.6, 0.7, 0.1), vec3 (0.9, 0.2, 0.1),
             smoothstep (0.7, 4.5, ro.y)), 0.3);
        }
        if (vf.x > 0.) vn = VaryNf (vf.x * ro, vn, vf.y);
        isSh = true;
        isBg = false;
      } else if (rd.y < 0.) {
        dstObj = - ro.y / rd.y;
        if (dstObj < dstFar) {
          ro += dstObj * rd;
          isSh = true;
        }
      }
      sh = (isSh) ? ObjSShadow (ro + 0.01 * sunDir, sunDir) : 1.;
      if (! isBg) col = col4.rgb * (0.2 + 0.8 * sh * max (dot (vn, sunDir), 0.)) +
         col4.a * step (0.95, sh) * pow (max (dot (normalize (sunDir - rd), vn), 0.), 32.);
    } else {
      ro += db * rd;
      vn = vnBlk;
      vn.z *= -1.;
      if (vn.z > -0.99) {
        col4 = vec4 (0.4, 0.25, 0.15, 0.);
        vf = vec2 (32., 2.);
      } else {
        f = 0.;
        if (abs (ro.y - bSize.y) < bSize.y - 0.5) f = abs (ro.x);
        if (abs (ro.x + 0.5 * bSize.x) < 0.5 * bSize.x - 1.) 
           f = abs (abs (ro.y - 0.7 * bSize.y) - 0.5);
        s = abs (ro.xy - bSize.xy * vec2 (-0.8, 1.6)) - 0.7;
        if (max (s.x, s.y) < 0.02) f = min (abs (s.x), abs (s.y));
        col4 = vec4 (0.7, 0.7, 0.65, 0.1);
        if (f > 0.) col4 *= 0.5 + 0.5 * smoothstep (0.01, 0.02, f);
        vf = vec2 (64., 0.5);
      }
      vn = VaryNf (vf.x * ro, vn, vf.y);
      col = col4.rgb * (0.2 + 0.8 * max (dot (vn, sunDir), 0.)) +
         col4.a * pow (max (dot (normalize (sunDir - rd), vn), 0.), 32.);
      isBg = false;
    }
  }
  if (isBg) {
    if (rd.y < 0.) {
      ro = roo;
      dstGrnd = - ro.y / rd.y;
      ro += dstGrnd * rd;
      vn = vec3 (0., 1., 0.);
      f = 1. - smoothstep (0.3, 0.5, dstGrnd / dstFar);
      if (f > 0.) vn = VaryNf (4. * ro, RippleNorm (ro.xz, vn, 6. * f), f);
      col = mix (vec3 (1., 0.8, 0.5), vec3 (0.9, 0.7, 0.5), 0.2 +
         0.6 * smoothstep (0.7, 1.1, 2. * Fbm2 (2. * ro.xz)));
      col = col * (0.2 + 0.8 * sh * max (dot (vn, sunDir), 0.));
      col = mix (col, vec3 (0.3, 0.41, 0.55), pow (1. + rd.y, 16.));
      col *= 0.7 + 0.3 * smoothstep (length (vec2 (max (abs (ro.x) - bSize.x, 0.),
         max (abs (ro.z + 0.1) - 0.05, 0.))), 0., 0.05);
    } else col = SkyBgCol (ro, rd);
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, ori, ca, sa;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.015 * pi;
  if (mPtr.z > 0.) {
    az -= 2. * pi * mPtr.x;
    el -= pi * mPtr.y;
  }
  az += 0.7 * pi * (2. * mod (floor (0.06 * tCur), 2.) - 1.) *
     SmoothBump (0.3, 0.7, 0.15, mod (0.06 * tCur, 1.));
  el = clamp (el, -0.4 * pi, 0.02 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 3.5, -25.);
  zmFac = 4.5;
  dstFar = 100.;
  sunDir = normalize (vec3 (0., 0., -1.));
  sunDir.xz = Rot2D (sunDir.xz, 0.2 * pi * sin (0.05 * 2. * pi * tCur));
  sunDir.yz = Rot2D (sunDir.yz, pi * (0.22 + 0.07 * sin (0.03 * 2. * pi * tCur)));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 3; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e;
  e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

#define S(a, b, t) smoothstep(a, b, t)

float dLine(vec2 p, vec2 a, vec2 b)
{
    vec2 pa = p-a;
    vec2 ba = b-a;
    float h = clamp(dot(pa,ba)/dot(ba,ba), 0.0, 1.0);
    return length(pa - ba*h);
}

float line(vec2 p, vec2 a, vec2 b)
{
    float d = dLine(p, a, b);
    float m = S(.03, .01, d);
    return m;
}

float dCross(vec2 p, vec2 b)
{
    const vec2 r = vec2(1.0, 0.0);
    const vec2 u = vec2(0.0, 1.0);
    vec2 bu = b * u;
    vec2 br = b * r;

    float m = line(p, -br, br);
    m += line(p, -bu, bu);
    return m;
}

float dCircle(vec2 p, float r)
{
    return length(p) - r;
}

float dBox(vec2 p, vec2 b)
{
    vec2 d = abs(p)-b;
    return length(max(d,vec2(0))) + min(max(d.x,d.y),0.0);
}

float N21(vec2 p)
{
    p = fract(p * vec2(233.34, 851.734));
    p += dot(p, p + 23.45);
    return fract(p.x * p.y);
}

vec2 N22(vec2 p)
{
    float n = N21(p);
    return vec2(n, N21(p+n));
}

vec2 GetPos(vec2 id)
{
    vec2 n = N22(id) * iTime;
    return sin(n)*.15;
}

float CrossLayer(vec2 uv)
{
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);
    float m = 0.;

    float t = iTime * 0.25;
    vec2 crossSize = vec2(0.125);
    crossSize *= sin(t) * 0.5 + 1.5;

    m += dCross(gv+GetPos(id), crossSize);
    m = min(m, -dBox(uv, vec2(6.0)));

    m = S(0.002, 0.0, m);
    m = 1.0 - m;

    return m;
}

float CircleLayer(vec2 uv)
{
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);
    float m = 0.;

    float t = iTime * 0.25;
    float r = 0.125;
    r *= sin(t) * 0.5 + 1.5;

    m += abs(dCircle(gv+GetPos(id), r)) - 0.025;
    m = max(m, dBox(uv, vec2(24.0)));

    m = S(0.002, 0.0, m);

    return m;
}

float SquareLayer(vec2 uv)
{
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);
    float m = 0.;

    float t = iTime * 0.25;
    float r = 0.125;
    r *= sin(t) * 0.5 + 1.5;

    m += abs(dBox(gv+GetPos(id), vec2(r))) - 0.025;
    m = max(m, dBox(uv, vec2(24.0)));

    m = S(0.002, 0.0, m);

    return m;
}

vec3 Background(vec2 p)
{
    return vec3(0.5, 0.5, sin(iTime * 0.5) * 0.1 + 0.5) * (0.7 - p.y * p.x);
}

mat2 GetRot(float t)
{
    float s = sin(t);
    float c = cos(t);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 bg = Background(uv);

    float m = 0.0;

	vec3 color1 = vec3(1., 1., 0.);
	vec3 color2 = vec3(1., 0., 1.);
	vec3 color3 = vec3(0., .6, 1.);


    // Cross layers

    const int numCross = 3;

    float speeds[numCross];
    speeds[0] = 0.2;
    speeds[1] = 0.2;
    speeds[2] = 0.2;

    float scales[numCross];
    scales[0] = 3.0;
    scales[1] = 5.0;
    scales[2] = 9.0;

    vec3 colors[numCross];
    colors[0] = color1;
    colors[1] = color2;
    colors[2] = color3;

    vec2 positions[numCross];
    positions[0] = vec2(0.0, 0.0);
    positions[1] = vec2(0.25, 0.1);
    positions[2] = vec2(-0.3, -0.2);

    float t = 0.0;
    int i = 0;
    float z = 0.0;
    float fade = 0.0;
    for (i = 0; i < numCross; ++i)
    {
        t = iTime * speeds[i];

        z = fract(float(i) * 0.1 + t);
        fade = S(0.0, 0.5, z) * S(1.0, 0.8, z);

        uv *= GetRot(t);
        uv += positions[i];
        m += CrossLayer(uv * scales[i]) * fade;
        if (fragColor.rgb == vec3(0.0))
            fragColor.rgb += m * colors[i];
    }

    uv -= vec2(-0.05, -0.1);
    t = iTime * 0.05;
    z = fract(float(i) * 0.1 +t);
    fade = S(0.0, 0.5, z) * S(1.0, 0.8, z);
    uv *= GetRot(t);
    m += CircleLayer(uv * 20.0) * fade;
    if (fragColor.rgb == vec3(0.0))
        fragColor.rgb += m * vec3(1.0);

    t = iTime * 0.2;
    z = fract(float(i) * 0.1 +t);
    fade = S(0.0, 0.5, z) * S(1.0, 0.8, z);
    t = iTime * 0.5;
    uv *= GetRot(-t);
    m += SquareLayer(uv * 16.0) * fade;
    if (fragColor.rgb == vec3(0.0))
        fragColor.rgb += m * vec3(1.0, 1.0, 1.0);

    // BG
    if (fragColor.rgb == vec3(0.0))
        fragColor.rgb += bg;

    fragColor.rgb = sqrt(fragColor.rgb);

    //imageStore(imgOut, ivec2(gl_GlobalInvocationID.xy), col);
}

