void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // GPU usage in GTX1070: 5=20% 20=35% 40=65% 50=100%
    const int HOTPOWER = 2;


    vec2 uv = fragCoord/iResolution.xy;

    float tmp;
    float arr[HOTPOWER];
    float size = float(HOTPOWER);

    for(int i=0; i<HOTPOWER; i++){
        vec2 seed = vec2(uv.x*float(i)*iTime, uv.y*float(i));
        arr[i] = rand(seed);
    }

    for (int i=0; i<HOTPOWER; ++i) {
		for (int j=i+1; j<HOTPOWER; ++j) {
        	if (arr[i] > arr[j]) {
            	tmp =  arr[i];
            	arr[i] = arr[j];
            	arr[j] = tmp;
          	}
        }
    }

	int idxr = int(mod(floor(uv.x*size*10.+iTime), float(size)));
    int idxg = int(mod(floor(uv.y*size*10.+iTime), float(size)));
    vec3 col = vec3(arr[idxr],arr[idxg],arr[HOTPOWER-1]);

    // Output to screen
    fragColor = vec4(col,1.0);
}

