float snoise(vec2 v)
  {
  const vec4 C = vec4(0.211324865405187,  // (3.0-sqrt(3.0))/6.0
                      0.366025403784439,  // 0.5*(sqrt(3.0)-1.0)
                     -0.577350269189626,  // -1.0 + 2.0 * C.x
                      0.024390243902439); // 1.0 / 41.0
// First corner
  vec2 i  = floor(v + dot(v, C.yy) );
  vec2 x0 = v -   i + dot(i, C.xx);

// Other corners
  vec2 i1;
  //i1.x = step( x0.y, x0.x ); // x0.x > x0.y ? 1.0 : 0.0
  //i1.y = 1.0 - i1.x;
  i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  // x0 = x0 - 0.0 + 0.0 * C.xx ;
  // x1 = x0 - i1 + 1.0 * C.xx ;
  // x2 = x0 - 1.0 + 2.0 * C.xx ;
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;

// Permutations
  i = mod289(i); // Avoid truncation effects in permutation
  vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
		+ i.x + vec3(0.0, i1.x, 1.0 ));

  vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy), dot(x12.zw,x12.zw)), 0.0);
  m = m*m ;
  m = m*m ;

// Gradients: 41 points uniformly over a line, mapped onto a diamond.
// The ring size 17*17 = 289 is close to a multiple of 41 (41*7 = 287)

  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;

// Normalise gradients implicitly by scaling m
// Approximation of: m *= inversesqrt( a0*a0 + h*h );
  m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );

// Compute final noise value at P
  vec3 g;
  g.x  = a0.x  * x0.x  + h.x  * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}
float pi = 3.14159;
float omega = .5f;
vec4 pt4( float x, float y, float dx, float dy, float xyOffset ) {
    float s = x;
    float t = y;

    float nx = xyOffset + cos(s * omega * pi) * dx / (omega * pi);
    float ny = -xyOffset + cos(t * omega * pi) * dy / (omega * pi);
    float nz = xyOffset + sin(s * omega * pi) * dx / (omega * pi);
    float nw = -xyOffset + sin(t * omega * pi) * dy / (omega * pi);

    return vec4(nx, ny, nz, nw);
}

float noise(vec2 p) {
    return   0.5333333*(abs(snoise(p)))
			+0.2666667*(abs(snoise(2.*p)))
			+0.1333333*(abs(snoise(4.*p)))
			+0.0666667*(abs(snoise(8.*p)));
}

float fbm(vec2 p, float sx, float sy) {
    return   sin(sx * p.x + sy * p.y + 0.5333333*abs(snoise(p))
			+0.2666667*abs(snoise(2.*p))
			+0.1333333*abs(snoise(4.*p))
			+0.0666667*abs(snoise(8.*p)));
}

float fbm2(vec2 p, float sx, float sy, float offset) {
    return   sin(sx * p.x + floor((p.y * sy / 2. / pi - offset)) * pi + 0.5333333*abs(snoise(p))
			+0.2666667*abs(snoise(2.*p))
			+0.1333333*abs(snoise(4.*p))
			+0.0666667*abs(snoise(8.*p)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float sx = 1.;
    float sy = 2.;
    float offset = 0.2;
    vec3 brick_color_base = vec3(147, 66, 34) / 256.;
    vec3 brick_shadow_1 = vec3(129, 60, 15) / 256.;
    vec3 brick_shadow_2 = vec3(107, 61, 20) / 256.;
    vec3 shadow_color = vec3(38, 6, 6) / 256.;

    vec2 p = fragCoord.xy/iResolution.y; 

    p.x += iTime * .2;

    float intensity1 = pow(clamp(fbm(p*20., 0., sy), 0., 1.) + .03, 40.);

    float intensity2 = pow(clamp(fbm2(p*20., sx, sy, offset), 0., 1.) + .03, 40.);

    float intensity = clamp(intensity1 + intensity2, 0., 1.);

    float c = pow(1.1 - noise(p * vec2(5., 20.) + 707.), 10.);
    float c2 = pow(1.1 - noise(p * vec2(5., 20.) + 34.), 10.);

    vec3 brick = mix(brick_color_base, brick_shadow_1, c);

    brick = mix(brick, brick_shadow_2, c2);

    brick = mix(brick, shadow_color, intensity);

    fragColor.xyz = brick;
    //fragColor.xyz = brick_color_1;
}

#define FDIST 0.5

//size of the windows
#define STRIDE 0.25

//radius of the sphere
#define RADIUS 1.

//depth of the holes
#define DEPTH 0.125

//offset of the holes
//#define OFFSET -0.125


// Raytrace sphere
float sphere(in vec3 ro, in vec3 rd, in float r, out vec3 ni) {
	float pd = dot(ro, rd);
    float disc = pd*pd + r*r - dot(ro, ro);
    if (disc < 0.) return -1.;
    float tdiff = sqrt(disc);
    float tin = -pd - tdiff;
    ni = normalize(ro + tin * rd);

    return tin;
}


// Raytrace box, returns (t_in, t_out)
vec2 box(in vec3 ro, in vec3 rd, in vec3 r, out vec3 no) {
    vec3 dr = 1.0/rd;
    vec3 n = ro * dr;
    vec3 k = r * abs(dr);

    vec3 pout =  k - n;
    vec3 pin =  - k - n;
    float tout = min(pout.x, min(pout.y, pout.z));
    float tin = max(pin.x, max(pin.y, pin.z));
    if (tin > tout) {
        return vec2(-1.);
    }
    no = -sign(rd) * step(pout.xyz, pout.zxy) * step(pout.xyz, pout.yzx);
    return vec2(tin, tout);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.x;
	float ang = iTime * 0.5;
    vec3 eye = 1.5*vec3(cos(ang), sin(ang), 1.);
    vec3 w = -normalize(eye);
    vec3 u = normalize(cross(w, vec3(0., 0., 1.)));
    vec3 v = cross(u, w);
    vec3 rd = normalize(u*uv.x + v*uv.y + FDIST * w);

    vec3 n;
    float t = sphere(eye, rd, RADIUS, n);
    vec3 col;

    if (t > 0.) {

        //get offsets and cell IDs and their centroids/normals
        vec3 ro = eye + t * rd;
        vec3 offset = vec3(iMouse.x/iResolution.x, iTime*0.3, iMouse.y/iResolution.y);
        ro += offset;
        vec3 mask = 1.-step(0.9,abs(fract(ro/STRIDE)-0.5)*2.);
        vec3 modro = fract(ro/STRIDE);
        vec3 id = floor(ro/STRIDE);
        ro -= offset;
        vec3 cc = (id+0.5)*STRIDE - offset;
        float OFFSET = length(cc) - RADIUS;
        vec3 nn = normalize(cc);
        vec3 sundir = normalize(vec3(1., 1., 1));

        // transform to tangent space for raytracing cavities
        vec3 uu = normalize(cross(vec3(0., 0., 1.), nn));
        vec3 vv = cross(nn, uu);
        mat3 rot = mat3(uu, vv, nn);
        mat3 rott = transpose(rot);
        vec3 rd_inner = rott * rd;
        vec3 ro_inner = rott * (ro-cc) + vec3(0., 0., OFFSET);
        vec3 n_inner;
        vec3 dims = vec3(vec2(0.3*STRIDE), DEPTH);
        vec2 t_inner = box(ro_inner, rd_inner, dims, n_inner);
        float fac = 1.;
        if (t_inner.y > 0. && t_inner.x < 0. && mask.x*mask.y*mask.z > 0.5) {
            // determine shadows
            vec3 sundir_inner = rott * sundir;
            vec3 n_shadow;
            vec3 ro_shadow = ro_inner + t_inner.y * rd_inner;
			vec2 t_shadow = box(ro_shadow, sundir_inner, dims, n_shadow);
            // where the shadow ray hits the cavity in world space; if it's outside the hull, no shadow
            vec3 ro_light = cc + rot * (ro_shadow - vec3(0., 0., OFFSET) + t_shadow.y * sundir_inner);
            fac = step(RADIUS, length(ro_light));
            n = rot * n_inner;
        }
        // shade
        fac *= max(0., dot(n, sundir));
        float fac2= max(0., dot(n, -sundir));
        col = fac * vec3(1., 1., 0.95) + fac2 * vec3(0.1, 0.1, 0.2);
    } else {
        col = vec3(0.);
    }
    fragColor = vec4(col,1.0);
}

#define FDIST 0.7
#define PI 3.1415926


#define GROUNDSPACING 0.5
#define GROUNDGRID 0.05
#define BOXDIMS vec3(0.6, 0.6, 1.)

#define ABSORPTION_RATE vec3(0.7, 0.5, 0.4)
#define IOR 1.33
#define SCATTER_FACTOR 0.02
#define SAMPLES 10
#define REFLECTIONS 4


vec2 rand2d(in vec2 uv) {
    return fract(mat2(-199.258, 457.1819, -1111.1895, 2244.185)*sin(mat2(111.415, -184, -2051, 505)*uv));
}

vec2 box(in vec3 ro, in vec3 rd, in vec3 r, out vec3 ni, out vec3 no) {
    vec3 dr = 1.0/rd;
    vec3 n = ro * dr;
    vec3 k = r * abs(dr);

    vec3 pin = - k - n;
    vec3 pout =  k - n;
    float tin = max(pin.x, max(pin.y, pin.z));
    float tout = min(pout.x, min(pout.y, pout.z));
    if (tin > tout) return vec2(-1.);
    ni = -sign(rd) * step(pin.zxy, pin.xyz) * step(pin.yzx, pin.xyz);
    no = sign(rd) * step(pout.xyz, pout.zxy) * step(pout.xyz, pout.yzx);
    return vec2(tin, tout);
}

vec2 sphere(in vec3 ro, in vec3 rd, in float r, out vec3 ni) {
	float pd = dot(ro, rd);
    float disc = pd*pd + r*r - dot(ro, ro);
    if (disc < 0.) return vec2(-1.);
    float tdiff = sqrt(disc);
    float tin = -pd - tdiff;
    float tout = -pd + tdiff;
    ni = normalize(ro + tin * rd);

    return vec2(tin, tout);
}

vec3 bgcol(in vec3 rd) {
    return mix(vec3(0., 0., 1.), vec3(0.6, 0.8, 1.), 1.-pow(abs(rd.z), 2.));
}

//raytrace the exterior surroundings
vec4 background(in vec3 ro, in vec3 rd) {
    float t = (-1. - ro.z)/rd.z;
    vec3 col1;
    if (t < 0.) {
        t = 1000.;
        col1 = bgcol(rd);
    } else {
        vec2 uv = ro.xy+t*rd.xy;
        if (max(abs(uv.x), abs(uv.y)) > 8.) col1 = bgcol(rd);
        else {
            vec2 checkers = smoothstep(vec2(GROUNDGRID*0.75), vec2(GROUNDGRID), abs(mod(uv, vec2(GROUNDSPACING))*2.-GROUNDSPACING));
            float aofac = smoothstep(-0.7, .4, length(abs(uv)-min(abs(uv), vec2(0.75))));
            col1 = mix(vec3(0.2), vec3(0.8), min(checkers.x,checkers.y)) * aofac;
        }
    }

    vec3 ni;
    vec2 t2 = sphere(ro - vec3(BOXDIMS.x + 0.6, 0., -0.4), rd, 0.6, ni);
    float tea = mix(t, t2.x, step(0., t2.x));
    return vec4(mix(col1, mix(vec3(1.), vec3(1., 1., 0.), (t2.x * rd + ro).z+0.5), step(0., t2.x)), tea);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.x;
    float mouseY = iMouse.y < 1. ? 0.5 : (1.0-1.15*iMouse.y/iResolution.y) * 0.5 * PI;
    float mouseX = iMouse.x < 1. ? iTime*0.25 : -(iMouse.x/iResolution.x) * 2. * PI;
    vec3 eye = 4.*vec3(cos(mouseX) * cos(mouseY), sin(mouseX) * cos(mouseY), sin(mouseY));
    vec3 w = normalize(-eye);
    vec3 up = vec3(0., 0., 1.);
    vec3 u = normalize(cross(w, up));
    vec3 v = cross(u, w);

    vec3 rd = normalize(w * FDIST + uv.x * u + uv.y * v);

    vec3 ni, no;
    vec2 t = box(eye, rd, BOXDIMS, ni, no);
    vec3 ro = eye + t.x * rd;
    vec2 coords = ro.xy * ni.z + ro.yz * ni.x + ro.zx * ni.y;

    vec4 bgg = background(eye, rd);

    if (t.x > 0. && t.x < bgg.w) {
        vec3 col = vec3(0.);
        float R0 = (IOR-1.)/(IOR+1.);
        R0*=R0;
        for (int i=0; i<SAMPLES; i++) {

            vec2 theta = rand2d(coords + float(i) * vec2(1., 0.) * vec2(104., -30.6));
            theta *= vec2(2.*PI, SCATTER_FACTOR*PI);
            vec3 n = vec3(cos(theta.x)*sin(theta.y), sin(theta.x)*sin(theta.y), cos(theta.y));
            // reflection
            vec3 nr = n.zxy * ni.x + n.yzx * ni.y + n.xyz * ni.z;
            vec3 rdr = reflect(rd, nr);
            vec3 reflcol = background(ro, rdr).xyz;

            // refraction, absorption and internal reflection
            vec3 rd_refr = refract(rd, nr, 1./IOR);

            vec3 insidecol = vec3(0.);
            float accum = 1.;
            vec3 transmission = vec3(1.);

            for (int j=0; j<REFLECTIONS; j++) {

                vec3 ni2, no2;
                vec2 tout = box(ro, rd_refr, BOXDIMS, ni2, no2);
                ro = ro + tout.y * rd_refr;
                vec3 rd_refr_out = refract(rd_refr, -no2, IOR);

                float fresnel2 = R0 + (1.-R0) * pow(1.-dot(rd_refr_out, no2), 5.);
                vec3 reps = ro;
                reps.z = max(reps.z, -0.999);            
            	rd_refr = reflect(rd_refr, -no2);
                transmission *= pow(ABSORPTION_RATE, vec3(tout.y));
            	insidecol += accum * (1.-fresnel2) * background(reps, rd_refr_out).xyz * transmission;
                accum *= fresnel2;
            }    

            float fresnel = R0 + (1.-R0) * pow(1.-dot(-rd, nr), 5.);
            col += mix(insidecol, reflcol, fresnel);
        }
        col /= float(SAMPLES);

        fragColor = vec4(col, 1.);
    } else {
		fragColor = vec4(bgg.xyz, 1.);
    }
}

// see also the curve cases: https://www.shadertoy.com/view/WdGXWG
//                           https://www.shadertoy.com/view/WsGXDy

// gradient noise derived from https://www.shadertoy.com/view/XdXGW8

float _z = 1.;
vec2 hash( vec2 x ) 
{
 // float s = 0.;                      // standard Perlin noise
    float s = mod(x.x+x.y,2.)*2.-1.;   // flow noise checkered rotation direction
 // float s = mod(floor(x.x)+floor(x.y),2.)*2.-1.;   // should be this but x is integer
 // float s = 1.;                      // flow noise universal rotation direction
    s *= iTime;                        // same rotation speed at all scales
 // s *= iTime/ _z;                    // rotation speed increase with small scale    
    const vec2 k = vec2( .3183099, .3678794 );
    x = x*k + k.yx + 5.;
    return ( -1. + 2.*fract( 16. * k*fract( x.x*x.y*(x.x+x.y)) ) ) 
        *  mat2(cos( s + vec4(0,33,11,0))); // rotating gradients. rot: https://www.shadertoy.com/view/XlsyWX
}

float noise( vec2 p )
{
    vec2 i = floor( p ),
         f = fract( p ),
	     u = f*f*(3.-2.*f);

#define P(x,y) dot( hash( i + vec2(x,y) ), f - vec2(x,y) )
    return mix( mix( P(0,0), P(1,0), u.x),
                mix( P(0,1), P(1,1), u.x), u.y);
}

float perlin( vec2 p )  //fractal noise
{	
    mat2 m = mat2(2.); // mat2( 1.6,  1.2, -1.2,  1.6 );
    float v  = 0.,s = 1.;
    for( int i=0; i < 4; i++, s /= 2. ) { _z = s; // for flownoise
        v += s*noise( p ); p *= m;
    }
    return v;
}

