#define S(v) smoothstep( pix, 0., v )

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( u -.5*R ) / R.y * 8., U0=U;
    O -= O;
    float pix = 12./R.y;

    O -= S(abs(U.x));

	if( U.x < 0. )          // left: noise	
		O += noise( U ) * 1.5;
    else	                // right: fractal noise
		O += perlin(U);

    O.r += S(fract(U0.x));  // grid
    O.r += S(fract(U0.y));
    O.g += S( abs(O.x) );   // isoline 0

	O = .5 + .5*O;
    O = mix(O, vec4(0,.5,0,1), S (length(fract(U0-.5)-.5)-.05) ); // grid vertices

}

//  Function from I√±igo Quiles
//  https://www.shadertoy.com/view/MsS3Wc
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix( vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float PI = 3.1415926;
    vec2 st = fragCoord.xy/iResolution.xy;
    vec3 color = vec3(0.0);

    vec2 toCenter = vec2(0.5)-st;
    float radius = length(toCenter)*2.0;
    float angle = iTime*2.0 + atan(toCenter.y,toCenter.x) + 2.0*(sin(iTime+radius));
    vec3 c = vec3((angle/(2.0*PI)),radius+0.5,1.0);
    color = hsb2rgb(c);

    float circleRadius = 0.5 *min(iResolution.x,iResolution.y);
    float dist2Center = length(fragCoord.xy - 0.5 * iResolution.xy);
    if (dist2Center < circleRadius) {
      fragColor = vec4(color, 1.0);              
    } else {
        fragColor = vec4(0.0);
    }
}

#define TWO_PI 6.2831853f

struct source{
	vec2 pos;
    float l;
    float amp;
};
// you can change evrything below
const bool decay = true;
const float scale = 200.f;
const float v = 10.f;
const source [2] sc=source[](
	source(
        vec2(-30,0),
          5.f,
          500.f), //high values due to quadratic decay
    source(
    	vec2(30,0),
        5.f,
        500.f)
        );
// no Changes past here

//calculate the overall amplitude of the wave at the position
float amp(vec2 pos,float t){
    float a = 0.f;
    for(int i = 0;i<sc.length();i++){
        source s = sc[i];
        float d = length(pos*scale-s.pos);
        float l = d/s.l;
        float f = v/s.l;
    	float ta = sin((l-f*t)*TWO_PI);
        if (decay){
            ta*= s.amp/d/d;
        }
        a+=ta;
    }
    return clamp(a,0.,1.);
}

void mainImage( out vec4 O, in vec2 u )
{
    // Normalized pixel coordinates (from -.5 to .5)
    vec2 R = iResolution.xy,
         U = ( u - .5*R ) / R.y;
    O = vec4( amp( U, iTime/2.f ) );
}

