  float baseCy1 = cylinder(p, vec3(0.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0), 12.0, 0.5, 1.0, 2.0);
  float baseDi1 = disk(p, vec3(0.0, 1.5, 0.0), vec3(0.0, 1.0, 0.0), 11.0, 1.0, 1.5);
  float base = Blend(baseCy1, baseDi1);

  float baseDi2 = disk(p, vec3(0.0, 2.5, 0.0), vec3(0.0, 1.0, 0.0), 10.0, 1.0, 1.5);
  base = Blend(base, baseDi2);

  float basePillar3 = cylinder(p, vec3(-3.0, 5.0, 0.0), vec3(0.0, 1.0, 0.0), 0.6, 2.5, 1.0, 1.0); 
  base = Blend(base, basePillar3);

  if(!simplified)
  {
 	  float basePillar1 = disk(p, vec3(3.0, 3.1, 5.5), vec3(0.0, 1.0, 0.0), 1.0, 1.0, 0.3);
 	  base = Blend(base, basePillar1);

      float basePillar2 = disk(p, vec3(3.0, 3.1, -5.5), vec3(0.0, 1.0, 0.0), 1.0, 1.0, 0.3);
      base = Blend(base, basePillar2);

      float holeTo1 = tore(p, vec3(0.0, 3.0, 0.0), vec3(0.0, 1.0, 0.0), 9.0, 1.0, 0.5);
      base = Diff(base, holeTo1);

      float holeCy1 = cylinder(p, vec3(0.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0), 10.0, 1.0, 1.0, 1.0);
      base = Diff(base, holeCy1);
  }

  return base;
}

