    float R1 = .25;
    float h1 = .07;

   	float alpha1 = bar(uv,clipAngle(a1),clipAngle(l1),R1,h1);

    //second bar
    float a2 = iTime*4.;
    float l2 = 1.5;
    float R2 = .5;
    float h2 = .14;

    float alpha2 = bar(uv,clipAngle(a2),clipAngle(l2),R2,h2);

    //third bar
    float a3 = -iTime*2.;
    float l3 = 1.;
    float R3 = 1.;
    float h3 = .32;

    float alpha3 = bar(uv,clipAngle(a3),clipAngle(l3),R3,h3);

    //center circle
    float alpha4 = rond(uv,0.14);

    vec3 col = c1*alpha1+c2*alpha2+c3*alpha3+c0*alpha4;

