float box(vec3 p, vec3 s) {
    vec3 ap=abs(p)-s;
    return length(max(vec3(0.),ap))+min(0.,max(ap.x,max(ap.y,ap.z)));
}

mat2 rot(float a) {
    float ca=cos(a);
    float sa=sin(a);
    return mat2(ca,sa,-sa,ca);
}

vec3 tunnel(vec3 p) {
    vec3 off=vec3(0);
    off.x += sin(p.z*.6)*.7;  // horizontal wiggle
    off.x += sin(p.z*.017)*1.5;
    off.y += cos(p.z*.27)*.5;  // vertical wiggle
    off.y += cos(p.z*.34)*.25;
   return off;
}

float stair(vec3 p,float s1,float s2) {
    p.z = (fract(p.z/s1-0.5)-0.5)*s1; // creates infinite line of boxes
    p.yz *= rot(PI*0.25);  // box turned on its side 
    float b = box(p,vec3(s2,s1,s1)); // was (s1) makes stair x-axis wider
    return b;
}

float map(vec3 p) {
