vec3 norm(vec3 p) {
   vec2 off = vec2(0.01,0.0);
   return normalize(map(p)-vec3(map(p-off.xyy), 
                    map(p-off.yxy),
                    map(p-off.yyx)));
}

float rnd(vec2 uv) {
   return fract(sin(dot(uv,vec2(127.478,36.874))) * 77894.6214 + 0.395);
}

float rnd(float t) {
   return fract(sin(t*435.232)*7412.223);
}

float curve(float t,float d) {
   float g=t/d;
   return mix(rnd(floor(g)),rnd(floor(g)+1.),pow(smoothstep(0.,1.,fract(g)),10.));
}

float curve2(float t,float d) {
   float g=t/d;
   float ig= floor(g);
   float fg= fract(g);
   fg = pow(fg,rnd(ig)*2.+.5);
   fg = smoothstep(0.,1.,fg);
   return (ig+fg)*d;
}

