              s.xz *= rot(ttt*0.5);  // Spins on y axis, s.xz=Copter t*=.5
           }
         }
       }  
    }
    vec3 cz=normalize(t-s);
    vec3 cx=normalize(cross(cz,vec3(0,1,0)-offt*0.2));
    vec3 cy=normalize(cross(cz,cx));
    vec3 r=normalize(cx*uv.x+cy*uv.y + cz*(.7+sin(ttt*2.)*.4));  
    // normalize(-uv,.8); .8=zoom  , cz(sin()) gives FOV Distortions
    vec3 col = vec3(0);
    vec3 back = vec3(0);
    vec3 back2 = vec3(0);

    vec3 ln = normalize(vec3(-.7,-1.,-0.5)); //s y-spin only sways, was v3(-1)
    if (cda==0) {   
       back = mix(vec3(.1,.3,1.)*.1,vec3(1.,.3,.5)*.5, 
                    pow(r.y*.5+.5,2.)); // Controls Blue to Maroon 
    } else {
//     back2 = mix(vec3(.1,.3,1.)*.1,vec3(1.,.3,.5)*.5, 
//                     1.-pow(r.y*.5+.5,2.)); // for at2 but unused 
//     back = mix(vec3(.5,.5,.5)*.1,vec3(1.,.5,.4)*.5, 
//                      pow(r.y*.5+.5,1.9)); // CDA Controls Grey to Orange 
       back = mix(vec3(1.-cos(ttt/3.),.65,.5)*.1,vec3(0.4,.5,sin(ttt/5.))*.5, 
                      pow(r.y*.5+.5,2.)); // CDA Time varying colour controls
    }
    vec3 p=s;
    float dd = 0.;
    int i=0;
    float at = 0.0;         // chase this light ?
    float at2 =0.0; 

    for (i=0;i<100;++i) {
