        if (d<0.001) {
           vec3 n = norm(p);
   //        dd = length(p-s);
           float fog = clamp(10.-dd*.018,0.,1.);  // .018 is fog strength
           float bot = pow(clamp(-(p.y-10.)*.1,0.,1.),2.)*fog;
//           fog *= clamp(-p.y+10.,0.,1.);
           float aodist=.3;                   // ambient occlusion distance
           float ao=clamp(map(p+n*aodist)/aodist,0.,1.); // ambient occlusion value
           ao=mix(ao,1.0,0.3);
           float f = pow(1.-dot(n,-r),2.);
           col += vec3(.3,.4,.7)*max(0.,dot(n,ln))*bot*ao; // abs(n)*.5; AZ debug RGB
           col += 4.*f*back*2.*(-n.y*.5+.5)*bot*ao; // blue Light, back was v3(.5,.6,1.)
//           col += mix(col,vec3(.1,.3,1.),bot); // *0.05*exp(dd*0.04);
           dd = mix(100.,d,bot);
           break;
