#define PI 3.14159
#define N 12.

// https://vogerdesign.com/collections/latest-ui-kits/products/vintage-dials-dark-set-vol-2

vec4 check(vec2 uv) {
    return vec4(vec3(0.02*mod(floor(5.0*uv.x)+floor(5.0*uv.y),2.0)), 1.);
}

float Hash(in vec2 p, in float scale) {
	return fract(sin(dot(mod(p, scale), vec2(27.16898, 38.90563))) * 5151.5473453);
}

float Noise(in vec2 p, in float scale ) {
	vec2 f;
	p *= scale;
	f = fract(p);
    p = floor(p);
    f = f*f*(3.0-2.0*f);
    return mix(mix(Hash(p, scale),
			Hash(p + vec2(1.0, 0.0), scale), f.x),
			mix(Hash(p + vec2(0.0, 1.0), scale),
			Hash(p + vec2(1.0, 1.0), scale), f.x), f.y);
}

float fBm(in vec2 p) {
	float f = 0.0;
	float scale = 10.;
    p = mod(p, scale);
	float amp   = .6;
	for (int i = 0; i < 5; i++)
	{
		f += Noise(p, scale) * amp;
		amp *= .5;
		scale *= 2.;
	}
	return min(f, 1.0);
}

vec4 over( in vec4 a, in vec4 b ) {
    return mix(a, b, 1.-a.w);
}

float nCap(vec2 uv, float angle) {
    const float r1 = 0.44;
    float a = atan(uv.y, uv.x)+angle;
    float b = a/PI*N/2.-.25;
    float f = fract(b);
    float l = length(uv);
    float d = step(f, r1);
    return (1.-d*0.1)*.5-l;
}

float line( in vec2 p, in vec2 a, in vec2 b ) {
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

