    float g = -1. + smoothstep(.75, -.75, uv.y) * 1.5;
	col = vec4(g*.02+.16);
    col = over(vec4(vec3(.0), max(smoothstep(0.01, 0.1, dCap+.08), smoothstep(0.02, 0.01, length(st)-0.5))*.8), col); // shadow
    col = over(vec4(vec3(g*.06+.04), smoothstep(0.02, 0.01, l-0.5)), col); // bottom edge
    col = over(vec4(vec3(colN*.02), smoothstep(0.02, 0.01, l-0.48)), col); // bottom fill
    col = over(vec4(vec3(colN*.1+g*.04+col1*.1), smoothstep(0.001, 0.02, sCap)), col); // shape
    col = over(vec4(vec3(1.)*(.95+g*.05), smoothstep(0.0, 0.12, .02-abs(sCap-.01))), col); // shape edge
    col = over(vec4(vec3((col0-smoothstep(0.2, 1., noise)*.03+smoothstep(0.5, 0.3, noise)*.05))+g*.03, smoothstep(0.02, 0.01, capShape)), col); // cap fill
    col = over(vec4(vec3(0.), smoothstep(0.008, 0.0, abs(capShape-.01))), col); // cap edge dark
    col = over(vec4(vec3(g*.16+.20), smoothstep(0.01, 0.0, abs(capShape))), col); // cap edge tint

	fragColor = col;
}

#define PI 3.14159265359
#define EPSILON 1e-4
#define INFTY 1e6
float wireBox(vec3 p) {
    vec3 q = abs(p) - 0.5;
    vec3 r = -1.0 * min(q, 0.0);
    float minVal = min(r.x, min(r.y, r.z));
    float maxVal = max(r.x, max(r.y, r.z));
    float midVal = min(max(r.x, r.y), min(max(r.y, r.z), max(r.z, r.x)));
    return sqrt(pow(length(max(q, 0.0)), 2.0) + pow(minVal, 2.0) + pow(midVal, 2.0));
}
float box(vec3 p) {
    vec3 q = abs(p) - 0.5;
    return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}
float oct(vec3 p) {
    return abs(p.x) + abs(p.y) + abs(p.z) - 1.0;
}
float sphere(vec3 p) {
    return length(p) - 0.5;
}
float sphereCloud(vec3 p) {
    p = abs(p);
    return length(mod(3.0 * p, 1.0) - 0.5) - 0.4 * (1.0 - max(max(p.x, p.y), p.z));
}
float surfaceSDF(vec3 p) {
    float t = abs(mod(iTime * 0.3, 6.0) - 3.0);
    float val;
    if (t < 1.0)val = mix(sphereCloud(p), box(p), clamp(t, 0.0, 1.0));
    else if (t < 2.0)val = mix(box(p), wireBox(p), clamp(t - 1.0, 0.0, 1.0));
    else val = mix(wireBox(p), oct(p), clamp(t - 2.0, 0.0, 1.0));
    return val;
}

float getAngle(vec3 p, vec3 q) {
    return acos(dot(normalize(p), normalize(q)));
}
vec3 getNormal(vec3 p) {
    float d = EPSILON;
    return normalize(vec3(
            surfaceSDF(p + vec3(d, 0.0, 0.0)) - surfaceSDF(p),
            surfaceSDF(p + vec3(0.0, d, 0.0)) - surfaceSDF(p),
            surfaceSDF(p + vec3(0.0, 0.0, d)) - surfaceSDF(p)
    ));
}

mat3 rotX(float t) {
    return mat3(
        1.0, 0.0, 0.0,
        0.0, sin(t), cos(t),
        0.0, - cos(t), sin(t)
    );
}
mat3 rotY(float t) {
    return mat3(
        sin(t), 0.0, cos(t),
        0.0, 1.0, 0.0,
        - cos(t), 0.0, sin(t)
    );
}
mat3 scalar(float t) {
    return mat3(
        t, 0.0, 0.0,
        0.0, t, 0.0,
        0.0, 0.0, t
    );
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 lightDir = vec3(0.0, 2.0, 2.0);

    // fragment position
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    // camera
    vec3 cPos = vec3(0.0, 0.0, 2.0);
    vec3 cDir = vec3(0.0, - 0.0, - 1.0);
    vec3 cUp = vec3(0.0, 1.0, 0.0);
    float targetDepth = 1.0;
    float t = iTime * 0.3;
    mat3 g = rotX(t) * rotY(t);
    cPos = g * cPos;
    cDir = g * cDir;
    cUp = g * cUp;
    vec3 cSide = cross(cDir, cUp);

    // ray
    vec3 ray = normalize(cSide * p.x + cUp * p.y + cDir * targetDepth);
    float ang = getAngle(ray, - normalize(cPos));
    vec3 rPos = cPos + (1.0 / cos(ang)) * ray;

    // marching loop
    for(int i = 0; i < 120; i ++ ) {
        rPos += 0.015 * ray;
        if (surfaceSDF(rPos) < 0.01) {
            break;
        }
    }
    vec3 normal = getNormal(rPos);
    float diff = clamp(dot(normalize(lightDir - rPos), normal), 0.01, 1.0);

    // hit check
    if (surfaceSDF(rPos) < 0.01) {
        fragColor = vec4(diff * vec3(rPos.x, 1.0, rPos.p), 1.0);
    } else {
        fragColor = vec4(vec3(1.0, 1.0, 1.0), 1.0);
    }
}

#define MAX_RAY_TRACE_STEPS 20
#define EPS 0.0001
#define N 5

struct Circle {
    vec2 pos;
    float r;
    vec3 color;
};

Circle obstacles[N];
Circle light;

float CircleSDF(vec2 pos, Circle c) {
    return length(pos - c.pos) - c.r;
}

float SceneSDF(vec2 pos) {
    float result = CircleSDF(pos, obstacles[0]);
    for (int i = 1; i < N; i++) {
        result = min(result, CircleSDF(pos, obstacles[i]));
    }
    return result;
}

float ShadowCasting(vec2 pos) {
    vec2 resultPos = pos;
    float resultDist = 0.;
    vec2 dir = normalize(light.pos - pos);
    for (int i = 0; i < MAX_RAY_TRACE_STEPS; i++) {
        float d = SceneSDF(resultPos);
        if (abs(d) < EPS) {
            break;
        }
        resultPos += d * dir;
        resultDist += abs(d);
    }
    return resultDist;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.y;
    vec3 c = vec3(0.);

    light = Circle(vec2(0.5), 0.05, vec3(1.));
    light.pos.x += cos(iTime) / 2.5;

    vec2 lightToUV = uv - light.pos;
    c += light.r / pow(length(lightToUV), 0.8);

    obstacles[0] = Circle(iMouse.xy / iResolution.y, 0.03, vec3(1., 0, 0));
    obstacles[1] = Circle(vec2(0.3, 0.3), 0.04, vec3(0.4, 0.3, 0.7));
    obstacles[2] = Circle(vec2(0.7, 0.2), 0.1, vec3(1., 0.7, 0));
    obstacles[3] = Circle(vec2(0.6, 0.7), 0.02, vec3(1., 0, 1));
    obstacles[4] = Circle(vec2(0.2, 0.7), 0.06, vec3(0., 0.5, 1.));

    for (int i = 0; i < N; i++) {
        c += length(uv - obstacles[i].pos) < obstacles[i].r ? 
            obstacles[i].color :
            vec3(0);
    }

    float surfDist = ShadowCasting(uv);
    if (surfDist < length(lightToUV) - EPS) {
        c *= 0.5;
    }

    fragColor = vec4(c, 1.);    
}

//note: range [-2;2]
//note: not normalized
float Mitchell1D(float x)
{
    //const float B = 0.0; //Catmull-Rom?
	const float B = 1.0/3.0; //Mitchell
	const float C = 0.5 * (1.0-B);
	x = abs( 2.0 * x );
    if ( x > 2.0 )
        return 0.0;
	if (x > 1.0)
		return ((-B - 6.0*C) * x*x*x + (6.0*B + 30.0*C) * x*x + (-12.0*B - 48.0*C) * x + (8.0*B + 24.0*C)) * (1.0/6.0);
	else
		return ((12.0 - 9.0*B - 6.0*C) * x*x*x + (-18.0 + 12.0*B + 6.0*C) * x*x + (6.0 - 2.0*B)) * (1.0/6.0);
}
float FilterMitchell_sep(vec2 p, vec2 r)
{
    p /= r;
    return Mitchell1D(p.x) * Mitchell1D(p.y);
}
float FilterMitchell_rad(vec2 p, vec2 r)
{
    p /= r;
    return Mitchell1D( length(p) ); //note: radius version...
}

