float Gaussian( float x )
{
	const float alpha = 4.0;
	const float expv = exp(-alpha); //note: radius = 1.0
    return max(0.0, (exp(-alpha * x * x) - expv));
    //return max(0.0, (exp(-alpha * x * x) - expv) / (1.0-expv)); //note: normalisation-factor to always get f(0)=1
}
float FilterGaussian_sep(vec2 p, vec2 radius )
{
    p /= radius;
	return Gaussian(p.x) * Gaussian(p.y);
}
float FilterGaussian_rad(vec2 p, vec2 radius )
{
    p /= radius;

    return Gaussian( length(p) );
}

float filter_sep( vec2 p, vec2 r )
{
    return FilterMitchell_sep( p, r );
    //return FilterGaussian_sep( p, r );
}
float filter_rad( vec2 p, vec2 r )
{
    return FilterMitchell_rad( p, r );
    //return FilterGaussian_rad( p, r );
}

