float N21(vec2 uv){
	return fract(sin(uv.x*32342. + uv.y*4899999.)*11456.);    
}

float perlinNoise(vec2 uv){

	vec2 gv = fract(uv);	
	vec2 id = floor(uv);


    gv = gv*gv*gv*(3.-2.*gv);

    float bl = N21(id);
    float br = N21(id+vec2(1,0));
    float b = mix(bl,br,gv.x);

    float tl = N21(id+vec2(0,1));
    float tr = N21(id+vec2(1,1));
    float t = mix(tl,tr,gv.x);

    float c = mix(b,t,gv.y);


    return c;
}


#define OCTAVES 3
float fbm(vec2 gv,float frequency,float amplitude){
	float c=0.;
	float n=0.;
	for(int i=0;i<OCTAVES;i++){
		c+=perlinNoise(gv*frequency*1.)*amplitude;
		frequency*=2.14325434;
		amplitude*=.18124;
		n+=amplitude;
	}
	c/=n;
	return c;

}
vec2 N22(vec2 p){

 	vec2 c = fract(p*vec2(84337.834,12.5465));
    c+= (dot(p.y*12.3,p.x*7656756.4343));

    return c;

}


