void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // camera	
    float an = 12.0 - sin(0.1*iTime);
    vec3 ro = vec3( 3.0*cos(0.1*an), 1.0, -3.0*sin(0.1*an) );
    vec3 ta = vec3( 0.0, -0.4, 0.0 );

    // camera-to-world transformation
    mat3 ca = setCamera( ro, ta, 0.0 );

    seed = sin(fragCoord.x*114.0)*sin(fragCoord.y*211.1)*sin(iTime);

    // animation
    int technique = (fract(iTime/3.0)>0.5) ? 1 : 0;
    float lightSize = 0.05 + 0.04*sin(0.7*iTime);

    // render
    vec3 tot = vec3(0.0);
#if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
#else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
#endif

        // ray direction
        vec3 rd = ca * normalize( vec3(p.xy,2.0) );

        // render	
        vec3 col = render( ro, rd, technique, lightSize);

        // gain
        col = 1.8*col/(1.0+dot(col,vec3(0.333)));

		// gamma
        col = pow( col, vec3(0.4545) );

        tot += col;
#if AA>1
    }
    tot /= float(AA*AA);
#endif


    fragColor = vec4( tot, 1.0 );
}

#define PI 3.14159265358979323846

vec2 random2D(vec2 st){
  st=vec2(dot(st,vec2(127.1,311.7)),dot(st,vec2(269.5,183.3)));
  return-1.+2.*fract(sin(st)*43758.5453123);
}

// Gradient Noise by Inigo Quilez - iq/2013
// https://www.shadertoy.com/view/XdXGW8
float noise(vec2 st){
  vec2 i=floor(st);
  vec2 f=fract(st);
  // mixing function
  vec2 u=f*f*(3.-2.*f);

  return mix(mix(dot(random2D(i+vec2(0.,0.)),f-vec2(0.,0.)),
  dot(random2D(i+vec2(1.,0.)),f-vec2(1.,0.)),u.x),
  mix(dot(random2D(i+vec2(0.,1.)),f-vec2(0.,1.)),
  dot(random2D(i+vec2(1.,1.)),f-vec2(1.,1.)),u.x),u.y);
}

float circle(in vec2 _st,in float _radius){
  vec2 l=_st-vec2(.5);
  return 1.-smoothstep(_radius-(_radius*.01),
  _radius+(_radius*.01),
  dot(l,l)*4.);
}

float shape(vec2 st,float radius){
  st=st-vec2(.5);
  float r=length(st)*2.;
  float a=atan(st.y,st.x);
  float m=abs(mod(2.*iTime,2.*PI)-PI)/a*2.2;
  float f=radius;

  // stripeys, where they coming from, idk
  m+=noise(st+iTime*.1)*10.;
  st-=.9;
  m+=noise(st+iTime*.1)*5.;
  st.y+=.4;
  m+=noise(st+iTime*.1)*7.;
  st=1.-st;
  m+=noise(st+iTime*.1)*5.;
  st+=.9;
  m+=noise(st+iTime*.1)*10.;

  f+=sin(a*100.)*noise(st+iTime*.2)*.1;
  f+=(sin(a*100.)*.1*pow(m,2.));
  return smoothstep(f,f+.007,r);
}

float shapeBorder(vec2 st,float radius,float width){
  return shape(st,radius)-shape(st,radius+width);
}

vec2 rotate2D(vec2 _st,float _angle){
  _st-=.5;
  _st=mat2(cos(_angle),-sin(_angle),
  sin(_angle),cos(_angle))*_st;
  _st+=.5;
  return _st;
}

float squigglyCircle(vec2 st,float radius){
  st=vec2(.5)-st;
  float r=length(st)*2.;
  float a=atan(st.y,st.x);
  float m=abs(mod(iTime*2.,PI*2.)-PI)/3.6;
  float f=radius;
  m+=noise(st+iTime*.1)*.5;

  f+=sin(a*50.)*noise(st+iTime*.2)*.1;
  f+=(sin(a*20.)*.1*pow(m,2.));
  return 1.-smoothstep(f,f+.007,r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  vec2 st=fragCoord.xy/iResolution.yy;
  st.x-=0.4;
  vec3 color=vec3(0.);
  st=rotate2D(st,PI*iTime/3.);
  color=vec3(shapeBorder(st,1.5,30.));
  color*=squigglyCircle(st,.5*(sin(iTime)+2.1));

  fragColor=vec4(color,1.);
}

