// based on https://www.shadertoy.com/view/MlKcDD


// signed distance to a quadratic bezier
float sdBezier(vec2 pos, vec2 A, vec2 B, vec2 C, out vec3 col)
{    
    vec2 a = B - A;
    vec2 b = A - 2.0*B + C;
    vec2 c = a * 2.0;
    vec2 d = A - pos;
    vec2 e = 2. * (C - B);

    float kk = 1.0 / dot(b,b);
    float kx = kk * dot(a,b);
    float ky = kk * (2.0*dot(a,a)+dot(d,b)) / 3.0;
    float kz = kk * dot(d,a);      

    float res = 0.0;

    float p = ky - kx*kx;
    float p3 = p*p*p;
    float q = kx*(2.0*kx*kx - 3.0*ky) + kz;
    float h = q*q + 4.0*p3;

    col = 1. - vec3(0.1,0.4,0.7);

    if(h >= 0.0) 
    { 
        h = sqrt(h);
        vec2 x = (vec2(h, -h) - q) / 2.0;
        vec2 uv = sign(x)*pow(abs(x), vec2(1.0/3.0));
        float t = uv.x + uv.y - kx;
      	//t = clamp( t, 0.0, 1.0 );

        vec2 tangent = (1. - t) * c + t * e;

        // 1 root
        vec2 qos = d + (c + b*t)*t;
        res = length(qos);

        // inside if true
        if (tangent.x * qos.y < tangent.y * qos.x)
            col = 1. - col;
    }
    else
    {
        float z = sqrt(-p);
        float v = acos( q/(p*z*2.0) ) / 3.0;
        float m = cos(v);
        float n = sin(v)*1.732050808;
        vec3 t = vec3(m + m, -n - m, n - m) * z - kx;
        //t = clamp( t, 0.0, 1.0 );

        // 3 roots
        vec2 qos1 = d + (c + b*t.x)*t.x;
        vec2 tangent1 = (1. - t.x) * c + t.x * e;
        float dis1 = dot(qos1,qos1);

        vec2 qos2 = d + (c + b*t.y)*t.y;
		vec2 tangent2 = (1. - t.y) * c + t.y * e;
        float dis2 = dot(qos2,qos2);

        vec2 qos3 = d + (c + b*t.z)*t.z;
		vec2 tangent3 = (1. - t.z) * c + t.z * e;
        float dis3 = dot(qos3,qos3);

        vec2 qos;
        vec2 tangent;
        if (dis1 < dis2) {
            if (dis1 < dis3) {
                qos = qos1;
                tangent = tangent1;
            } else {
                qos = qos3;
                tangent = tangent3;
            }
        } else {
            if (dis2 < dis3) {
                qos = qos2;
                tangent = tangent2;
            } else {
                qos = qos3;
                tangent = tangent3;
            }
        }

        res = length( qos );

		if (tangent.x * qos.y < tangent.y * qos.x)
            col = 1. - col;
    }

    return res;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

	vec2 v0 = vec2(-.5 + .1 * cos(1. * iTime), .1 * sin(1. * iTime));
    vec2 v1 = vec2( .0 + .3 * cos(2. * iTime), .0);
    vec2 v2 = vec2( .5 + .1 * cos(3. * iTime), .1 * sin(3. * iTime));

    vec3 col;
    float d = sdBezier( p, v0,v1,v2, col );

	col *= 1.0 - exp(-4.0*abs(d));
	col *= 0.8 + 0.2*cos(140.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );


	fragColor = vec4(col,1.0);
}

