#define PI 3.14
const int Steps = 1000;
const float Epsilon = 0.05; // Marching epsilon
const float T = 0.5;

const float rA = 10.0; // Maximum and minimum ray marching or sphere tracing distance from origin
const float rB = 40.0;

// Transforms
vec3 rotateX(vec3 p, float a)
{
  float sa = sin(a);
  float ca = cos(a);
  return vec3(p.x, ca * p.y - sa * p.z, sa * p.y + ca * p.z);
}

vec3 rotateY(vec3 p, float a)
{
  float sa = sin(a);
  float ca = cos(a);
  return vec3(ca * p.x + sa * p.z, p.y, -sa * p.x + ca * p.z);
}

vec3 rotateZ(vec3 p, float a)
{
  float sa = sin(a);
  float ca = cos(a);
  return vec3(ca * p.x + sa * p.y, -sa * p.x + ca * p.y, p.z);
}

// Smooth cubic falloff function
// x : distance
// R : radius
float falloff(float x, float R)
{
  float u = clamp(x / R, 0.0, 1.0);
  float v = (1.0 - u * u);
  return v * v * v;
}

// Primitives

// Point skeleton
// p : point
// c : center of skeleton
// e : energy associated to skeleton
// R : radius
float point(vec3 p, vec3 c, float e, float R)
{
  return e * falloff(length(p - c), R);
}

// Segment skeleton
// p : point
// a : First extremity of the skeleton
// b : Second extremity of the skeleton
// e : energy associated to skeleton
// R : radius
float segment(vec3 p, vec3 a, vec3 b, float e, float R)
{
  float len;
  vec3 u = normalize(b - a);
  vec3 ap = p - a;
  vec3 bp = p - b;
  if (dot(ap, u) < 0.) // We are on the left of the segment
    len = length(p - a);
  else if (dot(bp, u) > 0.) //We are on the right of the segment
    len = length(b - p);
  else
    len = sqrt((length(ap) * length(ap)) - (dot(ap, u) * dot(ap, u))); // On calcule la distance au segment
  return e * falloff(len, R);
}

// Disk skeleton
// p : point
// c : center of skeleton
// n : normal of the skeleton
// e : energy associated to skeleton
// R : radius
float disk(vec3 p, vec3 c, vec3 n, float e, float R)
{
  float len = 0.0;
  // First get the distance to c on the disk plane
  vec3 pc = p - c;
  float pcDn = abs(dot(pc, n));
  float projected2C = sqrt(pow(length(pc), 2.0) - pow(pcDn, 2.0));
  if (projected2C <= R)
    len = pcDn;
  else
  {
    len = sqrt(pow(pcDn, 2.0) + pow(projected2C - R, 2.0));
  }
  return e * falloff(len, R);
}

// Circle skeleton
// p : point
// c : center of skeleton
// n : normal of the skeleton
// e : energy associated to skeleton
// R : radius
float circle(vec3 p, vec3 c, vec3 n, float e, float R)
{
  float len = 0.0;
  // First get the distance to c on the disk plane
  vec3 pc = p - c;
  float pcDn = abs(dot(pc, n));
  float projected2C = sqrt(pow(length(pc), 2.0) - pow(pcDn, 2.0));
  if (projected2C <= R / 2.)
  {
    len = R - projected2C;
  }
  else
  {
    len = sqrt(pow(pcDn, 2.0) + pow(projected2C - R, 2.0));
  }
  return e * falloff(len, R);
}

// Cylinder skeleton
// p : point
// c : center of skeleton
// n : orientation of the skeleton
// e : energy associated to skeleton
// H : height of the skeleton
// R : radius
float cylinder(vec3 p, vec3 c, vec3 n, float e, float H, float R)
{
  float len = 0.;

  vec3 pc = p - c;
  float vDist = abs(dot(pc, n));
  float hDist = sqrt(pow(length(pc), 2.0) - pow(vDist, 2.0));

  // 	Orientate the normal to where p is from c
  //If we are on the extremities
  if (vDist > H)
  {
    if (dot(pc, n) < 0.0)
      n = -n;
    //Compute center of the concerned disk
    vec3 cc = c + (n * H);

    vec3 pcc = p - cc;
    float pccDn = abs(dot(pcc, n));
    float projected2CC = sqrt(pow(length(pcc), 2.0) - pow(pccDn, 2.0));
    if (projected2CC <= R)
      len = pccDn;
    else
    {
      len = sqrt(pow(pccDn, 2.0) + pow(projected2CC - R, 2.0));
    }
  }
  else
  {
    //	get the coplanar disk center to the point
    vec3 cc = c + n * dot(p - c, n);
    len = length(p - cc) - R;
  }
  return e * falloff(len, R);
}

// Sphere skeleton
// p : point
// c : center of skeleton
// e : energy associated to skeleton
// R : radius
float sphere(vec3 p, vec3 c, float e, float R)
{
  float len = length(p - c) - R;
  return e * falloff(len, R);
}

// Hemisphere skeleton
// p : point
// c : center of skeleton
// n : normal
// e : energy associated to skeleton
// R : radius
float hemisphere(vec3 p, vec3 c, vec3 n, float e, float R)
{
  float len = 0.;
  if (dot(p - c, n) > 0.) // On top
  {
    len = length(p - c) - R;
  }
  else
  {
    vec3 pc = p - c;
    float pcDn = abs(dot(pc, n));
    float projected2C = sqrt(pow(length(pc), 2.0) - pow(pcDn, 2.0));
    if (projected2C <= R)
      len = pcDn;
    else
    {
      len = sqrt(pow(pcDn, 2.0) + pow(projected2C - R, 2.0));
    }
  }
  return e * falloff(len, R);
}

// Bubble skeleton
// p : point
// c : center of skeleton
// n : Elongation axis
// e : energy associated to skeleton
// R : radius
float bubble(vec3 p, vec3 c, vec3 n, float e, float R)
{
  float len = 0.0;
  len = length(p - c) - R + min(R, pow(dot(p - c, n), 2.));
  return e * falloff(len, R);
}

// Cone skeleton
// p : point
// c : center of skeleton
// n : normal of the skeleton
// e : energy associated to skeleton
// H : Height
// R : radius
float cone(vec3 p, vec3 c, vec3 n, float e, float H, float R)
{
  float len = 10.;
  vec3 pc = p - c;
  //	Get the tip
  vec3 tip = c - n * H;
  //	Get the bottom extremity
  //	Get the projected Vector on the plane
  vec3 planeProj = normalize(pc - n * dot(pc, n));
  //	Compute the extremity
  vec3 extremity = c + planeProj * R;
  //Get the vector going along the edge of the cone
  vec3 edge = normalize(extremity - tip);
  //Get the closest point on the cone
  vec3 q = tip + (edge * dot(p - tip, edge));
  if (dot(q - extremity, -n) < 0.)
  {
    float pcDn = abs(dot(pc, n));
    float projected2C = sqrt(pow(length(pc), 2.0) - pow(pcDn, 2.0));
    if (projected2C <= R)
      len = pcDn;
    else
    {
      len = sqrt(pow(pcDn, 2.0) + pow(projected2C - R, 2.0));
    }
  }
  else if (dot(q - extremity, -n) < H) // If q is in the middle of the cone
  {
    //Get the vector going along the edge of the cone and forming a right angle
    //triangle with p
    vec3 edge90 = tip + (edge * dot(p - tip, edge)) - tip;
    len = sqrt(pow(length(p - tip), 2.) - pow(length(edge90), 2.));
  }
  else // We are on the top of the cone
  {
    len = length(p - tip);
  }

  return e * falloff(len, R);
}

// Box skeleton
// p : point
// c : center of skeleton
// e : energy associated to skeleton
// W : width of the skeleton
// H : height of the skeleton
// D : depth of the skeleton
float box(vec3 p, vec3 c, float e, float R, float W, float H, float D)
{
  float len = 0.;
  vec3 lenVec = vec3(0.0, 0.0, 0.0);

  //Component wise length computation
  lenVec.x = max(abs(c.x - p.x) - W / 2.0,0.);
  lenVec.y = max(abs(c.y - p.y) - H / 2.0,0.);
  lenVec.z = max(abs(c.z - p.z) - D / 2.0,0.);

  len =  length(lenVec);

  return e * falloff(len, R);
}

// Operators

// Blending
// a,b : field function of the sub-trees
float Blend(float a, float b)
{
  return a + b;
}

// Union
// a : field function of left sub-tree
// b : field function of right sub-tree
float Union(float a, float b)
{
  return max(a, b);
}

// Intersection
// a : field function of left sub-tree
// b : field function of right sub-tree
float Intersection(float a, float b)
{
  return min(a, b);
}

// Substraction
// a : field function of left sub-tree
// b : field function of right sub-tree
float Minus(float a, float b, float T)
{
  return min(a, 2. * T - b);
}

// Animation interpolation
float interpolation(float duration, float transitionDuration, float transition1, float transition2)
{
  float t = mod(iTime, duration);
  if (t < transition1 || t > transition2 + transitionDuration)
    return 1.;
  else if (t > transition1 + transitionDuration && t < transition2)
    return 0.;
  else if (t < transition2)
    return cos((t - transition1) / transitionDuration * (PI / 2.));
  else
    return sin((t - transition2) / transitionDuration * (PI / 2.));
}

