                 box(p, tvPos + tvNormal, 1., 1., tvWidth - 2., tvHeight - 2., tvDepth - 1.), T);

  tvBody = Minus(tvBody,
                 cylinder(p, tvPos - vec3(tvWidth / 2. - 0.5, tvHeight / 2. - .25, tvDepth / 2.),
                          tvNormal, 1.0, 0.5, .25),
                 T);
  tvBody = Minus(tvBody,
                 cylinder(p, tvPos - vec3(-tvWidth / 2. + 0.5, tvHeight / 2. - .25, tvDepth / 2.),
                          tvNormal, 1.0, 0.5, .25),
                 T);

  tvBody = Minus(tvBody,
                 segment(p, tvPos - vec3(tvWidth / 2. - 2., tvHeight / 2. - .25, tvDepth / 2. + .5),
                         tvPos - vec3(-tvWidth / 2. + 2., tvHeight / 2. - .25, tvDepth / 2. + .5),
                         1., .5),
                 T);

  tvBody = Minus(tvBody,
                 cone(p, tvPos - tvDepth / 2. * tvNormal, tvNormal, 1., 3., 1.5),
                 T);

  v = Union(tvBody, tvHead);
  v = Union(v, screen);
  v = Union(v, buttonLeft);
  v = Union(v, buttonRight);
  v = Union(v, tvBack);
  if (screen > 0.5)
    return vec2(v - T, dot(p - tvPos, cross(tvNormal, tvUp)) / 4.);
  else
    return vec2(v - T, -999.);
}

// Calculate object normal
// p : point
vec4 ObjectNormal(in vec3 p)
{
  float eps = 0.0001;
  vec3 n;
  vec2 v = Object(p);
  n.x = Object(vec3(p.x + eps, p.y, p.z)).x - v.x;
  n.y = Object(vec3(p.x, p.y + eps, p.z)).x - v.x;
  n.z = Object(vec3(p.x, p.y, p.z + eps)).x - v.x;
  return vec4(normalize(n), v.y);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// h : hit
// s : Number of steps
float Trace(vec3 o, vec3 u, out bool h, out int s)
{
  h = false;

  // Don't start at the origin, instead move a little bit forward
  float t = rA;

  for (int i = 0; i < Steps; i++)
  {
    s = i;
    vec3 p = o + t * u;
    float v = Object(p).x;
    // Hit object
    if (v > 0.0)
    {
      s = i;
      h = true;
      break;
    }
    // Move along ray
    t += Epsilon;
    // Escape marched far away
    if (t > rB)
    {
      break;
    }
  }
  return t;
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// h : hit
// s : Number of steps
float SphereTrace(vec3 o, vec3 u, out bool h, out int s)
{
  h = false;

  // Don't start at the origin, instead move a little bit forward
  float t = rA;

  for (int i = 0; i < Steps; i++)
  {
    s = i;
    vec3 p = o + t * u;
    float v = Object(p).x;
    // Hit object
    if (v > 0.0)
    {
      s = i;
      h = true;
      break;
    }
    // Move along ray
    t += max(Epsilon, abs(v) / 4.0);
    // Escape marched far away
    if (t > rB)
    {
      break;
    }
  }
  return t;
}

// Background color
// d : Ray direction
vec3 Background(vec3 d)
{
  return mix(vec3(0.4, 0.3, 0.75), vec3(0.75, 0.85, 1.0), d.y * 0.5 + 0.5);
}

// Shading and lighting
// p : point,
// n : normal at point
vec3 Shade(vec3 p, vec3 n)
{
  // point light
  const vec3 lightPos = vec3(5.0, 5.0, 5.0);
  const vec3 lightColor = vec3(0.5, 0.5, 0.5);

  vec3 c = 0.25 * Background(n);
  vec3 l = normalize(lightPos - p);

  // Not even Phong shading, use weighted cosine instead for smooth transitions
  float diff = 0.5 * (1.0 + dot(n, l));

  c += diff * lightColor;

  return c;
}

// Shading with number of steps
vec3 ShadeSteps(int n)
{
  float t = float(n) / (float(Steps - 1));
  return vec3(t, 0.25 + 0.75 * t, 0.5 - 0.5 * t);
}

vec3 screenColor(float x, float t)
{
  // t at 1 means
  float freq = 10.;
  float speed = 3.;
  vec3 col = t * vec3(cos(x * freq + iTime * speed),
                      cos(x * freq + freq / 2. + iTime * speed),
                      cos(x * freq + freq + iTime * speed)) +
             (1. - t) * vec3(0.);
  // Output to screen
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
  vec2 pixel = (gl_FragCoord.xy / iResolution.xy) * 2.0 - 1.0;

  // compute ray origin and direction
  float asp = iResolution.x / iResolution.y;
  vec3 rd = normalize(vec3(asp * pixel.x, pixel.y, -4.0));
  vec3 ro = vec3(0.0, 0.0, 20.0);

  vec2 mouse = iMouse.xy / iResolution.xy;

  /*ro = rotateY(ro, -mouse.x * 5.0);
  rd = rotateY(rd, -mouse.x * 5.0);
  ro = rotateZ(ro, -mouse.y * 5.0);
  rd = rotateZ(rd, -mouse.y * 5.0);*/

  float a = iTime * 0.25;
  ro = rotateY(ro, a);
  rd = rotateY(rd, a);

  // Trace ray
  bool hit;

  // Number of steps
  int s;

  float t = SphereTrace(ro, rd, hit, s);
  vec3 pos = ro + t * rd;
  // Shade background
  vec3 rgb = Background(rd);

  if (hit)
  {
    // Compute normal
    vec4 nScreen = ObjectNormal(pos);
    vec3 n = nScreen.xyz;
    float t = interpolation(30., .25, 5., 24.75);
    if (nScreen.w == -999.)
      // Shade object with light
      rgb = Shade(pos, n);
    else
      rgb = screenColor(nScreen.w, t);
  }

  // Uncomment this line to shade image with false colors representing the number of steps
  //rgb = ShadeSteps(s);

  fragColor = vec4(rgb, 1.0);
}

