// ========== Hash Functions ==========

#define HD 43758.5453123
#define HE 12.989824344
#define HF 78.233235235
#define HG 44.230257362
#define HH 83.597411735

float hash11(float st) {
    #if defined(NOISE_TEXTURE_3D)
    return texelFetch( NOISE_TEXTURE_3D, ivec3(vec3(HF,HG,HH)*st)&31,0).x;
    #elif defined(NOISE_TEXTURE_2D)
    return texelFetch( NOISE_TEXTURE_2D, ivec2(vec2(HF,HG)*st)&255,0).x;
    # else
    return fract(sin(st*HE)*HD); 
    #endif
}

float hash21(vec2 st) {
    #if defined(NOISE_TEXTURE_3D)
    return texelFetch( NOISE_TEXTURE_3D, ivec3(st*HE,(st.x*st.y)*HE) &31,0).x;
    #elif defined(NOISE_TEXTURE_2D)
    return texelFetch( NOISE_TEXTURE_2D, ivec2(st*HE)&255,0).x;
    # else
    return fract(sin(dot(st,vec2(HE,HF)))*HD);
    #endif
}

float hash31(vec3 st) {
    #if defined(NOISE_TEXTURE_3D)
    return texelFetch( NOISE_TEXTURE_3D, ivec3(st*HE)&31,0).x;
    #elif defined(NOISE_TEXTURE_2D)
    return texelFetch( NOISE_TEXTURE_2D, ivec2(st.x*HE+st.z*HF,st.y*HG+st.z*HH)&255,0).x;
    # else
    return fract(sin(dot(st,vec3(HE,HF,HG)))*HD);
    #endif
}


//FIXME: find alternativs with texture lookup
vec3 hash13(float st) {
    return fract(sin(st*vec3(HE,HH,HG))*HD);
}

vec3 hash23(vec2 st) {
    return fract(sin(st.xyx*vec3(HE,HF,HH))*HD);
}

vec3 hash33(vec3 st) {
    return fract(sin((st+vec3(HD,HH,HE))*vec3(HH,HE,HG))*HD);
}

