// ========== Distance Feilds ==========
// All primitives are centered at the origin.

// == 2D ==
float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,vec2(0))) + min(max(d.x,d.y),0.0);
}

// == ==

// == 3D ==

float sdPlane( vec3 p )
{
    return p.y;
}

float sdSphere( vec3 p, float s )
{
    return length(p)-s;
}

float sdBox( vec3 p, vec3 b )
{
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
    vec3 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}

float sdXAlignedCapsule( vec3 p, float l, float r )
{
    p.z -= clamp( p.z, 0.0, l );
    return length(p) - r;
}

float sdYAlignedCapsule( vec3 p, float l, float r )
{
    p.y -= clamp( p.y, 0.0, l );
    return length(p) - r;
}

float sdZAlignedCapsule( vec3 p, float l, float r )
{
    p.x -= clamp( p.x, 0.0, l );
    return length(p) - r;
}

float sdXAlignedCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.yz),p.x)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sdYAlignedCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}


