// ========== ==========

float getFog(vec3 direction, float time) {
    time=time*0.1;
    float fog = 0.0;
    vec2 t = vec2(time,-time);

    fog+= valueNoise3du(direction + vec3(t.x));
    fog+= valueNoise3du((direction + vec3(t.xyx)) * 2.0) * 0.5;
    fog+= valueNoise3du((direction + vec3(t.xxy)) * 4.0) * 0.25;
    fog+= valueNoise3du((direction + vec3(t.yyy)) * 8.0) * 0.125;
    fog+= valueNoise3du((direction + vec3(t.yyy)) * 16.0)* 0.0625;

    //return fog/1.9375;
    return fog;//*0.516129032258;
}

// --quick hack to strobe the road lights so they always strobe away from the camera

float getRoadLightStrobe(float distanceFromCamera, float time) {
    return smoothstep(0.0,1.0,clamp(sin(time*5.0 - (distanceFromCamera/RAY_MAX_DISTANCE)*PI*5.0)*0.5,0.0,1.0));
}

// ========== ==========

// ========== Camera Motion ==========

vec3 getCameraPath(float time) {
    time *= 0.05;

    float b = blockSize;

    vec3 path[16];
    int i=0;

    path[i++] = vec3( 0.0*b,	0.0,	 0.0*b);
    path[i++] = vec3( 0.0*b,	0.0,	 1.0*b - gridSize);
    path[i++] = vec3( 0.0*b,	0.0,	 1.0*b + gridSize);
    path[i++] = vec3( 0.0*b,	0.0,	 2.0*b);
    path[i++] = vec3( 2.0*b - 1.0,	0.0,	 2.0*b);
    path[i++] = vec3( 2.0*b,	1.0*b,	 2.0*b);
    path[i++] = vec3( 2.0*b,	1.0*b,	 0.0*b);
    path[i++] = vec3( 4.0*b,	1.0*b,	 0.0*b);
    path[i++] = vec3( 4.0*b,	1.0*b,	-2.0*b+1.0);
    path[i++] = vec3( 4.0*b,	0.0,	-2.0*b);
    path[i++] = vec3( 2.0*b,	0.0,	-2.0*b);
    path[i++] = vec3( 2.0*b,	0.0,	-4.0*b);
    path[i++] = vec3( 0.0*b,	0.0,	-4.0*b);
    path[i++] = vec3( 0.0*b,	0.0,	-2.0*b);
    path[i++] = vec3(-2.0*b,	0.0,	-2.0*b);
    path[i++] = vec3(-2.0*b,	0.0,	 0.0*b);

    time = mod(time,float(path.length()));

    float cornerRadius = 0.1;
    float inverseCornerRadius = 1.0 - cornerRadius;

    int timeI = int(floor(time));
    float timeF = fract(time)*inverseCornerRadius+cornerRadius;

    vec3 p0 = path[timeI];
    vec3 r;
    timeI += 1;
    if (timeI>=path.length()) {
        timeI = 0;
    }
    vec3 p1 = path[timeI];
    if (timeF >= inverseCornerRadius) {
        timeI += 1;
        if (timeI>=path.length()) {
            timeI = 0;
        }
        vec3 p2 = path[timeI];
        vec3 p01 = p0 + ((p1-p0) * (inverseCornerRadius));
        vec3 p12 = p2 + ((p1-p2) * (inverseCornerRadius));
        float t = map(timeF,inverseCornerRadius,1.0,0.0,1.0);
        vec3 a = mix(p01,p1,t);
        vec3 b = mix(p1,p12,t);
        r= mix(a,b,t);
    } else {
        r=mix( p0, p1, timeF);
    }


    vec3 rd;
    rd.y = (sin(time*5.0) + sin(time*9.0)) * 0.8;
    rd.x = sin(time*6.0) + sin(time*8.0);
    rd.z = sin(time*7.0) + sin(time*7.0);

    rd *= sin(fract(time)*PI);
    rd *= clamp(length(r)/gridSize-gridSize,0.0,1.0);
    r+=rd*0.5;
    return r;
}

camera getCamera(vec3 origin, float roll, float pitch, float yaw, float zoom) {
    camera camera;
    camera.origin = origin;
    mat3x3 rotationMatrix = eulerToMatrix( roll, pitch, yaw);

    camera.forward = vec3(0.0,0.0,1.0) * rotationMatrix;
    camera.right = vec3(1.0,0.0,0.0) * rotationMatrix;
    camera.up = vec3(0.0,1.0,0.0) * rotationMatrix;

    camera.rotationMatrix = rotationMatrix;
    camera.roll = roll;
    camera.pitch = pitch;
    camera.yaw = yaw;

    camera.zoom = zoom;
    return camera;
}

camera getCamera(vec3 lookAt, float distance, float roll, float pitch, float yaw, float zoom) {
    camera camera;

	mat3x3 rotationMatrix = eulerToMatrix( roll, pitch, yaw );

    camera.forward = vec3(0.0,0.0,1.0) * rotationMatrix;
    camera.right = vec3(1.0,0.0,0.0) * rotationMatrix;
    camera.up = vec3(0.0,1.0,0.0) * rotationMatrix;

    camera.origin = lookAt + camera.forward*-distance;

    camera.rotationMatrix = rotationMatrix;
    camera.roll = roll;
    camera.pitch = pitch;
    camera.yaw = yaw;

    camera.zoom = zoom;
    return camera;
}

camera getCamera(vec3 origin, vec3 forward, float roll, float zoom) {
    float yaw = atan(forward.x,forward.z);
    float pitch = asin(forward.y);
    return getCamera(origin, roll, pitch, yaw, zoom);
}

camera getCamera(vec2 mouse, float time) {
    #if defined(DEBUG_CAMERA_FLY)
    vec3 cameraPos1 = getCameraPath(time);
    if (iMouse.z<0.5) {
        vec3 cameraPos2 = getCameraPath(time+0.5);
        vec3 cameraPos3 = getCameraPath(time-0.5);

        vec3 cameraForward = normalize(cameraPos2 - cameraPos1);

        vec3 cameraForward2 = normalize(cameraPos1 - cameraPos3);

        //Take atan and nivt to -1 to +1 range
        float yaw1 = atan(cameraForward.x,cameraForward.z)/PI;
        float yaw2 = atan(cameraForward2.x,cameraForward2.z)/PI;

        // get the differance and renormalize using fract.
        float roll = -(fract((yaw1-yaw2)*0.5+0.5)*2.0-1.0);
        roll = clamp(roll,-0.5,0.5);//clamp(-0.1,0.1,1.0-pow(1.0-roll,5.0));
        return getCamera(cameraPos1, cameraForward, roll,  0.5);
    } else  {
        return getCamera(cameraPos1, 0.0, mouse.y*PI, mouse.x*2.0*PI,  0.5);
    }
    #elif defined(DEBUG_CAMERA_TABLE)
    return getCamera(vec3(0.0)+vec3(0.5), 2.5, 0.0, -mouse.y*PI, -mouse.x*2.0*PI,  0.5);
    #else
    return getCamera(vec3(0.0,0.0,-5.0), 0.0, mouse.y*PI, mouse.x*2.0*PI,  0.5);
    #endif
}

