rayMarchHit GetRayMarchHit(vec3 position, float time) {
    rayMarchHit hit;
    #if defined(DEBUG_USE_SQUARE_DISTANCE)
    hit.distance = RAY_MAX_DISTANCE*RAY_MAX_DISTANCE;
    #else
    hit.distance = RAY_MAX_DISTANCE;
    #endif

    float newDistance = hit.distance;

    newDistance = min(newDistance, sdBox( position-vec3(0.5), vec3(0.48) )-0.02);
    newDistance = min(newDistance, sdBox( position-vec3(0.5) - vec3(1.0,0.0,sin(time*0.5)), vec3(0.48))-0.02);
    newDistance = min(newDistance, sdBox( position-vec3(0.5) - vec3(-1.0,sin(time*0.5),0.0), vec3(0.48))-0.02);
    newDistance = min(newDistance, sdBox( position-vec3(0.5) - vec3(sin(time*0.5),1.0,0.0), vec3(0.48))-0.02);

    newDistance = min(newDistance, sdYAlignedCappedCylinder( position-vec3(0.5,-0.5,0.0), 0.98, 0.48 )-0.02);
    newDistance = min(newDistance, sdXAlignedCappedCylinder( position-vec3(1.25,-0.5,0.0), 1.48, 0.23 )-0.02);

    if (newDistance < hit.distance) {
        hit.origin = position;
        hit.distance = newDistance;
        hit.materialId = MAT_WALL;
    }

    /*if (newDistance < hit.distance) {
        hit.origin = position;
        hit.distance = newDistance;
        hit.materialId = MAT_WINDOW;
    }*/

    return hit;
}

rayMarchHit RayMarch(ray ray, float maxDistance, float minSurfaceDistance, int maxSteps, float time)
{
    rayMarchHit hit;
    float accumilatedDistance = 0.0;
    float modulatedMinSurfaceDistance = minSurfaceDistance;
    int i;
    for(i=0; i<maxSteps; i++) {
        float distanceRatio = clamp((accumilatedDistance/maxDistance),0.0,1.0);

        //the more itterations the less precise we are, improve perfomance a bit with only mild artifacts
        modulatedMinSurfaceDistance=mix(minSurfaceDistance,minSurfaceDistance*20.0,distanceRatio);

        vec3 currentPoint = ray.origin + ray.direction * accumilatedDistance;
        hit = GetRayMarchHit(currentPoint, time);

        #if defined (DEBUG_RAY_DITHER)
        if(hit.distance > modulatedMinSurfaceDistance*2.0) {
            //we take smaller steps closer to the camera, gives a better Neon result.           
            hit.distance *= 0.5+mix(distanceRatio,1.0, hash31(fract(currentPoint+time)))*0.5;
        }
        #endif

        accumilatedDistance += hit.distance;

        if(accumilatedDistance >= maxDistance) {
            accumilatedDistance = maxDistance;
            hit.surfaceNormal = -ray.direction;
            hit.materialId=MAT_SKY;
            break;
        }
        //forget bouncing back, if we end up inside just terminate
        else if(hit.distance < modulatedMinSurfaceDistance) {
            break;
        }

    }
    hit.iteration = i;
    hit.distance = max(0.0,accumilatedDistance);
    return hit;
}
