float getConcreteErosion(vec3 position, bool showLine) {
    float concrete=0.0;
    concrete+= valueNoise3du(position * 4.0) * 0.25;
    concrete+= valueNoise3du(position * 8.0) * 0.125;
    concrete+= valueNoise3du(position * 16.0) * 0.0625;
    concrete+= valueNoise3du(position * 32.0) * 0.03125;
    concrete/=0.25+0.125+0.0625+0.03125;
    concrete = (abs(concrete*2.0-1.0)+0.3);

    if(showLine) {
        float line = abs(fract(position.y)-0.5);
        line = 1.0-clamp(line*25.0,0.0,1.0);
        line*=line*line*4.0;
        line = clamp(1.0-line,-10.0,1.0);
        concrete = min(line,concrete*line);
    }

    return concrete;
}

float sampleConcrete(vec3 position, bool showLine) {
    float concrete = valueNoise3du(position * 4.0);
    concrete += valueNoise3du(position * 8.0) * 0.5;
    concrete += valueNoise3du(position * 16.0) * 0.25;
    concrete += valueNoise3du(position * 32.0) * 0.125;
    concrete /= 1.0+0.5+0.25+0.125;
    concrete = (abs(concrete*2.0-1.0)+0.8);
    concrete = clamp(concrete,0.0,1.0);

    if(showLine) {
        float line = abs(fract(position.y)-0.5);
        line = 1.0-clamp(line*25.0,0.0,1.0);
        line*=line*line;
        line = 1.0-clamp(line-0.2,0.0,1.0);
        concrete *= line;
    }

    return concrete;
}

float getConcrete(vec3 position, inout vec3 normal, bool showLine) {
    float center=getConcreteErosion(position, showLine);

    vec3 rightDir;
	vec3 upDir;

    if(abs(normal.y)<0.999) {
    	rightDir = normalize(cross(vec3(0.0, 1.0, 0.0), normal));
		upDir = normalize(cross(normal, rightDir));
    } else {
        rightDir = normalize(cross(vec3(1.0, 0.0, 0.0), normal));
		upDir = normalize(cross(normal, rightDir));
    }
    float right = getConcreteErosion(position+rightDir*0.01, showLine);
    float up = getConcreteErosion(position+upDir*0.01, showLine);
    //FIXME: apply correct normal calculations, this is NOT how you do it!
    vec3 pNormal;
    pNormal=(vec3(
        right - center,
        up - center,
        right - center
    ));

    pNormal*=-1.3;
    normal = normalize(normal + pNormal);    

    //float concrete=clamp(getConcreteErosion(position, showLine)+0.5,0.0,1.0);
    float concrete=sampleConcrete(position, showLine);
    return concrete;
}

void textureSolid(in vec3 block, inout ray ray, inout rayMarchHit hit, inout vec3 colour, in float time) {
    float concrete = getConcrete(hit.origin, hit.surfaceNormal, true);
    colour = hash33(block.xyx) * vec3(0.25,0.1,0.2) + 0.5;
    colour = clamp(colour,vec3(0.0),vec3(1.0));
    colour *= concrete;
}


