    colour = pow( exposure * colour, vec3(1.0/2.2) );
    colour = clamp(colour,0.0,1.0); 
    colour = 1.7*colour/(1.0+colour);
    colour = colour*colour*(3.0-2.0*colour);
    fragColour = vec4(colour,1.0);
}

